#include <stdio.h>
#include <stddef.h>

/* Probe helper: log control flow and relevant state to /tmp/logs/2423.txt */
static void
mhd_probe_log (const struct MHD_RqDAuthParam *const algo_param,
               const char *stage,
               const char *detected)
{
  FILE *f = fopen("/tmp/logs/2423.txt", "a");
  if (f == NULL)
    return;

  /* Log stage and pointers */
  fprintf (f, "stage=%s, algo_param=%p\n", stage, (const void *)algo_param);
  if (algo_param == NULL)
  {
    fprintf (f, "  algo_param is NULL\n");
    fclose (f);
    return;
  }

  fprintf (f, "  quoted=%d, value.len=%zu, value.str=%p\n",
           (int)algo_param->quoted,
           (size_t)algo_param->value.len,
           (const void *)algo_param->value.str);

  if (algo_param->value.str != NULL)
  {
    /* Log up to 256 bytes of the value (not altering program state). */
    size_t to_print = algo_param->value.len;
    if (to_print > 256u)
      to_print = 256u;
    /* Use %.*s to safely print possibly non-null-terminated strings. */
    fprintf (f, "  value.str (len=%zu, show=%zu): '%.*s'\n",
             (size_t)algo_param->value.len,
             to_print,
             (int)to_print,
             algo_param->value.str);
  }
  if (detected != NULL)
    fprintf (f, "  detected=%s\n", detected);
  fprintf (f, "\n");
  fclose (f);
}

static enum MHD_DigestAuthAlgo3
get_rq_dauth_algo (const struct MHD_RqDAuthParam *const algo_param)
{
  /* Log entry */
  mhd_probe_log (algo_param, "entry", NULL);

  if (NULL == algo_param->value.str)
  {
    mhd_probe_log (algo_param, "null_value_str", "MD5 (default)");
    return MHD_DIGEST_AUTH_ALGO3_MD5; /* Assume MD5 by default */
  }

  if (algo_param->quoted)
  {
    if (MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str, \
                                               algo_param->value.len, \
                                               _MHD_MD5_TOKEN))
    {
      mhd_probe_log (algo_param, "quoted_match", "MD5");
      return MHD_DIGEST_AUTH_ALGO3_MD5;
    }
    if (MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str, \
                                               algo_param->value.len, \
                                               _MHD_SHA256_TOKEN))
    {
      mhd_probe_log (algo_param, "quoted_match", "SHA256");
      return MHD_DIGEST_AUTH_ALGO3_SHA256;
    }
    if (MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str, \
                                               algo_param->value.len, \
                                               _MHD_MD5_TOKEN _MHD_SESS_TOKEN))
    {
      mhd_probe_log (algo_param, "quoted_match", "SHA512_256");
      return MHD_DIGEST_AUTH_ALGO3_SHA512_256;
    }
    if (MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str, \
                                               algo_param->value.len, \
                                               _MHD_SHA512_256_TOKEN \
                                               _MHD_SESS_TOKEN))
    {
      /* Algorithms below are not supported by MHD for authentication */
      mhd_probe_log (algo_param, "quoted_match", "MD5_SESSION (unsupported)");
      return MHD_DIGEST_AUTH_ALGO3_MD5_SESSION;
    }
    if (MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str, \
                                               algo_param->value.len, \
                                               _MHD_SHA256_TOKEN \
                                               _MHD_SESS_TOKEN))
    {
      mhd_probe_log (algo_param, "quoted_match", "SHA256_SESSION (unsupported)");
      return MHD_DIGEST_AUTH_ALGO3_SHA256_SESSION;
    }
    if (MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str, \
                                               algo_param->value.len, \
                                               _MHD_SHA512_256_TOKEN))
    {
      mhd_probe_log (algo_param, "quoted_match", "SHA512_256_SESSION (unsupported)");
      return MHD_DIGEST_AUTH_ALGO3_SHA512_256_SESSION;
    }

    /* No known algorithm has been detected */
    mhd_probe_log (algo_param, "quoted_no_match", "INVALID");
    return MHD_DIGEST_AUTH_ALGO3_INVALID;
  }
  /* The algorithm value is not quoted */
  if (MHD_str_equal_caseless_s_bin_n_ (_MHD_MD5_TOKEN, \
                                       algo_param->value.str, \
                                       algo_param->value.len))
  {
    mhd_probe_log (algo_param, "unquoted_match", "MD5");
    return MHD_DIGEST_AUTH_ALGO3_MD5;
  }
  if (MHD_str_equal_caseless_s_bin_n_ (_MHD_SHA256_TOKEN, \
                                       algo_param->value.str, \
                                       algo_param->value.len))
  {
    mhd_probe_log (algo_param, "unquoted_match", "SHA256");
    return MHD_DIGEST_AUTH_ALGO3_SHA256;
  }
  if (MHD_str_equal_caseless_s_bin_n_ (_MHD_SHA512_256_TOKEN, \
                                       algo_param->value.str, \
                                       algo_param->value.len))
  {
    mhd_probe_log (algo_param, "unquoted_match", "SHA512_256");
    return MHD_DIGEST_AUTH_ALGO3_SHA512_256;
  }

  /* Algorithms below are not supported by MHD for authentication */

  if (MHD_str_equal_caseless_s_bin_n_ (_MHD_MD5_TOKEN _MHD_SESS_TOKEN, \
                                       algo_param->value.str, \
                                       algo_param->value.len))
  {
    mhd_probe_log (algo_param, "unquoted_match", "MD5_SESSION (unsupported)");
    return MHD_DIGEST_AUTH_ALGO3_MD5_SESSION;
  }
  if (MHD_str_equal_caseless_s_bin_n_ (_MHD_SHA256_TOKEN _MHD_SESS_TOKEN, \
                                       algo_param->value.str, \
                                       algo_param->value.len))
  {
    mhd_probe_log (algo_param, "unquoted_match", "SHA256_SESSION (unsupported)");
    return MHD_DIGEST_AUTH_ALGO3_SHA256_SESSION;
  }
  if (MHD_str_equal_caseless_s_bin_n_ (_MHD_SHA512_256_TOKEN _MHD_SESS_TOKEN, \
                                       algo_param->value.str, \
                                       algo_param->value.len))
  {
    mhd_probe_log (algo_param, "unquoted_match", "SHA512_256_SESSION (unsupported)");
    return MHD_DIGEST_AUTH_ALGO3_SHA512_256_SESSION;
  }

  /* No known algorithm has been detected */
  mhd_probe_log (algo_param, "unquoted_no_match", "INVALID");
  return MHD_DIGEST_AUTH_ALGO3_INVALID;
}
