#include <stdio.h>
#include <stdlib.h>

/* Probe logging helper: append a single line with current check information. */
static void
mhd_log_check (const char *check_name,
               const char *token_str,
               const struct MHD_RqDAuthParam *const algo_param,
               int cmp_result)
{
  FILE *f = fopen("/tmp/logs/2423.txt", "a");
  if (NULL == f)
    return;

  /* Log pointer values and key fields. Keep casts to avoid format warnings. */
  fprintf (f,
           "CHECK=%s token='%s' algo_param=%p value.str=%p len=%lu quoted=%d cmp=%d\n",
           check_name ? check_name : "(null)",
           token_str ? token_str : "(null)",
           (const void *) algo_param,
           (const void *) (algo_param ? algo_param->value.str : NULL),
           (unsigned long) (algo_param ? (unsigned long) algo_param->value.len : 0UL),
           (int) (algo_param ? algo_param->quoted : 0),
           (int) cmp_result);

  fclose (f);
}

/* Entry log for the function invocation. */
static void
mhd_log_entry (const struct MHD_RqDAuthParam *const algo_param)
{
  FILE *f = fopen("/tmp/logs/2423.txt", "a");
  if (NULL == f)
    return;

  fprintf (f, "ENTER get_rq_dauth_algo algo_param=%p\n",
           (const void *) algo_param);
  if (algo_param)
    fprintf (f,
             "  -> value.str=%p len=%lu quoted=%d\n",
             (const void *) algo_param->value.str,
             (unsigned long) algo_param->value.len,
             (int) algo_param->quoted);
  fclose (f);
}

static enum MHD_DigestAuthAlgo3
get_rq_dauth_algo (const struct MHD_RqDAuthParam *const algo_param)
{
  /* Log entry and state to help reproduce the reported violation scenario. */
  mhd_log_entry (algo_param);

  if (NULL == algo_param->value.str)
  {
    mhd_log_check ("null-value", "(NULL)", algo_param, 1);
    return MHD_DIGEST_AUTH_ALGO3_MD5; /* Assume MD5 by default */
  }

  if (algo_param->quoted)
  {
    int cmp;

    cmp = MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                                 algo_param->value.len,
                                                 _MHD_MD5_TOKEN);
    mhd_log_check ("quoted:MD5", _MHD_MD5_TOKEN, algo_param, cmp);
    if (cmp)
      return MHD_DIGEST_AUTH_ALGO3_MD5;

    cmp = MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                                 algo_param->value.len,
                                                 _MHD_SHA256_TOKEN);
    mhd_log_check ("quoted:SHA256", _MHD_SHA256_TOKEN, algo_param, cmp);
    if (cmp)
      return MHD_DIGEST_AUTH_ALGO3_SHA256;

    cmp = MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                                 algo_param->value.len,
                                                 _MHD_MD5_TOKEN _MHD_SESS_TOKEN);
    mhd_log_check ("quoted:MD5-SESSION", _MHD_MD5_TOKEN _MHD_SESS_TOKEN, algo_param, cmp);
    if (cmp)
      return MHD_DIGEST_AUTH_ALGO3_SHA512_256;

    cmp = MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                                 algo_param->value.len,
                                                 _MHD_SHA512_256_TOKEN _MHD_SESS_TOKEN);
    mhd_log_check ("quoted:SHA512_256-SESSION", _MHD_SHA512_256_TOKEN _MHD_SESS_TOKEN, algo_param, cmp);

    /* Algorithms below are not supported by MHD for authentication */

    if (cmp)
      return MHD_DIGEST_AUTH_ALGO3_MD5_SESSION;

    cmp = MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                                 algo_param->value.len,
                                                 _MHD_SHA256_TOKEN _MHD_SESS_TOKEN);
    mhd_log_check ("quoted:SHA256-SESSION", _MHD_SHA256_TOKEN _MHD_SESS_TOKEN, algo_param, cmp);
    if (cmp)
      return MHD_DIGEST_AUTH_ALGO3_SHA256_SESSION;

    cmp = MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                                 algo_param->value.len,
                                                 _MHD_SHA512_256_TOKEN);
    mhd_log_check ("quoted:SHA512_256", _MHD_SHA512_256_TOKEN, algo_param, cmp);
    if (cmp)
      return MHD_DIGEST_AUTH_ALGO3_SHA512_256_SESSION;

    /* No known algorithm has been detected */
    mhd_log_check ("quoted:INVALID", algo_param->value.str, algo_param, 0);
    return MHD_DIGEST_AUTH_ALGO3_INVALID;
  }

  /* The algorithm value is not quoted */
  {
    int cmp;

    cmp = MHD_str_equal_caseless_s_bin_n_ (_MHD_MD5_TOKEN,
                                           algo_param->value.str,
                                           algo_param->value.len);
    mhd_log_check ("unquoted:MD5", _MHD_MD5_TOKEN, algo_param, cmp);
    if (cmp)
      return MHD_DIGEST_AUTH_ALGO3_MD5;

    cmp = MHD_str_equal_caseless_s_bin_n_ (_MHD_SHA256_TOKEN,
                                           algo_param->value.str,
                                           algo_param->value.len);
    mhd_log_check ("unquoted:SHA256", _MHD_SHA256_TOKEN, algo_param, cmp);
    if (cmp)
      return MHD_DIGEST_AUTH_ALGO3_SHA256;

    cmp = MHD_str_equal_caseless_s_bin_n_ (_MHD_SHA512_256_TOKEN,
                                           algo_param->value.str,
                                           algo_param->value.len);
    mhd_log_check ("unquoted:SHA512_256", _MHD_SHA512_256_TOKEN, algo_param, cmp);
    if (cmp)
      return MHD_DIGEST_AUTH_ALGO3_SHA512_256;

    /* Algorithms below are not supported by MHD for authentication */

    cmp = MHD_str_equal_caseless_s_bin_n_ (_MHD_MD5_TOKEN _MHD_SESS_TOKEN,
                                           algo_param->value.str,
                                           algo_param->value.len);
    mhd_log_check ("unquoted:MD5-SESSION", _MHD_MD5_TOKEN _MHD_SESS_TOKEN, algo_param, cmp);
    if (cmp)
      return MHD_DIGEST_AUTH_ALGO3_MD5_SESSION;

    cmp = MHD_str_equal_caseless_s_bin_n_ (_MHD_SHA256_TOKEN _MHD_SESS_TOKEN,
                                           algo_param->value.str,
                                           algo_param->value.len);
    mhd_log_check ("unquoted:SHA256-SESSION", _MHD_SHA256_TOKEN _MHD_SESS_TOKEN, algo_param, cmp);
    if (cmp)
      return MHD_DIGEST_AUTH_ALGO3_SHA256_SESSION;

    cmp = MHD_str_equal_caseless_s_bin_n_ (_MHD_SHA512_256_TOKEN _MHD_SESS_TOKEN,
                                           algo_param->value.str,
                                           algo_param->value.len);
    mhd_log_check ("unquoted:SHA512_256-SESSION", _MHD_SHA512_256_TOKEN _MHD_SESS_TOKEN, algo_param, cmp);
    if (cmp)
      return MHD_DIGEST_AUTH_ALGO3_SHA512_256_SESSION;

    /* No known algorithm has been detected */
    mhd_log_check ("unquoted:INVALID", algo_param->value.str, algo_param, 0);
    return MHD_DIGEST_AUTH_ALGO3_INVALID;
  }
}
