#include <stdio.h>

/* Probe logging helpers: append human-readable traces to /tmp/logs/439.txt */
static void probe_log_open_and_write(const char *msg)
{
    FILE *f = fopen("/tmp/logs/439.txt", "a");
    if (f)
    {
        fprintf(f, "%s\n", msg);
        fclose(f);
    }
}

static void probe_log_conn_stage(const struct MHD_Connection *c,
                                 enum MHD_ProcRecvDataStage stage,
                                 const char *location)
{
    FILE *f = fopen("/tmp/logs/439.txt", "a");
    if (!f)
        return;

    if (c == NULL)
    {
        fprintf(f, "[handle_recv_no_space] %s: conn=NULL, stage=%d\n",
                location, (int)stage);
        fclose(f);
        return;
    }

    /* Log pointer values and a selection of fields used by control flow
       Note: do not dereference any pointers that might be NULL besides c. */
    fprintf(f, "[handle_recv_no_space] %s: conn=%p, stage=%d, state=%d, read_buffer=%p, read_buffer_offset=%zu\n",
            location,
            (const void *)c,
            (int)stage,
            (int)c->state,
            (const void *)c->read_buffer,
            (size_t)c->read_buffer_offset);

    /* Log request-related flags used in branching decisions. */
    fprintf(f, "  rq.have_chunked_upload=%d, rq.some_payload_processed=%d, rq.current_chunk_offset=%zu, rq.current_chunk_size=%zu\n",
            (int)c->rq.have_chunked_upload,
            (int)c->rq.some_payload_processed,
            (size_t)c->rq.current_chunk_offset,
            (size_t)c->rq.current_chunk_size);

    /* Log HTTP method/version fields that appear in the original asserts */
    fprintf(f, "  rq.http_mthd=%d, rq.http_ver=%d\n",
            (int)c->rq.http_mthd,
            (int)c->rq.http_ver);

    fclose(f);
}

static void
handle_recv_no_space (struct MHD_Connection *c,
                      enum MHD_ProcRecvDataStage stage)
{
  mhd_assert (MHD_PROC_RECV_INIT <= stage);
  mhd_assert (MHD_PROC_RECV_FOOTERS >= stage);
  mhd_assert (MHD_CONNECTION_FULL_REQ_RECEIVED > c->state);
  mhd_assert ((MHD_PROC_RECV_INIT != stage) || \
              (MHD_CONNECTION_INIT == c->state));
  mhd_assert ((MHD_PROC_RECV_METHOD != stage) || \
              (MHD_CONNECTION_REQ_LINE_RECEIVING == c->state));
  mhd_assert ((MHD_PROC_RECV_URI != stage) || \
              (MHD_CONNECTION_REQ_LINE_RECEIVING == c->state));
  mhd_assert ((MHD_PROC_RECV_HTTPVER != stage) || \
              (MHD_CONNECTION_REQ_LINE_RECEIVING == c->state));
  mhd_assert ((MHD_PROC_RECV_HEADERS != stage) || \
              (MHD_CONNECTION_REQ_HEADERS_RECEIVING == c->state));
  mhd_assert (MHD_PROC_RECV_COOKIE != stage); /* handle_req_cookie_no_space() must be called directly */
  mhd_assert ((MHD_PROC_RECV_BODY_NORMAL != stage) || \
              (MHD_CONNECTION_BODY_RECEIVING == c->state));
  mhd_assert ((MHD_PROC_RECV_BODY_CHUNKED != stage) || \
              (MHD_CONNECTION_BODY_RECEIVING == c->state));
  mhd_assert ((MHD_PROC_RECV_FOOTERS != stage) || \
              (MHD_CONNECTION_FOOTERS_RECEIVING == c->state));
  mhd_assert ((MHD_PROC_RECV_BODY_NORMAL != stage) || \
              (! c->rq.have_chunked_upload));
  mhd_assert ((MHD_PROC_RECV_BODY_CHUNKED != stage) || \
              (c->rq.have_chunked_upload));

  /* Log entry into the function with basic connection/state info */
  probe_log_conn_stage(c, stage, "entry");

  switch (stage)
  {
  case MHD_PROC_RECV_INIT:
  case MHD_PROC_RECV_METHOD:
    /* Some data has been received, but it is not clear yet whether
     * the received data is an valid HTTP request */
    probe_log_open_and_write("[handle_recv_no_space] branch: INIT/METHOD -> connection_close_error");
    connection_close_error (c,
                            _ ("No space left in the read buffer when " \
                               "receiving the initial part of " \
                               "the request line."));
    return;
  case MHD_PROC_RECV_URI:
  case MHD_PROC_RECV_HTTPVER:
    /* Some data has been received, but the request line is incomplete */
    mhd_assert (MHD_HTTP_MTHD_NO_METHOD != c->rq.http_mthd);
    mhd_assert (MHD_HTTP_VER_UNKNOWN == c->rq.http_ver);
    /* A quick simple check whether the incomplete line looks
     * like an HTTP request */
    if ((MHD_HTTP_MTHD_GET <= c->rq.http_mthd) &&
        (MHD_HTTP_MTHD_DELETE >= c->rq.http_mthd))
    {
      probe_log_open_and_write("[handle_recv_no_space] branch: URI/HTTPVER -> transmit_error_response_static (URI_TOO_LONG)");
      transmit_error_response_static (c,
                                      MHD_HTTP_URI_TOO_LONG,
                                      ERR_MSG_REQUEST_TOO_BIG);
      return;
    }
    probe_log_open_and_write("[handle_recv_no_space] branch: URI/HTTPVER -> connection_close_error (non-standard method)");
    connection_close_error (c,
                            _ ("No space left in the read buffer when " \
                               "receiving the URI in " \
                               "the request line. " \
                               "The request uses non-standard HTTP request " \
                               "method token."));
    return;
  case MHD_PROC_RECV_HEADERS:
    /* Log the exact parameters being passed to handle_req_headers_no_space */
    {
      char buf[256];
      snprintf(buf, sizeof(buf), "[handle_recv_no_space] branch: HEADERS -> handle_req_headers_no_space(c=%p, buffer=%p, len=%zu)",
               (const void *)c, (const void *)c->read_buffer, (size_t)c->read_buffer_offset);
      probe_log_open_and_write(buf);
    }
    handle_req_headers_no_space (c, c->read_buffer, c->read_buffer_offset);
    return;
  case MHD_PROC_RECV_BODY_NORMAL:
  case MHD_PROC_RECV_BODY_CHUNKED:
    mhd_assert ((MHD_PROC_RECV_BODY_CHUNKED != stage) || \
                ! c->rq.some_payload_processed);
    if (has_unprocessed_upload_body_data_in_buffer (c))
    {
      /* The connection must not be in MHD_EVENT_LOOP_INFO_READ state
         when external polling is used and some data left unprocessed. */
      mhd_assert (MHD_D_IS_USING_THREADS_ (c->daemon));
      /* failed to grow the read buffer, and the
         client which is supposed to handle the
         received data in a *blocking* fashion
         (in this mode) did not handle the data as
         it was supposed to!
         => we would either have to do busy-waiting
         (on the client, which would likely fail),
         or if we do nothing, we would just timeout
         on the connection (if a timeout is even
         set!).
         Solution: we kill the connection with an error */
      probe_log_open_and_write("[handle_recv_no_space] branch: BODY_* with unprocessed upload data -> transmit_error_response_static (INTERNAL_SERVER_ERROR)");
      transmit_error_response_static (c,
                                      MHD_HTTP_INTERNAL_SERVER_ERROR,
                                      ERROR_MSG_DATA_NOT_HANDLED_BY_APP);
    }
    else
    {
      if (MHD_PROC_RECV_BODY_NORMAL == stage)
      {
        /* A header probably has been added to a suspended connection and
           it took precisely all the space in the buffer.
           Very low probability. */
        mhd_assert (! c->rq.have_chunked_upload);
        probe_log_open_and_write("[handle_recv_no_space] branch: BODY_NORMAL -> handle_req_headers_no_space(c, NULL, 0)");
        handle_req_headers_no_space (c, NULL, 0);
      }
      else
      {
        mhd_assert (c->rq.have_chunked_upload);
        if (c->rq.current_chunk_offset != c->rq.current_chunk_size)
        { /* Receiving content of the chunk */
          /* A header probably has been added to a suspended connection and
             it took precisely all the space in the buffer.
             Very low probability. */
          probe_log_open_and_write("[handle_recv_no_space] branch: BODY_CHUNKED (receiving content) -> handle_req_headers_no_space(c, NULL, 0)");
          handle_req_headers_no_space (c, NULL, 0);
        }
        else
        {
          if (0 != c->rq.current_chunk_size)
          { /* Waiting for chunk-closing CRLF */
            /* Not really possible as some payload should be
               processed and the space used by payload should be available. */
            probe_log_open_and_write("[handle_recv_no_space] branch: BODY_CHUNKED (waiting for CRLF) -> handle_req_headers_no_space(c, NULL, 0)");
            handle_req_headers_no_space (c, NULL, 0);
          }
          else
          { /* Reading the line with the chunk size */
            {
              char buf[256];
              snprintf(buf, sizeof(buf), "[handle_recv_no_space] branch: BODY_CHUNKED (reading chunk size) -> handle_req_chunk_size_line_no_space(c=%p, buffer=%p, len=%zu)",
                       (const void *)c, (const void *)c->read_buffer, (size_t)c->read_buffer_offset);
              probe_log_open_and_write(buf);
            }
            handle_req_chunk_size_line_no_space (c,
                                                 c->read_buffer,
                                                 c->read_buffer_offset);
          }
        }
      }
    }
    return;
  case MHD_PROC_RECV_FOOTERS:
    {
      char buf[256];
      snprintf(buf, sizeof(buf), "[handle_recv_no_space] branch: FOOTERS -> handle_req_footers_no_space(c=%p, buffer=%p, len=%zu)",
               (const void *)c, (const void *)c->read_buffer, (size_t)c->read_buffer_offset);
      probe_log_open_and_write(buf);
    }
    handle_req_footers_no_space (c, c->read_buffer, c->read_buffer_offset);
    return;
  /* The next cases should not be possible */
  case MHD_PROC_RECV_COOKIE:
  default:
    break;
  }
  mhd_assert (0);
}
