static enum MHD_Result
internal_get_fdset2 (struct MHD_Daemon *daemon,
                     fd_set *read_fd_set,
                     fd_set *write_fd_set,
                     fd_set *except_fd_set,
                     MHD_socket *max_fd,
                     int fd_setsize)
{
  struct MHD_Connection *pos;
  struct MHD_Connection *posn;
  enum MHD_Result result = MHD_YES;
  MHD_socket ls;
  bool itc_added;
  FILE *logf = NULL;
  int __probe_iter_count = 0;

#ifndef HAS_FD_SETSIZE_OVERRIDABLE
  (void) fd_setsize;  /* Mute compiler warning */
  fd_setsize = (int) FD_SETSIZE; /* Help compiler to optimise */
#endif /* ! HAS_FD_SETSIZE_OVERRIDABLE */

  /* Open log file for append. If opening fails, continue without logging. */
  logf = fopen("/tmp/logs/1124.txt", "a");
  if (NULL != logf)
  {
    fprintf(logf, "internal_get_fdset2 ENTER daemon=%p connections_tail=%p\n",
            (void *) daemon, (void *) daemon->connections_tail);
    fflush(logf);
  }

  if (daemon->shutdown)
  {
    if (NULL != logf)
    {
      fprintf(logf, "daemon->shutdown -> returning MHD_YES\n");
      fclose(logf);
    }
    return MHD_YES;
  }

  /* The order of FDs added is important for W32 sockets as W32 fd_set has
     limits for number of added FDs instead of the limit for the higher
     FD value. */

  /* Add ITC FD first. The daemon must be able to respond on application
     commands issued in other threads. */
  itc_added = false;
  if (MHD_ITC_IS_VALID_ (daemon->itc))
  {
    itc_added = MHD_add_to_fd_set_ (MHD_itc_r_fd_ (daemon->itc),
                                    read_fd_set,
                                    max_fd,
                                    fd_setsize);
    if (! itc_added)
      result = MHD_NO;
    if (NULL != logf)
    {
      fprintf(logf, "ITC present itc_added=%d result=%d\n",
              (int) itc_added, (int) result);
      fflush(logf);
    }
  }

  ls = daemon->was_quiesced ? MHD_INVALID_SOCKET : daemon->listen_fd;
  if (! itc_added &&
      (MHD_INVALID_SOCKET != ls))
  {
    /* Add listen FD if ITC was not added. Listen FD could be used to signal
       the daemon shutdown. */
    if (MHD_add_to_fd_set_ (ls,
                            read_fd_set,
                            max_fd,
                            fd_setsize))
      ls = MHD_INVALID_SOCKET;   /* Already added */
    else
      result = MHD_NO;
    if (NULL != logf)
    {
      fprintf(logf, "listen fd handling ls=%ld result=%d\n",
              (long) ls, (int) result);
      fflush(logf);
    }
  }

  /* Add all sockets to 'except_fd_set' as well to watch for
   * out-of-band data. However, ignore errors if INFO_READ
   * or INFO_WRITE sockets will not fit 'except_fd_set'. */
  /* Start from oldest connections. Make sense for W32 FDSETs. */
  if (NULL != logf)
  {
    fprintf(logf, "Entering main connections loop: initial pos=%p\n",
            (void *) daemon->connections_tail);
    fflush(logf);
  }

  for (pos = daemon->connections_tail; NULL != pos; pos = posn)
  {
    /* Probe before assigning posn: log current pos and its prev pointer */
    if (NULL != logf)
    {
      fprintf(logf, "ITER %d: loop top pos=%p\n", __probe_iter_count, (void *) pos);
      /* try to print some useful state guarded by pos != NULL */
      if (NULL != pos)
      {
        fprintf(logf, "  pos->socket_fd=%ld pos->prev=%p event_loop_info=%d\n",
                (long) pos->socket_fd,
                (void *) pos->prev,
                (int) pos->event_loop_info);
      }
      fflush(logf);
    }

    posn = pos->prev;

    /* Log posn assignment to show that posn is assigned in the body */
    if (NULL != logf)
    {
      fprintf(logf, "ITER %d: posn assigned to pos->prev -> posn=%p (will be used in for's iteration expr)\n",
              __probe_iter_count, (void *) posn);
      fflush(logf);
    }

    switch (pos->event_loop_info)
    {
    case MHD_EVENT_LOOP_INFO_READ:
    case MHD_EVENT_LOOP_INFO_PROCESS_READ:
      if (! MHD_add_to_fd_set_ (pos->socket_fd,
                                read_fd_set,
                                max_fd,
                                fd_setsize))
        result = MHD_NO;
#ifdef MHD_POSIX_SOCKETS
      if (NULL != except_fd_set)
        (void) MHD_add_to_fd_set_ (pos->socket_fd,
                                   except_fd_set,
                                   max_fd,
                                   fd_setsize);
#endif /* MHD_POSIX_SOCKETS */
      break;
    case MHD_EVENT_LOOP_INFO_WRITE:
      if (! MHD_add_to_fd_set_ (pos->socket_fd,
                                write_fd_set,
                                max_fd,
                                fd_setsize))
        result = MHD_NO;
#ifdef MHD_POSIX_SOCKETS
      if (NULL != except_fd_set)
        (void) MHD_add_to_fd_set_ (pos->socket_fd,
                                   except_fd_set,
                                   max_fd,
                                   fd_setsize);
#endif /* MHD_POSIX_SOCKETS */
      break;
    case MHD_EVENT_LOOP_INFO_PROCESS:
      if ( (NULL == except_fd_set) ||
           ! MHD_add_to_fd_set_ (pos->socket_fd,
                                 except_fd_set,
                                 max_fd,
                                 fd_setsize))
        result = MHD_NO;
      break;
    case MHD_EVENT_LOOP_INFO_CLEANUP:
      /* this should never happen */
      break;
    }

    /* Log end of iteration (before for-loop's iteration expression executes) */
    if (NULL != logf)
    {
      fprintf(logf, "ITER %d: end of body pos=%p posn=%p result=%d\n",
              __probe_iter_count, (void *) pos, (void *) posn, (int) result);
      fflush(logf);
    }

    __probe_iter_count++;
  }

  if (NULL != logf)
  {
    fprintf(logf, "Exited main connections loop after %d iterations, pos=%p\n",
            __probe_iter_count, (void *) pos);
    fflush(logf);
  }

#ifdef MHD_WINSOCK_SOCKETS
  /* W32 use limited array for fd_set so add INFO_READ/INFO_WRITE sockets
   * only after INFO_BLOCK sockets to ensure that INFO_BLOCK sockets will
   * not be pushed out. */
  if (NULL != except_fd_set)
  {
    if (NULL != logf)
    {
      fprintf(logf, "Entering WINSOCK except_fd_set loop initial pos=%p\n",
              (void *) daemon->connections_tail);
      fflush(logf);
    }

    for (pos = daemon->connections_tail; NULL != pos; pos = posn)
    {
      posn = pos->prev;
      if (NULL != logf)
      {
        fprintf(logf, "W32 ITER: pos=%p pos->socket_fd=%ld posn=%p\n",
                (void *) pos, (long) pos->socket_fd, (void *) posn);
        fflush(logf);
      }
      MHD_add_to_fd_set_ (pos->socket_fd,
                          except_fd_set,
                          max_fd,
                          fd_setsize);
    }
  }
#endif /* MHD_WINSOCK_SOCKETS */
#if defined(HTTPS_SUPPORT) && defined(UPGRADE_SUPPORT)
  if (1)
  {
    struct MHD_UpgradeResponseHandle *urh;

    for (urh = daemon->urh_tail; NULL != urh; urh = urh->prev)
    {
      if (MHD_NO ==
          urh_to_fdset (urh,
                        read_fd_set,
                        write_fd_set,
                        except_fd_set,
                        max_fd,
                        fd_setsize))
        result = MHD_NO;
    }
  }
#endif

  if (MHD_INVALID_SOCKET != ls)
  {
    /* The listen socket is present and hasn't been added */
    if ((daemon->connections < daemon->connection_limit) &&
        ! daemon->at_limit)
    {
      if (! MHD_add_to_fd_set_ (ls,
                                read_fd_set,
                                max_fd,
                                fd_setsize))
        result = MHD_NO;
    }
  }

#if _MHD_DEBUG_CONNECT
#ifdef HAVE_MESSAGES
  if (NULL != max_fd)
    MHD_DLOG (daemon,
              _ ("Maximum socket in select set: %d\n"),
              *max_fd);
#endif
#endif /* HTTPS_SUPPORT && UPGRADE_SUPPORT */

  if (NULL != logf)
  {
    fprintf(logf, "internal_get_fdset2 EXIT result=%d max_fd=%p ls=%ld\n",
            (int) result, (void *) max_fd, (long) ls);
    fclose(logf);
  }
  return result;
}
