#include <stdio.h>
#include <stdarg.h>

/* Probe logging helper: appends human-readable logs to /tmp/logs/1124.txt */
static void mhd_probe_log(const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/1124.txt", "a");
  if (NULL == f)
    return;

  va_list ap;
  va_start(ap, fmt);
  (void) vfprintf(f, fmt, ap);
  va_end(ap);
  (void) fprintf(f, "\n");
  (void) fclose(f);
}

static enum MHD_Result
internal_get_fdset2 (struct MHD_Daemon *daemon,
                     fd_set *read_fd_set,
                     fd_set *write_fd_set,
                     fd_set *except_fd_set,
                     MHD_socket *max_fd,
                     int fd_setsize)
{
  struct MHD_Connection *pos;
  struct MHD_Connection *posn;
  enum MHD_Result result = MHD_YES;
  MHD_socket ls;
  bool itc_added;

#ifndef HAS_FD_SETSIZE_OVERRIDABLE
  (void) fd_setsize;  /* Mute compiler warning */
  fd_setsize = (int) FD_SETSIZE; /* Help compiler to optimise */
#endif /* ! HAS_FD_SETSIZE_OVERRIDABLE */

  /* Log entry and key daemon state */
  mhd_probe_log("internal_get_fdset2 ENTER daemon=%p shutdown=%d listen_fd=%ld urh_tail=%p connections=%u connection_limit=%u at_limit=%d",
                (void *) daemon,
                (int) daemon->shutdown,
                (long) daemon->listen_fd,
                (void *) daemon->urh_tail,
                (unsigned) daemon->connections,
                (unsigned) daemon->connection_limit,
                (int) daemon->at_limit);

  if (daemon->shutdown)
    return MHD_YES;

  /* The order of FDs added is important for W32 sockets as W32 fd_set has
     limits for number of added FDs instead of the limit for the higher
     FD value. */

  /* Add ITC FD first. The daemon must be able to respond on application
     commands issued in other threads. */
  itc_added = false;
  if (MHD_ITC_IS_VALID_ (daemon->itc))
  {
    itc_added = MHD_add_to_fd_set_ (MHD_itc_r_fd_ (daemon->itc),
                                    read_fd_set,
                                    max_fd,
                                    fd_setsize);
    if (! itc_added)
      result = MHD_NO;
  }

  ls = daemon->was_quiesced ? MHD_INVALID_SOCKET : daemon->listen_fd;
  if (! itc_added &&
      (MHD_INVALID_SOCKET != ls))
  {
    /* Add listen FD if ITC was not added. Listen FD could be used to signal
       the daemon shutdown. */
    if (MHD_add_to_fd_set_ (ls,
                            read_fd_set,
                            max_fd,
                            fd_setsize))
      ls = MHD_INVALID_SOCKET;   /* Already added */
    else
      result = MHD_NO;
  }

  /* Add all sockets to 'except_fd_set' as well to watch for
   * out-of-band data. However, ignore errors if INFO_READ
   * or INFO_WRITE sockets will not fit 'except_fd_set'. */
  /* Start from oldest connections. Make sense for W32 FDSETs. */
  for (pos = daemon->connections_tail; NULL != pos; pos = posn)
  {
    posn = pos->prev;

    switch (pos->event_loop_info)
    {
    case MHD_EVENT_LOOP_INFO_READ:
    case MHD_EVENT_LOOP_INFO_PROCESS_READ:
      if (! MHD_add_to_fd_set_ (pos->socket_fd,
                                read_fd_set,
                                max_fd,
                                fd_setsize))
        result = MHD_NO;
#ifdef MHD_POSIX_SOCKETS
      if (NULL != except_fd_set)
        (void) MHD_add_to_fd_set_ (pos->socket_fd,
                                   except_fd_set,
                                   max_fd,
                                   fd_setsize);
#endif /* MHD_POSIX_SOCKETS */
      break;
    case MHD_EVENT_LOOP_INFO_WRITE:
      if (! MHD_add_to_fd_set_ (pos->socket_fd,
                                write_fd_set,
                                max_fd,
                                fd_setsize))
        result = MHD_NO;
#ifdef MHD_POSIX_SOCKETS
      if (NULL != except_fd_set)
        (void) MHD_add_to_fd_set_ (pos->socket_fd,
                                   except_fd_set,
                                   max_fd,
                                   fd_setsize);
#endif /* MHD_POSIX_SOCKETS */
      break;
    case MHD_EVENT_LOOP_INFO_PROCESS:
      if ( (NULL == except_fd_set) ||
           ! MHD_add_to_fd_set_ (pos->socket_fd,
                                 except_fd_set,
                                 max_fd,
                                 fd_setsize))
        result = MHD_NO;
      break;
    case MHD_EVENT_LOOP_INFO_CLEANUP:
      /* this should never happen */
      break;
    }
  }
#ifdef MHD_WINSOCK_SOCKETS
  /* W32 use limited array for fd_set so add INFO_READ/INFO_WRITE sockets
   * only after INFO_BLOCK sockets to ensure that INFO_BLOCK sockets will
   * not be pushed out. */
  if (NULL != except_fd_set)
  {
    for (pos = daemon->connections_tail; NULL != pos; pos = posn)
    {
      posn = pos->prev;
      MHD_add_to_fd_set_ (pos->socket_fd,
                          except_fd_set,
                          max_fd,
                          fd_setsize);
    }
  }
#endif /* MHD_WINSOCK_SOCKETS */
#if defined(HTTPS_SUPPORT) && defined(UPGRADE_SUPPORT)
  if (1)
  {
    struct MHD_UpgradeResponseHandle *urh;

    /* Log the fact that we are about to enter the upgrade-response loop and
       capture the tail pointer. This loop's third clause uses 'urh = urh->prev',
       which is the subject of the reported MISRA 14.2 concern. */
    mhd_probe_log("URH loop START urh_tail=%p (third clause: urh = urh->prev)", (void *) daemon->urh_tail);

    for (urh = daemon->urh_tail; NULL != urh; urh = urh->prev)
    {
      /* Log the current urh and the value of urh->prev as observed before
         calling urh_to_fdset. Reading urh->prev here is the conservative and
         safe action (performed before the body call). We avoid dereferencing
         urh after calling urh_to_fdset to reduce the risk of use-after-free
         if that function frees the object. */
      mhd_probe_log("URH ITER: urh=%p urh->prev(before call)=%p",
                    (void *) urh,
                    (void *) urh->prev);

      enum MHD_Result urh_ret = urh_to_fdset (urh,
                                              read_fd_set,
                                              write_fd_set,
                                              except_fd_set,
                                              max_fd,
                                              fd_setsize);

      if (MHD_NO == urh_ret)
        result = MHD_NO;

      /* Log return from urh_to_fdset. Do NOT dereference 'urh' here since
         urh_to_fdset may free or modify 'urh' (this is precisely the
         behaviour that could lead to the MISRA 14.2 violation). Logging the
         pointer value and the function return gives evidence of the call and
         the state seen before the call. */
      mhd_probe_log("URH ITER RETURN: urh=%p urh_to_fdset_return=%d",
                    (void *) urh,
                    (int) urh_ret);
    }
  }
#endif

  if (MHD_INVALID_SOCKET != ls)
  {
    /* The listen socket is present and hasn't been added */
    if ((daemon->connections < daemon->connection_limit) &&
        ! daemon->at_limit)
    {
      if (! MHD_add_to_fd_set_ (ls,
                                read_fd_set,
                                max_fd,
                                fd_setsize))
        result = MHD_NO;
    }
  }

#if _MHD_DEBUG_CONNECT
#ifdef HAVE_MESSAGES
  if (NULL != max_fd)
    MHD_DLOG (daemon,
              _ ("Maximum socket in select set: %d\n"),
              *max_fd);
#endif
#endif /* HTTPS_SUPPORT && UPGRADE_SUPPORT */

  mhd_probe_log("internal_get_fdset2 LEAVE daemon=%p result=%d",
                (void *) daemon, (int) result);

  return result;
}
