static void mhd_log_probe(const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/1132.txt", "a");
  if (NULL == f)
    return;
  va_list ap;
  va_start(ap, fmt);
  vfprintf(f, fmt, ap);
  fprintf(f, "\n");
  va_end(ap);
  fclose(f);
}

static enum MHD_Result
internal_run_from_select (struct MHD_Daemon *daemon,
                          const fd_set *read_fd_set,
                          const fd_set *write_fd_set,
                          const fd_set *except_fd_set,
                          int fd_setsize)
{
  MHD_socket ds;
#if defined(HTTPS_SUPPORT) && defined(UPGRADE_SUPPORT)
  struct MHD_UpgradeResponseHandle *urh;
  struct MHD_UpgradeResponseHandle *urhn;
#endif /* HTTPS_SUPPORT && UPGRADE_SUPPORT */

  /* Log entry and key daemon state (for reproducing assertion-related behaviour) */
  mhd_log_probe("internal_run_from_select: enter daemon=%p options=0x%08x tid=%p fd_setsize=%d listen_fd=%d have_new=%d was_quiesced=%d",
                (void *) daemon,
                (unsigned) daemon->options,
                (void *) daemon->tid,
                fd_setsize,
                (int) daemon->listen_fd,
                (int) daemon->have_new,
                (int) daemon->was_quiesced);

  /* Probe the predicates that are wrapped in mhd_assert to record whether
     they would evaluate true/false at runtime. This helps to capture whether
     these checks are purely diagnostic (i.e. potentially removable) or
     whether they reflect essential state. */
  {
    bool use_select_flag = (0 != (daemon->options & MHD_USE_SELECT_INTERNALLY));
    bool thread_valid = MHD_thread_handle_ID_is_valid_ID_ (daemon->tid);
    bool thread_current = MHD_thread_handle_ID_is_current_thread_ (daemon->tid);

    mhd_log_probe("assert-probe: use_select_flag=%d thread_valid=%d thread_current=%d",
                  (int) use_select_flag, (int) thread_valid, (int) thread_current);
  }

  mhd_assert ((0 == (daemon->options & MHD_USE_SELECT_INTERNALLY)) || \
              (MHD_thread_handle_ID_is_valid_ID_ (daemon->tid)));
  mhd_assert ((0 != (daemon->options & MHD_USE_SELECT_INTERNALLY)) || \
              (! MHD_thread_handle_ID_is_valid_ID_ (daemon->tid)));
  mhd_assert ((0 == (daemon->options & MHD_USE_SELECT_INTERNALLY)) || \
              (MHD_thread_handle_ID_is_current_thread_ (daemon->tid)));

  mhd_assert (0 < fd_setsize);
  (void) fd_setsize; /* Mute compiler warning */
#ifndef HAS_FD_SETSIZE_OVERRIDABLE
  (void) fd_setsize; /* Mute compiler warning */
  mhd_assert (((int) FD_SETSIZE) <= fd_setsize);
  fd_setsize = FD_SETSIZE; /* Help compiler to optimise */
#endif /* ! HAS_FD_SETSIZE_OVERRIDABLE */

  /* Clear ITC to avoid spinning select */
  /* Do it before any other processing so new signals
     will trigger select again and will be processed */
  if (MHD_ITC_IS_VALID_ (daemon->itc))
  { /* Have ITC */
    bool need_to_clear_itc = true; /* ITC is always non-blocking, it is safe to clear even if ITC not activated */
    if (MHD_SCKT_FD_FITS_FDSET_SETSIZE_ (MHD_itc_r_fd_ (daemon->itc),
                                         NULL, fd_setsize))
      need_to_clear_itc = FD_ISSET (MHD_itc_r_fd_ (daemon->itc), \
                                    (fd_set *) _MHD_DROP_CONST (read_fd_set)); /* Skip clearing, if not needed */

    mhd_log_probe("itc-probe: itc_valid=1 itc_r_fd=%d fits_fdset=%d need_to_clear_before=%d",
                  (int) MHD_itc_r_fd_ (daemon->itc),
                  (int) MHD_SCKT_FD_FITS_FDSET_SETSIZE_ (MHD_itc_r_fd_ (daemon->itc), NULL, fd_setsize),
                  (int) need_to_clear_itc);

    if (need_to_clear_itc)
      MHD_itc_clear_ (daemon->itc);
  }
  else
  {
    mhd_log_probe("itc-probe: itc_valid=0");
  }

  /* Reset. New value will be set when connections are processed. */
  /* Note: no-op for thread-per-connection as it is always false in that mode. */
  daemon->data_already_pending = false;
  mhd_log_probe("state: data_already_pending set to false");

  /* Process externally added connection if any */
  if (daemon->have_new)
  {
    mhd_log_probe("process: have_new is true -> new_connections_list_process_");
    new_connections_list_process_ (daemon);
  }
  else
  {
    mhd_log_probe("process: have_new is false");
  }

  /* select connection thread handling type */
  ds = daemon->listen_fd;
  if ( (MHD_INVALID_SOCKET != ds) &&
       (! daemon->was_quiesced) )
  {
    bool need_to_accept;
    if (MHD_SCKT_FD_FITS_FDSET_SETSIZE_ (ds, NULL, fd_setsize))
      need_to_accept = FD_ISSET (ds,
                                 (fd_set *) _MHD_DROP_CONST (read_fd_set));
    else                                       /* Cannot check whether new connection are pending */
      need_to_accept = daemon->listen_nonblk;  /* Try to accept if non-blocking */

    mhd_log_probe("listen-probe: listen_fd=%d fits_fdset=%d need_to_accept=%d listen_nonblk=%d",
                  (int) ds,
                  (int) MHD_SCKT_FD_FITS_FDSET_SETSIZE_ (ds, NULL, fd_setsize),
                  (int) need_to_accept,
                  (int) daemon->listen_nonblk);

    if (need_to_accept)
    {
      mhd_log_probe("listen-probe: calling MHD_accept_connection daemon=%p", (void *) daemon);
      (void) MHD_accept_connection (daemon);
    }
  }
  else
  {
    mhd_log_probe("listen-probe: no listen socket or was_quiesced (ds=%d was_quiesced=%d)", (int) ds, (int) daemon->was_quiesced);
  }

  if (! MHD_D_IS_USING_THREAD_PER_CONN_ (daemon))
  {
    /* do not have a thread per connection, process all connections now */
    struct MHD_Connection *pos;
    for (pos = daemon->connections_tail; NULL != pos; pos = pos->prev)
    {
      MHD_socket cs;
      bool r_ready;
      bool w_ready;
      bool has_err;

      cs = pos->socket_fd;
      if (MHD_INVALID_SOCKET == cs)
        continue;

      if (MHD_SCKT_FD_FITS_FDSET_SETSIZE_ (cs, NULL, fd_setsize))
      {
        r_ready = FD_ISSET (cs,
                            (fd_set *) _MHD_DROP_CONST (read_fd_set));
        w_ready = FD_ISSET (cs,
                            (fd_set *) _MHD_DROP_CONST (write_fd_set));
        has_err = (NULL != except_fd_set) &&
                  FD_ISSET (cs,
                            (fd_set *) _MHD_DROP_CONST (except_fd_set));
      }
      else
      { /* Cannot check the real readiness */
        r_ready = pos->sk_nonblck;
        w_ready = r_ready;
        has_err = false;
      }

      mhd_log_probe("conn-probe: pos=%p cs=%d r_ready=%d w_ready=%d has_err=%d sk_nonblck=%d",
                    (void *) pos, (int) cs, (int) r_ready, (int) w_ready, (int) has_err, (int) pos->sk_nonblck);

      call_handlers (pos,
                     r_ready,
                     w_ready,
                     has_err);
    }
  }

#if defined(HTTPS_SUPPORT) && defined(UPGRADE_SUPPORT)
  /* handle upgraded HTTPS connections */
  for (urh = daemon->urh_tail; NULL != urh; urh = urhn)
  {
    urhn = urh->prev;
    /* update urh state based on select() output */
    urh_from_fdset (urh,
                    read_fd_set,
                    write_fd_set,
                    except_fd_set,
                    fd_setsize);
    /* call generic forwarding function for passing data */
    process_urh (urh);
    /* Finished forwarding? */
    if ( (0 == urh->in_buffer_size) &&
         (0 == urh->out_buffer_size) &&
         (0 == urh->in_buffer_used) &&
         (0 == urh->out_buffer_used) )
    {
      MHD_connection_finish_forward_ (urh->connection);
      urh->clean_ready = true;
      /* Resuming will move connection to cleanup list. */
      MHD_resume_connection (urh->connection);
    }
  }
#endif /* HTTPS_SUPPORT && UPGRADE_SUPPORT */
  mhd_log_probe("cleanup: calling MHD_cleanup_connections daemon=%p", (void *) daemon);
  MHD_cleanup_connections (daemon);
  mhd_log_probe("internal_run_from_select: exit returning MHD_YES");
  return MHD_YES;
}
