/* Probe logging helpers inserted to capture control flow and state for MISRA Rule 8.13 investigation. */
#include <stdio.h>
#include <stdarg.h>
#include <time.h>

static void probe_log(const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/1133.txt", "a");
  if (NULL == f)
    return;
  {
    time_t t = time(NULL);
    struct tm *tm = gmtime(&t);
    if (tm)
      fprintf(f, "%04d-%02d-%02dT%02d:%02d:%02dZ ", tm->tm_year + 1900,
              tm->tm_mon + 1, tm->tm_mday, tm->tm_hour, tm->tm_min, tm->tm_sec);
  }
  va_list ap;
  va_start(ap, fmt);
  vfprintf(f, fmt, ap);
  va_end(ap);
  fputc('\n', f);
  fclose(f);
}

static void probe_log_fdset_state(const char *where,
                                  const fd_set *read_fd_set,
                                  const fd_set *write_fd_set,
                                  const fd_set *except_fd_set,
                                  int fd_setsize)
{
  probe_log("%s: read_fd_set=%p, write_fd_set=%p, except_fd_set=%p, fd_setsize=%d",
            where, (const void *) read_fd_set, (const void *) write_fd_set,
            (const void *) except_fd_set, fd_setsize);
}

static enum MHD_Result
internal_run_from_select (struct MHD_Daemon *daemon,
                          const fd_set *read_fd_set,
                          const fd_set *write_fd_set,
                          const fd_set *except_fd_set,
                          int fd_setsize)
{
  MHD_socket ds;
#if defined(HTTPS_SUPPORT) && defined(UPGRADE_SUPPORT)
  struct MHD_UpgradeResponseHandle *urh;
  struct MHD_UpgradeResponseHandle *urhn;
#endif /* HTTPS_SUPPORT && UPGRADE_SUPPORT */

  /* Log entry and basic daemon/options state */
  probe_log("internal_run_from_select: entry daemon=%p options=0x%08x tid=%p",
            (void *) daemon, (unsigned) daemon->options, (void *) daemon->tid);

  mhd_assert ((0 == (daemon->options & MHD_USE_SELECT_INTERNALLY)) || \
              (MHD_thread_handle_ID_is_valid_ID_ (daemon->tid)));
  mhd_assert ((0 != (daemon->options & MHD_USE_SELECT_INTERNALLY)) || \
              (! MHD_thread_handle_ID_is_valid_ID_ (daemon->tid)));
  mhd_assert ((0 == (daemon->options & MHD_USE_SELECT_INTERNALLY)) || \
              (MHD_thread_handle_ID_is_current_thread_ (daemon->tid)));

  mhd_assert (0 < fd_setsize);
  (void) fd_setsize; /* Mute compiler warning */
#ifndef HAS_FD_SETSIZE_OVERRIDABLE
  (void) fd_setsize; /* Mute compiler warning */
  mhd_assert (((int) FD_SETSIZE) <= fd_setsize);
  fd_setsize = FD_SETSIZE; /* Help compiler to optimise */
#endif /* ! HAS_FD_SETSIZE_OVERRIDABLE */

  /* Log fd_set pointers and size at function start */
  probe_log_fdset_state("start", read_fd_set, write_fd_set, except_fd_set, fd_setsize);

  /* Clear ITC to avoid spinning select */
  /* Do it before any other processing so new signals
     will trigger select again and will be processed */
  if (MHD_ITC_IS_VALID_ (daemon->itc))
  { /* Have ITC */
    bool need_to_clear_itc = true; /* ITC is always non-blocking, it is safe to clear even if ITC not activated */
    if (MHD_SCKT_FD_FITS_FDSET_SETSIZE_ (MHD_itc_r_fd_ (daemon->itc),
                                         NULL, fd_setsize))
    {
      probe_log("Checking ITC FD_ISSET with read_fd_set (const)=%p casting away const for FD_ISSET",
                (const void *) read_fd_set);
      /* Skip clearing, if not needed */
      need_to_clear_itc = FD_ISSET (MHD_itc_r_fd_ (daemon->itc), \
                                    (fd_set *) _MHD_DROP_CONST (read_fd_set));
      probe_log("ITC FD_ISSET result need_to_clear_itc=%d (itc_r_fd=%d)",
                need_to_clear_itc, (int) MHD_itc_r_fd_ (daemon->itc));
    }
    if (need_to_clear_itc)
    {
      probe_log("Clearing ITC (daemon->itc=%p)", (void *) daemon->itc);
      MHD_itc_clear_ (daemon->itc);
    }
  }

  /* Reset. New value will be set when connections are processed. */
  /* Note: no-op for thread-per-connection as it is always false in that mode. */
  daemon->data_already_pending = false;

  /* Process externally added connection if any */
  if (daemon->have_new)
  {
    probe_log("Processing externally added connections (have_new=true)");
    new_connections_list_process_ (daemon);
  }

  /* select connection thread handling type */
  ds = daemon->listen_fd;
  if ( (MHD_INVALID_SOCKET != ds) &&
       (! daemon->was_quiesced) )
  {
    bool need_to_accept;
    if (MHD_SCKT_FD_FITS_FDSET_SETSIZE_ (ds, NULL, fd_setsize))
    {
      probe_log("Checking listen fd (%d) in read_fd_set (const=%p) casting away const for FD_ISSET",
                (int) ds, (const void *) read_fd_set);
      need_to_accept = FD_ISSET (ds,
                                 (fd_set *) _MHD_DROP_CONST (read_fd_set));
      probe_log("listen FD_ISSET result need_to_accept=%d (listen_fd=%d)", need_to_accept, (int) ds);
    }
    else                                       /* Cannot check whether new connection are pending */
      need_to_accept = daemon->listen_nonblk;  /* Try to accept if non-blocking */

    if (need_to_accept)
    {
      probe_log("Calling MHD_accept_connection (daemon=%p)", (void *) daemon);
      (void) MHD_accept_connection (daemon);
    }
  }

  if (! MHD_D_IS_USING_THREAD_PER_CONN_ (daemon))
  {
    /* do not have a thread per connection, process all connections now */
    struct MHD_Connection *pos;
    for (pos = daemon->connections_tail; NULL != pos; pos = pos->prev)
    {
      MHD_socket cs;
      bool r_ready;
      bool w_ready;
      bool has_err;

      cs = pos->socket_fd;
      if (MHD_INVALID_SOCKET == cs)
        continue;

      if (MHD_SCKT_FD_FITS_FDSET_SETSIZE_ (cs, NULL, fd_setsize))
      {
        probe_log("Checking connection socket %d in fd_sets (const ptrs read=%p write=%p except=%p)",
                  (int) cs, (const void *) read_fd_set, (const void *) write_fd_set,
                  (const void *) except_fd_set);
        r_ready = FD_ISSET (cs,
                            (fd_set *) _MHD_DROP_CONST (read_fd_set));
        w_ready = FD_ISSET (cs,
                            (fd_set *) _MHD_DROP_CONST (write_fd_set));
        has_err = (NULL != except_fd_set) &&
                  FD_ISSET (cs,
                            (fd_set *) _MHD_DROP_CONST (except_fd_set));
        probe_log("Socket %d readiness: r_ready=%d w_ready=%d has_err=%d",
                  (int) cs, (int) r_ready, (int) w_ready, (int) has_err);
      }
      else
      { /* Cannot check the real readiness */
        r_ready = pos->sk_nonblck;
        w_ready = r_ready;
        has_err = false;
        probe_log("Socket %d not representable in fd_set; falling back to sk_nonblck=%d",
                  (int) cs, (int) pos->sk_nonblck);
      }
      call_handlers (pos,
                     r_ready,
                     w_ready,
                     has_err);
    }
  }

#if defined(HTTPS_SUPPORT) && defined(UPGRADE_SUPPORT)
  /* handle upgraded HTTPS connections */
  for (urh = daemon->urh_tail; NULL != urh; urh = urhn)
  {
    urhn = urh->prev;
    /* update urh state based on select() output */
    probe_log("About to call urh_from_fdset for urh=%p: fd_set addrs read=%p write=%p except=%p fd_setsize=%d",
              (void *) urh, (const void *) read_fd_set, (const void *) write_fd_set,
              (const void *) except_fd_set, fd_setsize);
    /* The following call mirrors the original behavior; it casts away const where necessary. */
    urh_from_fdset (urh,
                    read_fd_set,
                    write_fd_set,
                    except_fd_set,
                    fd_setsize);
    probe_log("Returned from urh_from_fdset for urh=%p", (void *) urh);
    /* call generic forwarding function for passing data */
    process_urh (urh);
    /* Finished forwarding? */
    if ( (0 == urh->in_buffer_size) &&
         (0 == urh->out_buffer_size) &&
         (0 == urh->in_buffer_used) &&
         (0 == urh->out_buffer_used) )
    {
      probe_log("urh=%p finished forwarding; calling MHD_connection_finish_forward_ and marking clean_ready", (void *) urh);
      MHD_connection_finish_forward_ (urh->connection);
      urh->clean_ready = true;
      /* Resuming will move connection to cleanup list. */
      MHD_resume_connection (urh->connection);
    }
  }
#endif /* HTTPS_SUPPORT && UPGRADE_SUPPORT */
  probe_log("Calling MHD_cleanup_connections (daemon=%p)", (void *) daemon);
  MHD_cleanup_connections (daemon);
  probe_log("internal_run_from_select: exit.");
  return MHD_YES;
}
