static enum MHD_Result
internal_run_from_select (struct MHD_Daemon *daemon,
                          const fd_set *read_fd_set,
                          const fd_set *write_fd_set,
                          const fd_set *except_fd_set,
                          int fd_setsize)
{
  MHD_socket ds;
#if defined(HTTPS_SUPPORT) && defined(UPGRADE_SUPPORT)
  struct MHD_UpgradeResponseHandle *urh;
  struct MHD_UpgradeResponseHandle *urhn;
#endif /* HTTPS_SUPPORT && UPGRADE_SUPPORT */
  FILE *logf = NULL;

  mhd_assert ((0 == (daemon->options & MHD_USE_SELECT_INTERNALLY)) || \
              (MHD_thread_handle_ID_is_valid_ID_ (daemon->tid)));
  mhd_assert ((0 != (daemon->options & MHD_USE_SELECT_INTERNALLY)) || \
              (! MHD_thread_handle_ID_is_valid_ID_ (daemon->tid)));
  mhd_assert ((0 == (daemon->options & MHD_USE_SELECT_INTERNALLY)) || \
              (MHD_thread_handle_ID_is_current_thread_ (daemon->tid)));

  mhd_assert (0 < fd_setsize);
  (void) fd_setsize; /* Mute compiler warning */
#ifndef HAS_FD_SETSIZE_OVERRIDABLE
  (void) fd_setsize; /* Mute compiler warning */
  mhd_assert (((int) FD_SETSIZE) <= fd_setsize);
  fd_setsize = FD_SETSIZE; /* Help compiler to optimise */
#endif /* ! HAS_FD_SETSIZE_OVERRIDABLE */

  /* Clear ITC to avoid spinning select */
  /* Do it before any other processing so new signals
     will trigger select again and will be processed */
  if (MHD_ITC_IS_VALID_ (daemon->itc))
  { /* Have ITC */
    bool need_to_clear_itc = true; /* ITC is always non-blocking, it is safe to clear even if ITC not activated */
    if (MHD_SCKT_FD_FITS_FDSET_SETSIZE_ (MHD_itc_r_fd_ (daemon->itc),
                                         NULL, fd_setsize))
    {
      /* Log probe: ITC fd fits fdset size and FD_ISSET will be called with a cast from const */
      logf = fopen("/tmp/logs/1133.txt", "a");
      if (logf)
      {
        fprintf(logf, "[internal_run_from_select] ITC check: itc_fd=%d, read_fd_set=%p, fd_setsize=%d - calling FD_ISSET with cast to non-const\n",
                (int) MHD_itc_r_fd_ (daemon->itc), (const void *) read_fd_set, fd_setsize);
        fclose(logf);
      }

      need_to_clear_itc = FD_ISSET (MHD_itc_r_fd_ (daemon->itc), \
                                    (fd_set *) _MHD_DROP_CONST (read_fd_set)); /* Skip clearing, if not needed */

      /* Log the result of FD_ISSET for ITC */
      logf = fopen("/tmp/logs/1133.txt", "a");
      if (logf)
      {
        fprintf(logf, "[internal_run_from_select] ITC FD_ISSET result: need_to_clear_itc=%d\n", (int) need_to_clear_itc);
        fclose(logf);
      }
    }
    if (need_to_clear_itc)
      MHD_itc_clear_ (daemon->itc);
  }

  /* Reset. New value will be set when connections are processed. */
  /* Note: no-op for thread-per-connection as it is always false in that mode. */
  daemon->data_already_pending = false;

  /* Process externally added connection if any */
  if (daemon->have_new)
    new_connections_list_process_ (daemon);

  /* select connection thread handling type */
  ds = daemon->listen_fd;
  if ( (MHD_INVALID_SOCKET != ds) &&
       (! daemon->was_quiesced) )
  {
    bool need_to_accept;
    if (MHD_SCKT_FD_FITS_FDSET_SETSIZE_ (ds, NULL, fd_setsize))
    {
      /* Log probe: listening socket check before FD_ISSET with cast */
      logf = fopen("/tmp/logs/1133.txt", "a");
      if (logf)
      {
        fprintf(logf, "[internal_run_from_select] listen fd check: listen_fd=%d, read_fd_set=%p, fd_setsize=%d - calling FD_ISSET with cast to non-const\n",
                (int) ds, (const void *) read_fd_set, fd_setsize);
        fclose(logf);
      }

      need_to_accept = FD_ISSET (ds,
                                 (fd_set *) _MHD_DROP_CONST (read_fd_set));

      /* Log the decision on whether to accept */
      logf = fopen("/tmp/logs/1133.txt", "a");
      if (logf)
      {
        fprintf(logf, "[internal_run_from_select] listen FD_ISSET result: need_to_accept=%d\n", (int) need_to_accept);
        fclose(logf);
      }
    }
    else                                       /* Cannot check whether new connection are pending */
      need_to_accept = daemon->listen_nonblk;  /* Try to accept if non-blocking */

    if (need_to_accept)
      (void) MHD_accept_connection (daemon);
  }

  if (! MHD_D_IS_USING_THREAD_PER_CONN_ (daemon))
  {
    /* do not have a thread per connection, process all connections now */
    struct MHD_Connection *pos;
    for (pos = daemon->connections_tail; NULL != pos; pos = pos->prev)
    {
      MHD_socket cs;
      bool r_ready;
      bool w_ready;
      bool has_err;

      cs = pos->socket_fd;
      if (MHD_INVALID_SOCKET == cs)
        continue;

      if (MHD_SCKT_FD_FITS_FDSET_SETSIZE_ (cs, NULL, fd_setsize))
      {
        /* Log probe: about to check connection socket readiness with FD_ISSET and cast to non-const */
        logf = fopen("/tmp/logs/1133.txt", "a");
        if (logf)
        {
          fprintf(logf, "[internal_run_from_select] conn socket check: conn=%p, socket_fd=%d, read_fd_set=%p, write_fd_set=%p, except_fd_set=%p, fd_setsize=%d - calling FD_ISSET with cast to non-const\n",
                  (const void *) pos, (int) cs, (const void *) read_fd_set, (const void *) write_fd_set, (const void *) except_fd_set, fd_setsize);
          fclose(logf);
        }

        r_ready = FD_ISSET (cs,
                            (fd_set *) _MHD_DROP_CONST (read_fd_set));
        w_ready = FD_ISSET (cs,
                            (fd_set *) _MHD_DROP_CONST (write_fd_set));
        has_err = (NULL != except_fd_set) &&
                  FD_ISSET (cs,
                            (fd_set *) _MHD_DROP_CONST (except_fd_set));

        /* Log readiness results for this connection */
        logf = fopen("/tmp/logs/1133.txt", "a");
        if (logf)
        {
          fprintf(logf, "[internal_run_from_select] conn readiness: socket_fd=%d, r_ready=%d, w_ready=%d, has_err=%d\n",
                  (int) cs, (int) r_ready, (int) w_ready, (int) has_err);
          fclose(logf);
        }
      }
      else
      { /* Cannot check the real readiness */
        r_ready = pos->sk_nonblck;
        w_ready = r_ready;
        has_err = false;

        /* Log fallback readiness values */
        logf = fopen("/tmp/logs/1133.txt", "a");
        if (logf)
        {
          fprintf(logf, "[internal_run_from_select] conn fallback readiness: socket_fd=%d, sk_nonblck=%d -> r_ready=%d, w_ready=%d, has_err=%d\n",
                  (int) cs, (int) pos->sk_nonblck, (int) r_ready, (int) w_ready, (int) has_err);
          fclose(logf);
        }
      }
      call_handlers (pos,
                     r_ready,
                     w_ready,
                     has_err);
    }
  }

#if defined(HTTPS_SUPPORT) && defined(UPGRADE_SUPPORT)
  /* handle upgraded HTTPS connections */
  for (urh = daemon->urh_tail; NULL != urh; urh = urhn)
  {
    urhn = urh->prev;
    /* update urh state based on select() output */
    /* Log probe: urh_from_fdset invocation with const fd_set pointers (no cast) */
    logf = fopen("/tmp/logs/1133.txt", "a");
    if (logf)
    {
      fprintf(logf, "[internal_run_from_select] urh_from_fdset: urh=%p, read_fd_set=%p, write_fd_set=%p, except_fd_set=%p, fd_setsize=%d\n",
              (const void *) urh, (const void *) read_fd_set, (const void *) write_fd_set, (const void *) except_fd_set, fd_setsize);
      fclose(logf);
    }

    urh_from_fdset (urh,
                    read_fd_set,
                    write_fd_set,
                    except_fd_set,
                    fd_setsize);
    /* call generic forwarding function for passing data */
    process_urh (urh);
    /* Finished forwarding? */
    if ( (0 == urh->in_buffer_size) &&
         (0 == urh->out_buffer_size) &&
         (0 == urh->in_buffer_used) &&
         (0 == urh->out_buffer_used) )
    {
      MHD_connection_finish_forward_ (urh->connection);
      urh->clean_ready = true;
      /* Resuming will move connection to cleanup list. */
      MHD_resume_connection (urh->connection);
    }
  }
#endif /* HTTPS_SUPPORT && UPGRADE_SUPPORT */
  MHD_cleanup_connections (daemon);

  /* Final log entry indicating exit */
  logf = fopen("/tmp/logs/1133.txt", "a");
  if (logf)
  {
    fprintf(logf, "[internal_run_from_select] exit: daemon=%p, fd_setsize=%d\n", (const void *) daemon, fd_setsize);
    fclose(logf);
  }

  return MHD_YES;
}
