#include <stdio.h>
#include <stdarg.h>
#include <time.h>

static void probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/491.txt", "a");
    if (f == NULL)
        return;

    time_t t = time(NULL);
    fprintf(f, "[%ld] ", (long)t);

    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);

    fprintf(f, "\n");
    fclose(f);
}

static enum replyBodyUse
is_reply_body_needed (struct MHD_Connection *connection,
                      unsigned int rcode)
{
  struct MHD_Connection *const c = connection; /**< a short alias */

  /* Log entry and basic state so we can trace control flow. */
  probe_log("enter is_reply_body_needed: connection=%p, rcode=%u",
            (void *)connection, rcode);

  mhd_assert (100 <= rcode);
  mhd_assert (999 >= rcode);

  if (199 >= rcode) {
    probe_log("decision: rcode <= 199 -> RP_BODY_NONE (rcode=%u)", rcode);
    return RP_BODY_NONE;
  }

  if (MHD_HTTP_NO_CONTENT == rcode) {
    probe_log("decision: MHD_HTTP_NO_CONTENT -> RP_BODY_NONE (rcode=%u)", rcode);
    return RP_BODY_NONE;
  }

#if 0
  /* This check is not needed as upgrade handler is used only with code 101 */
#ifdef UPGRADE_SUPPORT
  if (NULL != rp.response->upgrade_handler)
    return RP_BODY_NONE;
#endif /* UPGRADE_SUPPORT */
#endif

  /* Probe: document that the upgrade handler check is compiled out */
  probe_log("note: upgrade-handler check block was wrapped in #if 0 and is not compiled");

#if 0
  /* CONNECT is not supported by MHD */
  /* Successful responses for connect requests are filtered by
   * MHD_queue_response() */
  if ( (MHD_HTTP_MTHD_CONNECT == c->rq.http_mthd) &&
       (2 == rcode / 100) )
    return false; /* Actually pass-through CONNECT is not supported by MHD */
#endif

  /* Probe: document that the CONNECT check block is compiled out */
  probe_log("note: CONNECT handling check block was wrapped in #if 0 and is not compiled");

  /* Reply body headers could be used.
   * Check whether reply body itself must be used. */

  if (MHD_HTTP_MTHD_HEAD == c->rq.http_mthd) {
    probe_log("decision: HTTP HEAD method -> RP_BODY_HEADERS_ONLY (method=%d)", c->rq.http_mthd);
    return RP_BODY_HEADERS_ONLY;
  }

  if (MHD_HTTP_NOT_MODIFIED == rcode) {
    probe_log("decision: MHD_HTTP_NOT_MODIFIED -> RP_BODY_HEADERS_ONLY (rcode=%u)", rcode);
    return RP_BODY_HEADERS_ONLY;
  }

  /* Reply body must be sent. The body may have zero length, but body size
   * must be indicated by headers ('Content-Length:' or
   * 'Transfer-Encoding: chunked'). */
  probe_log("decision: default -> RP_BODY_SEND (rcode=%u, method=%d)", rcode, c->rq.http_mthd);
  return RP_BODY_SEND;
}
