#include <stdio.h>
#include <stdarg.h>

/* Probe logging helper: append human-readable logs to /tmp/logs/491.txt */
static void probe_log(const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/491.txt", "a");
  if (NULL == f)
    return;

  va_list ap;
  va_start(ap, fmt);
  vfprintf(f, fmt, ap);
  va_end(ap);
  fputc('\n', f);
  fclose(f);
}

static enum replyBodyUse
is_reply_body_needed (struct MHD_Connection *connection,
                      unsigned int rcode)
{
  struct MHD_Connection *const c = connection; /**< a short alias */

  mhd_assert (100 <= rcode);
  mhd_assert (999 >= rcode);

  /* Log entry state */
  if (NULL != c)
    probe_log("is_reply_body_needed: entry rcode=%u, http_mthd=%d", rcode, (int)c->rq.http_mthd);
  else
    probe_log("is_reply_body_needed: entry rcode=%u, connection=NULL", rcode);

  if (199 >= rcode)
  {
    probe_log("Decision: rcode <= 199 -> RP_BODY_NONE");
    return RP_BODY_NONE;
  }

  if (MHD_HTTP_NO_CONTENT == rcode)
  {
    probe_log("Decision: rcode == MHD_HTTP_NO_CONTENT -> RP_BODY_NONE");
    return RP_BODY_NONE;
  }

  /* The following block used to contain code disabled by #if 0 in the
   * original source. That code is never compiled. To help reproduce the
   * violation scenario at runtime we log when we observe the situation
   * that the disabled code would have handled: a CONNECT method with a
   * successful (2xx) response. We do NOT change control flow here. */
  if (NULL != c)
  {
    if ( (MHD_HTTP_MTHD_CONNECT == c->rq.http_mthd) && (2 == rcode / 100) )
    {
      probe_log("Observed: CONNECT method with 2xx response. Note: path inside '#if 0' (disabled) is not executed in this build.");
      /* Do not alter behavior; original code was disabled and would have
       * returned false. We intentionally only log this fact so other
       * agents can gather evidence that the code path is unreachable. */
    }
  }

  /* Reply body headers could be used.
   * Check whether reply body itself must be used. */

  if (NULL != c && MHD_HTTP_MTHD_HEAD == c->rq.http_mthd)
  {
    probe_log("Decision: HEAD method -> RP_BODY_HEADERS_ONLY");
    return RP_BODY_HEADERS_ONLY;
  }

  if (MHD_HTTP_NOT_MODIFIED == rcode)
  {
    probe_log("Decision: rcode == MHD_HTTP_NOT_MODIFIED -> RP_BODY_HEADERS_ONLY");
    return RP_BODY_HEADERS_ONLY;
  }

  /* Reply body must be sent. The body may have zero length, but body size
   * must be indicated by headers ('Content-Length:' or
   * 'Transfer-Encoding: chunked'). */
  probe_log("Decision: default -> RP_BODY_SEND");
  return RP_BODY_SEND;
}
