#include <stdio.h>

/* Probe/logging helper: writes connection and response state to /tmp/logs/503.txt.
   Uses const-qualified parameters to avoid modifying inspected objects. */
static void mhd_probe_log_state(const struct MHD_Connection *c,
                                const struct MHD_Response *r,
                                const char *label)
{
  FILE *f = fopen("/tmp/logs/503.txt", "a");
  if (NULL == f)
    return;

  /* Print the most relevant fields used by keepalive_possible. Casts are
     used so printing is robust even if underlying types are enums/bitfields. */
  fprintf(f,
          "[%s] conn=%p rp.response=%p keepalive=%u read_closed=%u discard_request=%u stop_with_error=%u rq.http_ver=%u r->flags=0x%08x r->flags_auto=0x%08x r->upgrade_handler=%p\n",
          label,
          (void *)c,
          (void *)(r),
          (unsigned)c->keepalive,
          (unsigned)c->read_closed,
          (unsigned)c->discard_request,
          (unsigned)c->stop_with_error,
          (unsigned)c->rq.http_ver,
          (unsigned)r->flags,
          (unsigned)r->flags_auto,
          (void *)r->upgrade_handler);

  fclose(f);
}

static enum MHD_ConnKeepAlive
keepalive_possible (struct MHD_Connection *connection)
{
  struct MHD_Connection *const c = connection; /**< a short alias */
  struct MHD_Response *const r = c->rp.response;  /**< a short alias */

  /* Log entry state */
  mhd_probe_log_state((const struct MHD_Connection *)c,
                      (const struct MHD_Response *)r,
                      "entry");

  mhd_assert (NULL != r);
  if (MHD_CONN_MUST_CLOSE == c->keepalive)
  {
    mhd_probe_log_state((const struct MHD_Connection *)c,
                        (const struct MHD_Response *)r,
                        "return_MUST_CLOSE_keepalive_forced");
    return MHD_CONN_MUST_CLOSE;
  }

#ifdef UPGRADE_SUPPORT
  /* TODO: Move below the next check when MHD stops closing connections
   * when response is queued in first callback */
  if (NULL != r->upgrade_handler)
  {
    /* No "close" token is enforced by 'add_response_header_connection()' */
    mhd_assert (0 == (r->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE));
    /* Valid HTTP version is enforced by 'MHD_queue_response()' */
    mhd_assert (MHD_IS_HTTP_VER_SUPPORTED (c->rq.http_ver));
    mhd_assert (! c->stop_with_error);

    mhd_probe_log_state((const struct MHD_Connection *)c,
                        (const struct MHD_Response *)r,
                        "return_MUST_UPGRADE_upgrade_handler_present");
    return MHD_CONN_MUST_UPGRADE;
  }
#endif /* UPGRADE_SUPPORT */

  mhd_assert ( (! c->stop_with_error) || (c->discard_request));
  if ((c->read_closed) || (c->discard_request))
  {
    mhd_probe_log_state((const struct MHD_Connection *)c,
                        (const struct MHD_Response *)r,
                        "return_MUST_CLOSE_read_closed_or_discard");
    return MHD_CONN_MUST_CLOSE;
  }

  if (0 != (r->flags & MHD_RF_HTTP_1_0_COMPATIBLE_STRICT))
  {
    mhd_probe_log_state((const struct MHD_Connection *)c,
                        (const struct MHD_Response *)r,
                        "return_MUST_CLOSE_response_http_1_0_strict");
    return MHD_CONN_MUST_CLOSE;
  }
  if (0 != (r->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE))
  {
    mhd_probe_log_state((const struct MHD_Connection *)c,
                        (const struct MHD_Response *)r,
                        "return_MUST_CLOSE_response_has_connection_close");
    return MHD_CONN_MUST_CLOSE;
  }

  if (! MHD_IS_HTTP_VER_SUPPORTED (c->rq.http_ver))
  {
    mhd_probe_log_state((const struct MHD_Connection *)c,
                        (const struct MHD_Response *)r,
                        "return_MUST_CLOSE_http_version_not_supported");
    return MHD_CONN_MUST_CLOSE;
  }

  {
    int has_close = MHD_lookup_header_s_token_ci (c,
                                                  MHD_HTTP_HEADER_CONNECTION,
                                                  "close");
    mhd_probe_log_state((const struct MHD_Connection *)c,
                        (const struct MHD_Response *)r,
                        has_close ? "header_connection_close_found" : "header_connection_close_not_found");
    if (has_close)
    {
      mhd_probe_log_state((const struct MHD_Connection *)c,
                          (const struct MHD_Response *)r,
                          "return_MUST_CLOSE_header_connection_close");
      return MHD_CONN_MUST_CLOSE;
    }
  }

  if ((MHD_HTTP_VER_1_0 == connection->rq.http_ver) ||
      (0 != (connection->rp.response->flags & MHD_RF_HTTP_1_0_SERVER)))
  {
    int has_keepalive = MHD_lookup_header_s_token_ci (connection,
                                                      MHD_HTTP_HEADER_CONNECTION,
                                                      "Keep-Alive");
    mhd_probe_log_state((const struct MHD_Connection *)c,
                        (const struct MHD_Response *)r,
                        has_keepalive ? "header_keepalive_found" : "header_keepalive_not_found");
    if (has_keepalive)
    {
      mhd_probe_log_state((const struct MHD_Connection *)c,
                          (const struct MHD_Response *)r,
                          "return_USE_KEEPALIVE_http_1_0_with_KeepAlive");
      return MHD_CONN_USE_KEEPALIVE;
    }

    mhd_probe_log_state((const struct MHD_Connection *)c,
                        (const struct MHD_Response *)r,
                        "return_MUST_CLOSE_http_1_0_no_KeepAlive");
    return MHD_CONN_MUST_CLOSE;
  }

  if (MHD_IS_HTTP_VER_1_1_COMPAT (c->rq.http_ver))
  {
    mhd_probe_log_state((const struct MHD_Connection *)c,
                        (const struct MHD_Response *)r,
                        "return_USE_KEEPALIVE_http_1_1_compat");
    return MHD_CONN_USE_KEEPALIVE;
  }

  mhd_probe_log_state((const struct MHD_Connection *)c,
                      (const struct MHD_Response *)r,
                      "return_MUST_CLOSE_final");
  return MHD_CONN_MUST_CLOSE;
}
