#ifndef HAVE_MESSAGES
#include <stdio.h>
#endif

static enum _MHD_ParseCookie
parse_cookie_header (struct MHD_Connection *connection)
{
  const char *hdr;
  size_t hdr_len;
  char *cpy;
  size_t i;
  enum _MHD_ParseCookie parse_res;
  struct MHD_HTTP_Req_Header *const saved_tail =
    connection->rq.headers_received_tail;
  const bool allow_partially_correct_cookie =
    (1 >= connection->daemon->client_discipline);

#ifndef HAVE_MESSAGES
  /* Probe function: log control path and state to /tmp/logs/532.txt (or
     fallback to /tmp/532.txt if the directory does not exist). This helper
     is placed under the same conditional as the probe call so it is only
     defined when used (avoids creating an unused-function situation when
     HAVE_MESSAGES is defined). */
  static void mhd_probe_log_saved_tail(const void *saved_tail_ptr,
                                      const void *current_tail_ptr,
                                      int parse_res_val)
  {
    FILE *f = fopen("/tmp/logs/532.txt", "a");
    if (NULL == f)
      f = fopen("/tmp/532.txt", "a");
    if (NULL == f)
      return; /* Best-effort logging; do not alter behavior on failure. */

    /* Log the fact that we reached the suppression site and record
       relevant pointers and parse result so other agents can reproduce
       the scenario. */
    fprintf(f, "[probe] reached saved_tail suppression: saved_tail=%p, current_tail=%p, parse_res=%d\n",
            saved_tail_ptr, current_tail_ptr, parse_res_val);
    fclose(f);
  }
#endif /* ! HAVE_MESSAGES */

  if (MHD_NO ==
      MHD_lookup_connection_value_n (connection,
                                     MHD_HEADER_KIND,
                                     MHD_HTTP_HEADER_COOKIE,
                                     MHD_STATICSTR_LEN_ (
                                       MHD_HTTP_HEADER_COOKIE),
                                     &hdr,
                                     &hdr_len))
    return MHD_PARSE_COOKIE_OK;
  if (0 == hdr_len)
    return MHD_PARSE_COOKIE_OK;

  cpy = MHD_connection_alloc_memory_ (connection,
                                      hdr_len + 1);
  if (NULL == cpy)
    parse_res = MHD_PARSE_COOKIE_NO_MEMORY;
  else
  {
    memcpy (cpy,
            hdr,
            hdr_len);
    cpy[hdr_len] = '\0';

    i = 0;
    /* Skip all initial whitespaces */
    while (i < hdr_len && (' ' == cpy[i] || '\t' == cpy[i]))
      i++;

    parse_res = parse_cookies_string (cpy + i, hdr_len - i, connection);
  }

  switch (parse_res)
  {
  case MHD_PARSE_COOKIE_OK:
    break;
  case MHD_PARSE_COOKIE_OK_LAX:
#ifdef HAVE_MESSAGES
    if (saved_tail != connection->rq.headers_received_tail)
      MHD_DLOG (connection->daemon,
                _ ("The Cookie header has been parsed, but it is not fully "
                   "compliant with the standard.\n"));
#endif /* HAVE_MESSAGES */
    break;
  case MHD_PARSE_COOKIE_MALFORMED:
    if (saved_tail != connection->rq.headers_received_tail)
    {
      if (! allow_partially_correct_cookie)
      {
        /* Remove extracted values from partially broken cookie */
        /* Memory remains allocated until the end of the request processing */
        connection->rq.headers_received_tail = saved_tail;
        saved_tail->next = NULL;
#ifdef HAVE_MESSAGES
        MHD_DLOG (connection->daemon,
                  _ ("The Cookie header has been ignored as it contains "
                     "malformed data.\n"));
#endif /* HAVE_MESSAGES */
      }
#ifdef HAVE_MESSAGES
      else
        MHD_DLOG (connection->daemon,
                  _ ("The Cookie header has been only partially parsed as it "
                     "contains malformed data.\n"));
#endif /* HAVE_MESSAGES */
    }
#ifdef HAVE_MESSAGES
    else
      MHD_DLOG (connection->daemon,
                _ ("The Cookie header has malformed data.\n"));
#endif /* HAVE_MESSAGES */
    break;
  case MHD_PARSE_COOKIE_NO_MEMORY:
#ifdef HAVE_MESSAGES
    MHD_DLOG (connection->daemon,
              _ ("Not enough memory in the connection pool to "
                 "parse client cookies!\n"));
#endif /* HAVE_MESSAGES */
    break;
  default:
    mhd_assert (0);
    break;
  }
#ifndef HAVE_MESSAGES
  /* Log that we are about to execute the suppression cast. This documents
     that the following (void) saved_tail; is reached during execution when
     HAVE_MESSAGES is not defined. */
  mhd_probe_log_saved_tail((const void *) saved_tail,
                           (const void *) connection->rq.headers_received_tail,
                           (int) parse_res);
  (void) saved_tail; /* Mute compiler warning */
#endif /* ! HAVE_MESSAGES */

  return parse_res;
}
