#include <stdio.h>
#include <stdarg.h>
#include <string.h>
#include <stddef.h>
#include <stdint.h>

/* Probe helpers to log control flow and state to /tmp/logs/2323.txt */
static void probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/2323.txt", "a");
    if (NULL == f)
        return;
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fprintf(f, "\n");
    fclose(f);
}

static void probe_pp_state(struct MHD_PostProcessor *pp, const char *tag)
{
    char boundary_snip[65];
    size_t n;

    if ((pp != NULL) && (pp->boundary != NULL) && (pp->blen > 0))
    {
        n = pp->blen < 64 ? pp->blen : 64;
        /* boundary may not be NUL-terminated; copy and NUL-terminate for logging */
        memcpy(boundary_snip, pp->boundary, n);
        boundary_snip[n] = '\0';
    }
    else
    {
        boundary_snip[0] = '\0';
        n = 0;
    }

    probe_log("[%s] pp=%p state=%d buffer_pos=%zu blen=%zu nlen=%zu boundary=%p blen_snip_len=%zu boundary_snip=\"%s\"",
              tag, (void *)pp, (int)pp->state, pp->buffer_pos, pp->blen, pp->nlen,
              (void *)pp->boundary, n, boundary_snip);
}

static void probe_buf_snip(const char *buf, size_t buflen, const char *tag)
{
    char snip[65];
    size_t n = buflen < 64 ? buflen : 64;
    if (buf != NULL && n > 0)
    {
        memcpy(snip, buf, n);
        snip[n] = '\0';
    }
    else
    {
        snip[0] = '\0';
    }
    probe_log("[%s] buf=%p buflen=%zu snip=\"%s\"", tag, (const void *)buf, buflen, snip);
}

static enum MHD_Result
post_process_multipart (struct MHD_PostProcessor *pp,
                        const char *post_data,
                        size_t post_data_len)
{
  char *buf;
  size_t max;
  size_t ioff;
  size_t poff;
  int state_changed;

  /* Log entry state */
  probe_log("enter post_process_multipart: post_data_len=%zu", post_data_len);
  if (pp == NULL)
  {
      probe_log("pp is NULL - returning MHD_NO");
      return MHD_NO;
  }
  probe_pp_state(pp, "ENTRY");

  buf = (char *) &pp[1];
  ioff = 0;
  poff = 0;
  state_changed = 1;
  while ( (poff < post_data_len) ||
          ( (pp->buffer_pos > 0) &&
            (0 != state_changed) ) )
  {
    /* first, move as much input data
       as possible to our internal buffer */
    max = pp->buffer_size - pp->buffer_pos;
    if (max > post_data_len - poff)
      max = post_data_len - poff;
    memcpy (&buf[pp->buffer_pos],
            &post_data[poff],
            max);
    poff += max;
    pp->buffer_pos += max;

    /* Log buffer copy details */
    probe_log("buffer copy: max=%zu poff=%zu pp->buffer_pos=%zu", max, poff, pp->buffer_pos);
    probe_buf_snip(buf, pp->buffer_pos, "AFTER_COPY");

    if ( (0 == max) &&
         (0 == state_changed) &&
         (poff < post_data_len) )
    {
      pp->state = PP_Error;
      probe_pp_state(pp, "OUT_OF_MEMORY_SET_ERROR");
      return MHD_NO;            /* out of memory */
    }
    state_changed = 0;

    /* first state machine for '\r'-'\n' and '--' handling */
    switch (pp->skip_rn)
    {
    case RN_Inactive:
      break;
    case RN_OptN:
      if (buf[0] == '\n')
      {
        ioff++;
        pp->skip_rn = RN_Inactive;
        goto AGAIN;
      }
    /* fall-through! */
    case RN_Dash:
      if (buf[0] == '-')
      {
        ioff++;
        pp->skip_rn = RN_Dash2;
        goto AGAIN;
      }
      pp->skip_rn = RN_Full;
    /* fall-through! */
    case RN_Full:
      if (buf[0] == '\r')
      {
        if ( (pp->buffer_pos > 1) &&
             ('\n' == buf[1]) )
        {
          pp->skip_rn = RN_Inactive;
          ioff += 2;
        }
        else
        {
          pp->skip_rn = RN_OptN;
          ioff++;
        }
        goto AGAIN;
      }
      if (buf[0] == '\n')
      {
        ioff++;
        pp->skip_rn = RN_Inactive;
        goto AGAIN;
      }
      pp->skip_rn = RN_Inactive;
      pp->state = PP_Error;
      probe_pp_state(pp, "NO_CRLF_SET_ERROR");
      return MHD_NO;            /* no '\r\n' */
    case RN_Dash2:
      if (buf[0] == '-')
      {
        ioff++;
        pp->skip_rn = RN_Full;
        pp->state = pp->dash_state;
        goto AGAIN;
      }
      pp->state = PP_Error;
      break;
    }

    /* main state engine */
    switch (pp->state)
    {
    case PP_Error:
      probe_pp_state(pp, "STATE_PP_ERROR_RETURN");
      return MHD_NO;
    case PP_Done:
      /* did not expect to receive more data */
      pp->state = PP_Error;
      probe_pp_state(pp, "STATE_PP_DONE_SET_ERROR");
      return MHD_NO;
    case PP_Init:
      /**
       * Per RFC2046 5.1.1 NOTE TO IMPLEMENTORS, consume anything
       * prior to the first multipart boundary:
       *
       * > There appears to be room for additional information prior
       * > to the first boundary delimiter line and following the
       * > final boundary delimiter line.  These areas should
       * > generally be left blank, and implementations must ignore
       * > anything that appears before the first boundary delimiter
       * > line or after the last one.
       */
      /* Log before calling find_boundary with pp->boundary */
      probe_pp_state(pp, "BEFORE_find_boundary_PP_Init");
      probe_buf_snip(pp->boundary, pp->blen, "BEFORE_find_boundary_boundary_snip");
      (void) find_boundary (pp,
                            pp->boundary,
                            pp->blen,
                            &ioff,
                            PP_ProcessEntryHeaders,
                            PP_Done);
      /* Log after calling find_boundary */
      probe_pp_state(pp, "AFTER_find_boundary_PP_Init");
      probe_buf_snip(pp->boundary, pp->blen, "AFTER_find_boundary_boundary_snip");
      break;
    case PP_NextBoundary:
      probe_pp_state(pp, "BEFORE_find_boundary_PP_NextBoundary");
      probe_buf_snip(pp->boundary, pp->blen, "BEFORE_find_boundary_boundary_snip_NEXT");
      if (MHD_NO == find_boundary (pp,
                                   pp->boundary,
                                   pp->blen,
                                   &ioff,
                                   PP_ProcessEntryHeaders,
                                   PP_Done))
      {
        probe_pp_state(pp, "AFTER_find_boundary_PP_NextBoundary_FAILED");
        if (pp->state == PP_Error)
          return MHD_NO;
        goto END;
      }
      probe_pp_state(pp, "AFTER_find_boundary_PP_NextBoundary_OK");
      break;
    case PP_ProcessEntryHeaders:
      pp->must_ikvi = true;
      if (MHD_NO ==
          process_multipart_headers (pp,
                                     &ioff,
                                     PP_PerformCheckMultipart))
      {
        probe_pp_state(pp, "PROCESS_HEADERS_FAILED");
        if (pp->state == PP_Error)
          return MHD_NO;
        else
          goto END;
      }
      state_changed = 1;
      break;
    case PP_PerformCheckMultipart:
      if ( (NULL != pp->content_type) &&
           (MHD_str_equal_caseless_n_ (pp->content_type,
                                       "multipart/mixed",
                                       MHD_STATICSTR_LEN_ ("multipart/mixed"))))
      {
        pp->nested_boundary = strstr (pp->content_type,
                                      "boundary=");
        if (NULL == pp->nested_boundary)
        {
          pp->state = PP_Error;
          probe_pp_state(pp, "MISSING_NESTED_BOUNDARY_SET_ERROR");
          return MHD_NO;
        }
        pp->nested_boundary =
          strdup (&pp->nested_boundary[MHD_STATICSTR_LEN_ ("boundary=")]);
        if (NULL == pp->nested_boundary)
        {
          /* out of memory */
          pp->state = PP_Error;
          probe_pp_state(pp, "STRDUP_NESTED_BOUNDARY_FAILED_SET_ERROR");
          return MHD_NO;
        }
        /* free old content type, we will need that field
           for the content type of the nested elements */
        free (pp->content_type);
        pp->content_type = NULL;
        pp->nlen = strlen (pp->nested_boundary);
        pp->state = PP_Nested_Init;
        state_changed = 1;
        probe_pp_state(pp, "PERFORM_CHECK_MULTIPART_CREATED_NESTED_BOUNDARY");
        break;
      }
      pp->state = PP_ProcessValueToBoundary;
      pp->value_offset = 0;
      state_changed = 1;
      probe_pp_state(pp, "PERFORM_CHECK_MULTIPART_NO_NESTED_SET_PROCESS_VALUE");
      break;
    case PP_ProcessValueToBoundary:
      probe_pp_state(pp, "BEFORE_process_value_to_boundary_MAIN");
      probe_buf_snip(pp->boundary, pp->blen, "BEFORE_process_value_boundary_snip_MAIN");
      if (MHD_NO == process_value_to_boundary (pp,
                                               &ioff,
                                               pp->boundary,
                                               pp->blen,
                                               PP_PerformCleanup,
                                               PP_Done))
      {
        probe_pp_state(pp, "AFTER_process_value_to_boundary_MAIN_FAILED");
        if (pp->state == PP_Error)
          return MHD_NO;
        break;
      }
      probe_pp_state(pp, "AFTER_process_value_to_boundary_MAIN_OK");
      break;
    case PP_PerformCleanup:
      /* clean up state of one multipart form-data element! */
      pp->have = NE_none;
      free_unmarked (pp);
      if (NULL != pp->nested_boundary)
      {
        free (pp->nested_boundary);
        pp->nested_boundary = NULL;
      }
      pp->state = PP_ProcessEntryHeaders;
      state_changed = 1;
      probe_pp_state(pp, "PERFORM_CLEANUP_DONE");
      break;
    case PP_Nested_Init:
      if (NULL == pp->nested_boundary)
      {
        pp->state = PP_Error;
        probe_pp_state(pp, "NESTED_INIT_MISSING_NESTED_SET_ERROR");
        return MHD_NO;
      }
      probe_pp_state(pp, "BEFORE_find_boundary_PP_Nested_Init");
      probe_buf_snip(pp->nested_boundary, pp->nlen, "BEFORE_find_boundary_nested_snip");
      if (MHD_NO == find_boundary (pp,
                                   pp->nested_boundary,
                                   pp->nlen,
                                   &ioff,
                                   PP_Nested_PerformMarking,
                                   PP_NextBoundary /* or PP_Error? */))
      {
        probe_pp_state(pp, "AFTER_find_boundary_PP_Nested_Init_FAILED");
        if (pp->state == PP_Error)
          return MHD_NO;
        goto END;
      }
      probe_pp_state(pp, "AFTER_find_boundary_PP_Nested_Init_OK");
      break;
    case PP_Nested_PerformMarking:
      /* remember what headers were given
         globally */
      pp->have = NE_none;
      if (NULL != pp->content_name)
        pp->have |= NE_content_name;
      if (NULL != pp->content_type)
        pp->have |= NE_content_type;
      if (NULL != pp->content_filename)
        pp->have |= NE_content_filename;
      if (NULL != pp->content_transfer_encoding)
        pp->have |= NE_content_transfer_encoding;
      pp->state = PP_Nested_ProcessEntryHeaders;
      state_changed = 1;
      probe_pp_state(pp, "NESTED_PERFORM_MARKING_DONE");
      break;
    case PP_Nested_ProcessEntryHeaders:
      pp->value_offset = 0;
      if (MHD_NO ==
          process_multipart_headers (pp,
                                     &ioff,
                                     PP_Nested_ProcessValueToBoundary))
      {
        probe_pp_state(pp, "NESTED_PROCESS_HEADERS_FAILED");
        if (pp->state == PP_Error)
          return MHD_NO;
        else
          goto END;
      }
      state_changed = 1;
      probe_pp_state(pp, "NESTED_PROCESS_HEADERS_OK");
      break;
    case PP_Nested_ProcessValueToBoundary:
      probe_pp_state(pp, "BEFORE_process_value_to_boundary_NESTED");
      probe_buf_snip(pp->nested_boundary, pp->nlen, "BEFORE_process_value_nested_boundary_snip");
      if (MHD_NO == process_value_to_boundary (pp,
                                               &ioff,
                                               pp->nested_boundary,
                                               pp->nlen,
                                               PP_Nested_PerformCleanup,
                                               PP_NextBoundary))
      {
        probe_pp_state(pp, "AFTER_process_value_to_boundary_NESTED_FAILED");
        if (pp->state == PP_Error)
          return MHD_NO;
        break;
      }
      probe_pp_state(pp, "AFTER_process_value_to_boundary_NESTED_OK");
      break;
    case PP_Nested_PerformCleanup:
      free_unmarked (pp);
      pp->state = PP_Nested_ProcessEntryHeaders;
      state_changed = 1;
      probe_pp_state(pp, "NESTED_PERFORM_CLEANUP_DONE");
      break;
    case PP_ProcessKey:
    case PP_ProcessValue:
    case PP_Callback:
    default:
      MHD_PANIC (_ ("internal error.\n")); /* should never happen! */
    }
  AGAIN:
    if (ioff > 0)
    {
      memmove (buf,
               &buf[ioff],
               pp->buffer_pos - ioff);
      pp->buffer_pos -= ioff;
      ioff = 0;
      state_changed = 1;
      probe_log("AGAIN: performed memmove, new buffer_pos=%zu", pp->buffer_pos);
      probe_buf_snip(buf, pp->buffer_pos, "AFTER_MEMMOVE");
    }
  }
END:
  if (0 != ioff)
  {
    memmove (buf,
             &buf[ioff],
             pp->buffer_pos - ioff);
    pp->buffer_pos -= ioff;
    probe_log("END: final memmove, buffer_pos=%zu", pp->buffer_pos);
  }
  if (poff < post_data_len)
  {
    pp->state = PP_Error;
    probe_pp_state(pp, "END_POFF_LT_POST_DATA_LEN_SET_ERROR");
    return MHD_NO;              /* serious error */
  }
  probe_pp_state(pp, "EXIT_SUCCESS");
  return MHD_YES;
}
