static void postproc_probe_log(const char *msg,
                                   struct MHD_PostProcessor *pp,
                                   size_t poff,
                                   const char *start_key,
                                   const char *end_key,
                                   const char *start_value,
                                   const char *end_value,
                                   const char *last_escape)
{
  FILE *f = fopen("/tmp/logs/2335.txt", "a");
  if (f)
  {
    fprintf(f,
            "%s: state=%d poff=%zu buffer_pos=%zu buffer_size=%zu must_unescape_key=%d must_ikvi=%d start_key=%p end_key=%p start_value=%p end_value=%p last_escape=%p\n",
            msg,
            (int) pp->state,
            poff,
            (size_t) pp->buffer_pos,
            (size_t) pp->buffer_size,
            (int) pp->must_unescape_key,
            (int) pp->must_ikvi,
            (const void *) start_key,
            (const void *) end_key,
            (const void *) start_value,
            (const void *) end_value,
            (const void *) last_escape);
    fclose(f);
  }
}

static enum MHD_Result
post_process_urlencoded (struct MHD_PostProcessor *pp,
                         const char *post_data,
                         size_t post_data_len)
{
  char *kbuf = (char *) &pp[1];
  size_t poff;
  const char *start_key = NULL;
  const char *end_key = NULL;
  const char *start_value = NULL;
  const char *end_value = NULL;
  const char *last_escape = NULL;

  mhd_assert (PP_Callback != pp->state);

  poff = 0;
  /* Log entry */
  postproc_probe_log("ENTRY", pp, poff, start_key, end_key, start_value, end_value, last_escape);

  while ( ( (poff < post_data_len) ||
            (pp->state == PP_Callback) ) &&
          (pp->state != PP_Error) )
  {
    postproc_probe_log("LOOP_TOP", pp, poff, start_key, end_key, start_value, end_value, last_escape);

    switch (pp->state)
    {
    case PP_Error:
      /* clearly impossible as per while loop invariant */
      postproc_probe_log("PP_Error aborting", pp, poff, start_key, end_key, start_value, end_value, last_escape);
      abort ();
      break; /* Unreachable */
    case PP_Init:
      /* initial phase */
      mhd_assert (NULL == start_key);
      mhd_assert (NULL == end_key);
      mhd_assert (NULL == start_value);
      mhd_assert (NULL == end_value);
      switch (post_data[poff])
      {
      case '=':
        /* Case: (no key)'=' */
        /* Empty key with value */
        pp->state = PP_Error;
        postproc_probe_log("PP_Init: found '=' -> PP_Error", pp, poff, start_key, end_key, start_value, end_value, last_escape);
        continue;
      case '&':
        /* Case: (no key)'&' */
        /* Empty key without value */
        poff++;
        postproc_probe_log("PP_Init: found '&' -> advance", pp, poff, start_key, end_key, start_value, end_value, last_escape);
        continue;
      case '\n':
      case '\r':
        /* Case: (no key)'\n' or (no key)'\r' */
        pp->state = PP_Done;
        poff++;
        postproc_probe_log("PP_Init: newline -> PP_Done", pp, poff, start_key, end_key, start_value, end_value, last_escape);
        break;
      default:
        /* normal character, key start, advance! */
        pp->state = PP_ProcessKey;
        start_key = &post_data[poff];
        pp->must_ikvi = true;
        poff++;
        postproc_probe_log("PP_Init: default -> PP_ProcessKey", pp, poff, start_key, end_key, start_value, end_value, last_escape);
        continue;
      }
      break; /* end PP_Init */
    case PP_ProcessKey:
      /* key phase */
      mhd_assert (NULL == start_value);
      mhd_assert (NULL == end_value);
      mhd_assert (NULL != start_key || 0 == poff);
      mhd_assert (0 != poff || NULL == start_key);
      mhd_assert (NULL == end_key);
      switch (post_data[poff])
      {
      case '=':
        /* Case: 'key=' */
        if (0 != poff)
          end_key = &post_data[poff];
        poff++;
        pp->state = PP_ProcessValue;
        postproc_probe_log("PP_ProcessKey: found '=' -> PP_ProcessValue", pp, poff, start_key, end_key, start_value, end_value, last_escape);
        break;
      case '&':
        /* Case: 'key&' */
        if (0 != poff)
          end_key = &post_data[poff];
        poff++;
        pp->state = PP_Callback;
        postproc_probe_log("PP_ProcessKey: found '&' -> PP_Callback", pp, poff, start_key, end_key, start_value, end_value, last_escape);
        break;
      case '\n':
      case '\r':
        /* Case: 'key\n' or 'key\r' */
        if (0 != poff)
          end_key = &post_data[poff];
        /* No advance here, 'PP_Done' will be selected by next 'PP_Init' phase */
        pp->state = PP_Callback;
        postproc_probe_log("PP_ProcessKey: newline -> PP_Callback (no advance)", pp, poff, start_key, end_key, start_value, end_value, last_escape);
        break;
      default:
        /* normal character, advance! */
        if (0 == poff)
          start_key = post_data;
        poff++;
        break;
      }
      mhd_assert (NULL == end_key || NULL != start_key);
      break; /* end PP_ProcessKey */
    case PP_ProcessValue:
      if (NULL == start_value)
        start_value = &post_data[poff];
      switch (post_data[poff])
      {
      case '=':
        /* case 'key==' */
        pp->state = PP_Error;
        postproc_probe_log("PP_ProcessValue: found extra '=' -> PP_Error", pp, poff, start_key, end_key, start_value, end_value, last_escape);
        continue;
      case '&':
        /* case 'value&' */
        end_value = &post_data[poff];
        poff++;
        if (pp->must_ikvi ||
            (start_value != end_value) )
        {
          pp->state = PP_Callback;
          postproc_probe_log("PP_ProcessValue: found '&' -> PP_Callback (has value or must_ikvi)", pp, poff, start_key, end_key, start_value, end_value, last_escape);
        }
        else
        {
          pp->buffer_pos = 0;
          pp->value_offset = 0;
          pp->state = PP_Init;
          start_value = NULL;
          end_value = NULL;
          postproc_probe_log("PP_ProcessValue: found '&' -> empty value, reset to PP_Init", pp, poff, start_key, end_key, start_value, end_value, last_escape);
        }
        continue;
      case '\n':
      case '\r':
        /* Case: 'value\n' or 'value\r' */
        end_value = &post_data[poff];
        if (pp->must_ikvi ||
            (start_value != end_value) )
          pp->state = PP_Callback; /* No poff advance here to set PP_Done in the next iteration */
        else
        {
          poff++;
          pp->state = PP_Done;
        }
        postproc_probe_log("PP_ProcessValue: newline handling", pp, poff, start_key, end_key, start_value, end_value, last_escape);
        break;
      case '%':
        last_escape = &post_data[poff];
        poff++;
        break;
      case '0':
      case '1':
      case '2':
      case '3':
      case '4':
      case '5':
      case '6':
      case '7':
      case '8':
      case '9':
        /* character, may be part of escaping */
        poff++;
        continue;
      default:
        /* normal character, no more escaping! */
        last_escape = NULL;
        poff++;
        continue;
      }
      break; /* end PP_ProcessValue */
    case PP_Done:
      switch (post_data[poff])
      {
      case '\n':
      case '\r':
        poff++;
        continue;
      }
      /* unexpected data at the end, fail! */
      pp->state = PP_Error;
      postproc_probe_log("PP_Done: unexpected data -> PP_Error", pp, poff, start_key, end_key, start_value, end_value, last_escape);
      break;
    case PP_Callback:
      mhd_assert ((NULL != end_key) || (NULL == start_key));
      /* Probe: entering PP_Callback */
      postproc_probe_log("PP_Callback: entering", pp, poff, start_key, end_key, start_value, end_value, last_escape);
      if (1)
      {
        const size_t key_len = (size_t) (end_key - start_key);
        mhd_assert (end_key >= start_key);
        postproc_probe_log("PP_Callback: if(1) taken", pp, poff, start_key, end_key, start_value, end_value, last_escape);
        if (0 != key_len)
        {
          if ( (pp->buffer_pos + key_len >= pp->buffer_size) ||
               (pp->buffer_pos + key_len < pp->buffer_pos) )
          {
            /* key too long, cannot parse! */
            pp->state = PP_Error;
            postproc_probe_log("PP_Callback: key too long -> PP_Error", pp, poff, start_key, end_key, start_value, end_value, last_escape);
            continue;
          }
          /* compute key, if we have not already */
          memcpy (&kbuf[pp->buffer_pos],
                  start_key,
                  key_len);
          pp->buffer_pos += key_len;
          start_key = NULL;
          end_key = NULL;
          pp->must_unescape_key = true;

          postproc_probe_log("PP_Callback: copied key into buffer", pp, poff, start_key, end_key, start_value, end_value, last_escape);
        }
      }
#ifdef _DEBUG
      else
        mhd_assert (0 != pp->buffer_pos);
#endif /* _DEBUG */
      if (pp->must_unescape_key)
      {
        kbuf[pp->buffer_pos] = '\0'; /* 0-terminate key */
        MHD_unescape_plus (kbuf);
        MHD_http_unescape (kbuf);
        pp->must_unescape_key = false;
        postproc_probe_log("PP_Callback: unescaped key", pp, poff, start_key, end_key, start_value, end_value, last_escape);
      }
      process_value (pp,
                     start_value,
                     end_value,
                     NULL);
      if (PP_Error == pp->state)
      {
        postproc_probe_log("PP_Callback: process_value set PP_Error", pp, poff, start_key, end_key, start_value, end_value, last_escape);
        continue;
      }
      pp->value_offset = 0;
      start_value = NULL;
      end_value = NULL;
      pp->buffer_pos = 0;
      pp->state = PP_Init;
      postproc_probe_log("PP_Callback: completed callback, reset to PP_Init", pp, poff, start_key, end_key, start_value, end_value, last_escape);
      break;
    case PP_NextBoundary:
    case PP_ProcessEntryHeaders:
    case PP_PerformCheckMultipart:
    case PP_ProcessValueToBoundary:
    case PP_PerformCleanup:
    case PP_Nested_Init:
    case PP_Nested_PerformMarking:
    case PP_Nested_ProcessEntryHeaders:
    case PP_Nested_ProcessValueToBoundary:
    case PP_Nested_PerformCleanup:
    default:
      MHD_PANIC (_ ("internal error.\n")); /* should never happen! */
    }
    mhd_assert ((end_key == NULL) || (start_key != NULL));
    mhd_assert ((end_value == NULL) || (start_value != NULL));
  }

  mhd_assert (PP_Callback != pp->state);

  if (PP_Error == pp->state)
  {
    /* State in error, returning failure */
    postproc_probe_log("EXIT: PP_Error -> MHD_NO", pp, poff, start_key, end_key, start_value, end_value, last_escape);
    return MHD_NO;
  }

  /* save remaining data for next iteration */
  if (NULL != start_key)
  {
    size_t key_len;
    mhd_assert ((PP_ProcessKey == pp->state) || (NULL != end_key));
    if (NULL == end_key)
      end_key = &post_data[poff];
    mhd_assert (end_key >= start_key);
    key_len = (size_t) (end_key - start_key);
    mhd_assert (0 != key_len); /* it must be always non-zero here */
    if (pp->buffer_pos + key_len >= pp->buffer_size)
    {
      pp->state = PP_Error;
      postproc_probe_log("save remaining: buffer overflow -> PP_Error", pp, poff, start_key, end_key, start_value, end_value, last_escape);
      return MHD_NO;
    }
    memcpy (&kbuf[pp->buffer_pos],
            start_key,
            key_len);
    pp->buffer_pos += key_len;
    pp->must_unescape_key = true;
    start_key = NULL;
    end_key = NULL;
    postproc_probe_log("save remaining: saved partial key", pp, poff, start_key, end_key, start_value, end_value, last_escape);
  }
  if ( (NULL != start_value) &&
       (PP_ProcessValue == pp->state) )
  {
    /* compute key, if we have not already */
    if (pp->must_unescape_key)
    {
      kbuf[pp->buffer_pos] = '\0'; /* 0-terminate key */
      MHD_unescape_plus (kbuf);
      MHD_http_unescape (kbuf);
      pp->must_unescape_key = false;
    }
    if (NULL == end_value)
      end_value = &post_data[poff];
    if ( (NULL != last_escape) &&
         (2 < (end_value - last_escape)) )
      last_escape = NULL;
    process_value (pp,
                   start_value,
                   end_value,
                   last_escape);
    pp->must_ikvi = false;
    postproc_probe_log("save remaining: processed remaining value", pp, poff, start_key, end_key, start_value, end_value, last_escape);
  }
  if (PP_Error == pp->state)
  {
    /* State in error, returning failure */
    postproc_probe_log("EXIT: PP_Error after save -> MHD_NO", pp, poff, start_key, end_key, start_value, end_value, last_escape);
    return MHD_NO;
  }
  postproc_probe_log("EXIT: success -> MHD_YES", pp, poff, start_key, end_key, start_value, end_value, last_escape);
  return MHD_YES;
}
