#include <stdio.h>
#include <stdarg.h>
#include <stddef.h>
#include <stdint.h>

/* Probe: append logs to /tmp/logs/2339.txt about internal state and control flow. */
static void log_probe(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/2339.txt", "a");
    if (!f)
        return;
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fprintf(f, "\n");
    fclose(f);
}

static enum MHD_Result
post_process_urlencoded (struct MHD_PostProcessor *pp,
                         const char *post_data,
                         size_t post_data_len)
{
  char *kbuf = (char *) &pp[1];
  size_t poff;
  const char *start_key = NULL;
  const char *end_key = NULL;
  const char *start_value = NULL;
  const char *end_value = NULL;
  const char *last_escape = NULL;

  /* Log entry into function with some key state */
  log_probe("ENTER post_process_urlencoded: pp=%p post_data=%p post_data_len=%zu buffer_pos=%zu buffer_size=%zu state=%d",
            (void*)pp, (const void*)post_data, post_data_len,
            (size_t)pp->buffer_pos, (size_t)pp->buffer_size, (int)pp->state);

  mhd_assert (PP_Callback != pp->state);

  poff = 0;
  while ( ( (poff < post_data_len) ||
            (pp->state == PP_Callback) ) &&
          (pp->state != PP_Error) )
  {
    switch (pp->state)
    {
    case PP_Error:
      /* clearly impossible as per while loop invariant */
      abort ();
      break; /* Unreachable */
    case PP_Init:
      /* initial phase */
      mhd_assert (NULL == start_key);
      mhd_assert (NULL == end_key);
      mhd_assert (NULL == start_value);
      mhd_assert (NULL == end_value);
      switch (post_data[poff])
      {
      case '=':
        /* Case: (no key)'=' */
        /* Empty key with value */
        pp->state = PP_Error;
        continue;
      case '&':
        /* Case: (no key)'&' */
        /* Empty key without value */
        poff++;
        continue;
      case '\n':
      case '\r':
        /* Case: (no key)'\n' or (no key)'\r' */
        pp->state = PP_Done;
        poff++;
        break;
      default:
        /* normal character, key start, advance! */
        pp->state = PP_ProcessKey;
        start_key = &post_data[poff];
        pp->must_ikvi = true;
        poff++;
        continue;
      }
      break; /* end PP_Init */
    case PP_ProcessKey:
      /* key phase */
      mhd_assert (NULL == start_value);
      mhd_assert (NULL == end_value);
      mhd_assert (NULL != start_key || 0 == poff);
      mhd_assert (0 != poff || NULL == start_key);
      mhd_assert (NULL == end_key);
      switch (post_data[poff])
      {
      case '=':
        /* Case: 'key=' */
        if (0 != poff)
          end_key = &post_data[poff];
        poff++;
        pp->state = PP_ProcessValue;
        break;
      case '&':
        /* Case: 'key&' */
        if (0 != poff)
          end_key = &post_data[poff];
        poff++;
        pp->state = PP_Callback;
        break;
      case '\n':
      case '\r':
        /* Case: 'key\n' or 'key\r' */
        if (0 != poff)
          end_key = &post_data[poff];
        /* No advance here, 'PP_Done' will be selected by next 'PP_Init' phase */
        pp->state = PP_Callback;
        break;
      default:
        /* normal character, advance! */
        if (0 == poff)
          start_key = post_data;
        poff++;
        break;
      }
      mhd_assert (NULL == end_key || NULL != start_key);
      break; /* end PP_ProcessKey */
    case PP_ProcessValue:
      if (NULL == start_value)
        start_value = &post_data[poff];
      switch (post_data[poff])
      {
      case '=':
        /* case 'key==' */
        pp->state = PP_Error;
        continue;
      case '&':
        /* case 'value&' */
        end_value = &post_data[poff];
        poff++;
        if (pp->must_ikvi ||
            (start_value != end_value) )
        {
          pp->state = PP_Callback;
        }
        else
        {
          pp->buffer_pos = 0;
          pp->value_offset = 0;
          pp->state = PP_Init;
          start_value = NULL;
          end_value = NULL;
        }
        continue;
      case '\n':
      case '\r':
        /* Case: 'value\n' or 'value\r' */
        end_value = &post_data[poff];
        if (pp->must_ikvi ||
            (start_value != end_value) )
          pp->state = PP_Callback; /* No poff advance here to set PP_Done in the next iteration */
        else
        {
          poff++;
          pp->state = PP_Done;
        }
        break;
      case '%':
        last_escape = &post_data[poff];
        poff++;
        break;
      case '0':
      case '1':
      case '2':
      case '3':
      case '4':
      case '5':
      case '6':
      case '7':
      case '8':
      case '9':
        /* character, may be part of escaping */
        poff++;
        continue;
      default:
        /* normal character, no more escaping! */
        last_escape = NULL;
        poff++;
        continue;
      }
      break; /* end PP_ProcessValue */
    case PP_Done:
      switch (post_data[poff])
      {
      case '\n':
      case '\r':
        poff++;
        continue;
      }
      /* unexpected data at the end, fail! */
      pp->state = PP_Error;
      break;
    case PP_Callback:
      mhd_assert ((NULL != end_key) || (NULL == start_key));
      if (1)
      {
        const size_t key_len = (size_t) (end_key - start_key);
        mhd_assert (end_key >= start_key);
        if (0 != key_len)
        {
          /* Log state before boundary checks and memcpy in PP_Callback */
          {
            size_t bp = pp->buffer_pos;
            size_t bsz = pp->buffer_size;
            size_t sum = bp + key_len;
            int overflow = (sum < bp);
            log_probe("PP_Callback: pp=%p start_key=%p end_key=%p key_len=%zu buffer_pos=%zu buffer_size=%zu sum=%zu overflow=%d poff=%zu",
                      (void*)pp, (const void*)start_key, (const void*)end_key,
                      key_len, bp, bsz, sum, overflow, poff);
          }

          if ( (pp->buffer_pos + key_len >= pp->buffer_size) ||
               (pp->buffer_pos + key_len < pp->buffer_pos) )
          {
            /* key too long, cannot parse! */
            log_probe("PP_Callback: transition to PP_Error due to length/overflow: buffer_pos=%zu key_len=%zu buffer_size=%zu",
                      (size_t)pp->buffer_pos, key_len, (size_t)pp->buffer_size);
            pp->state = PP_Error;
            continue;
          }
          /* compute key, if we have not already */
          log_probe("PP_Callback: about to memcpy dest=%p src=%p len=%zu (buffer_pos=%zu)",
                    (void*)&kbuf[pp->buffer_pos], (const void*)start_key, key_len, (size_t)pp->buffer_pos);
          memcpy (&kbuf[pp->buffer_pos],
                  start_key,
                  key_len);
          pp->buffer_pos += key_len;
          start_key = NULL;
          end_key = NULL;
          pp->must_unescape_key = true;
        }
      }
#ifdef _DEBUG
      else
        mhd_assert (0 != pp->buffer_pos);
#endif /* _DEBUG */
      if (pp->must_unescape_key)
      {
        kbuf[pp->buffer_pos] = '\0'; /* 0-terminate key */
        MHD_unescape_plus (kbuf);
        MHD_http_unescape (kbuf);
        pp->must_unescape_key = false;
      }
      process_value (pp,
                     start_value,
                     end_value,
                     NULL);
      if (PP_Error == pp->state)
        continue;
      pp->value_offset = 0;
      start_value = NULL;
      end_value = NULL;
      pp->buffer_pos = 0;
      pp->state = PP_Init;
      break;
    case PP_NextBoundary:
    case PP_ProcessEntryHeaders:
    case PP_PerformCheckMultipart:
    case PP_ProcessValueToBoundary:
    case PP_PerformCleanup:
    case PP_Nested_Init:
    case PP_Nested_PerformMarking:
    case PP_Nested_ProcessEntryHeaders:
    case PP_Nested_ProcessValueToBoundary:
    case PP_Nested_PerformCleanup:
    default:
      MHD_PANIC (_ ("internal error.\n")); /* should never happen! */
    }
    mhd_assert ((end_key == NULL) || (start_key != NULL));
    mhd_assert ((end_value == NULL) || (start_value != NULL));
  }

  mhd_assert (PP_Callback != pp->state);

  if (PP_Error == pp->state)
  {
    /* State in error, returning failure */
    log_probe("EXIT post_process_urlencoded: PP_Error at loop end: pp=%p buffer_pos=%zu buffer_size=%zu state=%d", (void*)pp, (size_t)pp->buffer_pos, (size_t)pp->buffer_size, (int)pp->state);
    return MHD_NO;
  }

  /* save remaining data for next iteration */
  if (NULL != start_key)
  {
    size_t key_len;
    mhd_assert ((PP_ProcessKey == pp->state) || (NULL != end_key));
    if (NULL == end_key)
      end_key = &post_data[poff];
    mhd_assert (end_key >= start_key);
    key_len = (size_t) (end_key - start_key);
    mhd_assert (0 != key_len); /* it must be always non-zero here */

    /* Log state before save-remaining-data check */
    {
      size_t bp = pp->buffer_pos;
      size_t bsz = pp->buffer_size;
      size_t sum = bp + key_len;
      int overflow = (sum < bp);
      log_probe("SAVE_REMAINING: pp=%p start_key=%p end_key=%p key_len=%zu buffer_pos=%zu buffer_size=%zu sum=%zu overflow=%d poff=%zu",
                (void*)pp, (const void*)start_key, (const void*)end_key,
                key_len, bp, bsz, sum, overflow, poff);
    }

    if (pp->buffer_pos + key_len >= pp->buffer_size)
    {
      pp->state = PP_Error;
      log_probe("SAVE_REMAINING: transition to PP_Error due to buffer overflow check: buffer_pos=%zu key_len=%zu buffer_size=%zu",
                (size_t)pp->buffer_pos, key_len, (size_t)pp->buffer_size);
      return MHD_NO;
    }
    memcpy (&kbuf[pp->buffer_pos],
            start_key,
            key_len);
    log_probe("SAVE_REMAINING: performed memcpy dest=%p src=%p len=%zu (buffer_pos before add=%zu)",
              (void*)&kbuf[pp->buffer_pos], (const void*)start_key, key_len, (size_t)pp->buffer_pos);
    pp->buffer_pos += key_len;
    pp->must_unescape_key = true;
    start_key = NULL;
    end_key = NULL;
  }
  if ( (NULL != start_value) &&
       (PP_ProcessValue == pp->state) )
  {
    /* compute key, if we have not already */
    if (pp->must_unescape_key)
    {
      kbuf[pp->buffer_pos] = '\0'; /* 0-terminate key */
      MHD_unescape_plus (kbuf);
      MHD_http_unescape (kbuf);
      pp->must_unescape_key = false;
    }
    if (NULL == end_value)
      end_value = &post_data[poff];
    if ( (NULL != last_escape) &&
         (2 < (end_value - last_escape)) )
      last_escape = NULL;
    process_value (pp,
                   start_value,
                   end_value,
                   last_escape);
    pp->must_ikvi = false;
  }
  if (PP_Error == pp->state)
  {
    /* State in error, returning failure */
    log_probe("EXIT post_process_urlencoded: PP_Error before final return: pp=%p buffer_pos=%zu buffer_size=%zu state=%d", (void*)pp, (size_t)pp->buffer_pos, (size_t)pp->buffer_size, (int)pp->state);
    return MHD_NO;
  }

  log_probe("EXIT post_process_urlencoded: success: pp=%p buffer_pos=%zu buffer_size=%zu state=%d",
            (void*)pp, (size_t)pp->buffer_pos, (size_t)pp->buffer_size, (int)pp->state);
  return MHD_YES;
}
