#include <stdio.h>
#include <stdarg.h>
#include <time.h>

static void probe_log(const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/1812.txt", "a");
  if (!f)
    return;
  time_t t = time(NULL);
  fprintf(f, "[%ld] ", (long)t);
  va_list ap;
  va_start(ap, fmt);
  vfprintf(f, fmt, ap);
  va_end(ap);
  fprintf(f, "\n");
  fclose(f);
}

static void
post_send_setopt (struct MHD_Connection *connection,
                  bool plain_send_next,
                  bool push_data)
{
  /* Try to buffer data if not sending the final piece.
   * Final piece is indicated by push_data == true. */
  const bool buffer_data = (! push_data);

  probe_log("post_send_setopt: entry connection=%p plain_send_next=%d push_data=%d buffer_data=%d is_nonip=%d sk_corked=%d sk_nodelay=%d",
            (void *)connection, (int)plain_send_next, (int)push_data, (int)buffer_data,
            connection ? (int)connection->is_nonip : -1,
            connection ? (int)connection->sk_corked : -1,
            connection ? (int)connection->sk_nodelay : -1);

  if (_MHD_YES == connection->is_nonip)
  {
    probe_log("post_send_setopt: returning early because connection->is_nonip == _MHD_YES");
    return;
  }
  if (buffer_data)
  {
    probe_log("post_send_setopt: buffer_data true -> Nothing to do after send(), returning");
    return; /* Nothing to do after send(). */
  }

#ifndef MHD_USE_MSG_MORE
  (void) plain_send_next; /* Mute compiler warning */
#endif /* ! MHD_USE_MSG_MORE */

  /* Need to push data. */
#ifdef MHD_TCP_CORK_NOPUSH
#ifdef _MHD_CORK_RESET_PUSH_DATA_ALWAYS
#ifdef _MHD_NODELAY_SET_PUSH_DATA_ALWAYS
#ifdef MHD_USE_MSG_MORE
  if (_MHD_OFF == connection->sk_corked)
  {
    probe_log("branch: _MHD_OFF == connection->sk_corked. sk_nodelay=%d", (int)connection->sk_nodelay);
    if (_MHD_ON == connection->sk_nodelay)
    {
      probe_log("post_send_setopt: Data already pushed by send(), returning");
      return; /* Data was already pushed by send(). */
    }
  }
  /* This is Linux kernel. There are options:
   * * Push the data by setting of TCP_NODELAY (without change
   *   of the cork on the socket),
   * * Push the data by resetting of TCP_CORK.
   * The optimal choice depends on the next final send functions
   * used on the same socket. If TCP_NODELAY wasn't set then push
   * data by setting TCP_NODELAY (TCP_NODELAY will not be removed
   * and is needed to push the data by send() without MSG_MORE).
   * If send()/sendmsg() will be used next than push data by
   * resetting of TCP_CORK so next send without MSG_MORE will push
   * data to the network (without additional sys-call to push data).
   * If next final send function will not support MSG_MORE (like
   * sendfile() or TLS-connection) than push data by setting
   * TCP_NODELAY so socket will remain corked (no additional
   * sys-call before next send()). */
  if ((_MHD_ON != connection->sk_nodelay) ||
      (! plain_send_next))
  {
    probe_log("attempt: set nodelay (preferred). sk_nodelay=%d plain_send_next=%d", (int)connection->sk_nodelay, (int)plain_send_next);
    if (MHD_connection_set_nodelay_state_ (connection, true))
    {
      probe_log("post_send_setopt: MHD_connection_set_nodelay_state_ returned true -> Data pushed by TCP_NODELAY, returning");
      return; /* Data has been pushed by TCP_NODELAY. */
    }
    /* Failed to set TCP_NODELAY for the socket.
     * Really unlikely to happen on TCP connections. */
    probe_log("set nodelay failed, attempting to uncork");
    if (MHD_connection_set_cork_state_ (connection, false))
    {
      probe_log("post_send_setopt: MHD_connection_set_cork_state_ returned true -> Data pushed by uncorking, returning");
      return; /* Data has been pushed by uncorking the socket. */
    }
    /* Failed to uncork the socket.
     * Really unlikely to happen on TCP connections. */

    /* The socket cannot be uncorked, no way to push data */
    probe_log("post_send_setopt: cannot push data -> socket remains corked");
  }
  else
  {
    probe_log("attempt: uncork first path");
    if (MHD_connection_set_cork_state_ (connection, false))
    {
      probe_log("post_send_setopt: MHD_connection_set_cork_state_ returned true -> Data pushed by uncorking, returning");
      return; /* Data has been pushed by uncorking the socket. */
    }
    /* Failed to uncork the socket.
     * Really unlikely to happen on TCP connections. */
    probe_log("uncork failed, attempting to set nodelay");
    if (MHD_connection_set_nodelay_state_ (connection, true))
    {
      probe_log("post_send_setopt: MHD_connection_set_nodelay_state_ returned true -> Data pushed by TCP_NODELAY, returning");
      return; /* Data has been pushed by TCP_NODELAY. */
    }
    /* Failed to set TCP_NODELAY for the socket.
     * Really unlikely to happen on TCP connections. */

    /* The socket cannot be uncorked, no way to push data */
    probe_log("post_send_setopt: cannot push data in else branch -> socket remains corked");
  }
#else  /* ! MHD_USE_MSG_MORE */
  /* Use setting of TCP_NODELAY here to avoid sys-call
   * for corking the socket during sending of the next response. */
  probe_log("attempt: set nodelay fallback (no MSG_MORE)");
  if (MHD_connection_set_nodelay_state_ (connection, true))
  {
    probe_log("post_send_setopt: MHD_connection_set_nodelay_state_ returned true -> Data pushed by TCP_NODELAY, returning");
    return; /* Data was pushed by TCP_NODELAY. */
  }
  /* Failed to set TCP_NODELAY for the socket.
   * Really unlikely to happen on TCP connections. */
  probe_log("set nodelay failed, attempting to uncork (fallback)");
  if (MHD_connection_set_cork_state_ (connection, false))
  {
    probe_log("post_send_setopt: MHD_connection_set_cork_state_ returned true -> Data pushed by uncorking, returning");
    return; /* Data was pushed by uncorking the socket. */
  }
  /* Failed to uncork the socket.
   * Really unlikely to happen on TCP connections. */

  /* The socket remains corked, no way to push data */
  probe_log("post_send_setopt: fallback cannot push data -> socket remains corked");
#endif /* ! MHD_USE_MSG_MORE */
#else  /* ! _MHD_NODELAY_SET_PUSH_DATA_ALWAYS */
  probe_log("attempt: uncork (nodeay not set push always)");
  if (MHD_connection_set_cork_state_ (connection, false))
  {
    probe_log("post_send_setopt: MHD_connection_set_cork_state_ returned true -> Data pushed by uncorking, returning");
    return; /* Data was pushed by uncorking the socket. */
  }
  /* Failed to uncork the socket.
   * Really unlikely to happen on TCP connections. */
  probe_log("post_send_setopt: uncork failed -> returning, socket remains corked");
  return; /* Socket remains corked, no way to push data */
#endif /* ! _MHD_NODELAY_SET_PUSH_DATA_ALWAYS */
#else  /* ! _MHD_CORK_RESET_PUSH_DATA_ALWAYS */
  /* This is a typical *BSD or Darwin kernel. */

  if (_MHD_OFF == connection->sk_corked)
  {
    probe_log("branch: BSD-like and _MHD_OFF == connection->sk_corked, sk_nodelay=%d", (int)connection->sk_nodelay);
    if (_MHD_ON == connection->sk_nodelay)
    {
      probe_log("post_send_setopt: Data already pushed by send(), returning");
      return; /* Data was already pushed by send(). */
    }

    /* Unlikely to reach this code.
     * TCP_NODELAY should be turned on before send(). */
    probe_log("attempt: set nodelay on uncorked socket");
    if (MHD_connection_set_nodelay_state_ (connection, true))
    {
      /* TCP_NODELAY has been set on uncorked socket.
       * Use zero-send to push the data. */
      probe_log("nodelay set, attempting zero_send_");
      if (zero_send_ (connection))
      {
        probe_log("post_send_setopt: zero_send_ returned true -> The data has been pushed by zero-send, returning");
        return; /* The data has been pushed by zero-send. */
      }
    }

    /* Failed to push the data by all means. */
    /* There is nothing left to try. */
    probe_log("post_send_setopt: failed to push data on uncorked BSD-like path");
  }
  else
  {
#ifdef _MHD_CORK_RESET_PUSH_DATA
    enum MHD_tristate old_cork_state = connection->sk_corked;
#endif /* _MHD_CORK_RESET_PUSH_DATA */
    /* The socket is corked or cork state is unknown. */

    probe_log("attempt: uncorking on BSD-like path. current sk_corked=%d", (int)connection->sk_corked);

    if (MHD_connection_set_cork_state_ (connection, false))
    {
#ifdef _MHD_CORK_RESET_PUSH_DATA
      /* FreeBSD kernel */
      if (_MHD_OFF == old_cork_state)
      {
        probe_log("post_send_setopt: uncorked and old_cork_state was OFF -> Data pushed by uncorking, returning");
        return; /* Data has been pushed by uncorking the socket. */
      }
#endif /* _MHD_CORK_RESET_PUSH_DATA */

      /* Unlikely to reach this code.
       * The data should be pushed by uncorking (FreeBSD) or
       * the socket should be uncorked before send(). */
      probe_log("uncork returned true, checking nodelay state to zero-send");
      if ((_MHD_ON == connection->sk_nodelay) ||
          (MHD_connection_set_nodelay_state_ (connection, true)))
      {
        /* TCP_NODELAY is turned ON on uncorked socket.
         * Use zero-send to push the data. */
        probe_log("nodelay on or set -> attempting zero_send_");
        if (zero_send_ (connection))
        {
          probe_log("post_send_setopt: zero_send_ returned true -> The data has been pushed by zero-send, returning");
          return; /* The data has been pushed by zero-send. */
        }
      }
    }
    /* The socket remains corked. Data cannot be pushed. */
    probe_log("post_send_setopt: BSD-like path failed to push data -> socket remains corked");
  }
#endif /* ! _MHD_CORK_RESET_PUSH_DATA_ALWAYS */
#else  /* ! MHD_TCP_CORK_NOPUSH */
  /* Corking is not supported. Buffering is controlled
   * by TCP_NODELAY only. */
  probe_log("branch: corking not supported. Asserting sk_corked != ON. current sk_corked=%d", (int)connection->sk_corked);
  probe_log("post_send_setopt: about to call mhd_assert for sk_corked");
  mhd_assert (_MHD_ON != connection->sk_corked);
  probe_log("post_send_setopt: returned from mhd_assert (note: if mhd_assert is a no-op, this is evidence)");
  if (_MHD_ON == connection->sk_nodelay)
  {
    probe_log("post_send_setopt: sk_nodelay == ON -> Data already pushed by send(), returning");
    return; /* Data was already pushed by send(). */
  }

  /* Unlikely to reach this code.
   * TCP_NODELAY should be turned on before send(). */
  probe_log("post_send_setopt: attempting to set nodelay");
  if (MHD_connection_set_nodelay_state_ (connection, true))
  {
    probe_log("post_send_setopt: MHD_connection_set_nodelay_state_ returned true -> TCP_NODELAY set, attempting zero_send_");
    /* TCP_NODELAY has been set.
     * Use zero-send to push the data. */
    if (zero_send_ (connection))
    {
      probe_log("post_send_setopt: zero_send_ returned true -> The data has been pushed by zero-send, returning");
      return; /* The data has been pushed by zero-send. */
    }
  }

  /* Failed to push the data. */
  probe_log("post_send_setopt: final attempts failed -> cannot push data");
#endif /* ! MHD_TCP_CORK_NOPUSH */
#ifdef HAVE_MESSAGES
  MHD_DLOG (connection->daemon,
            _ ("Failed to push the data from buffers to the network. "
               "Client may experience some delay "
               "(usually in range 200ms - 5 sec).\n"));
  probe_log("post_send_setopt: emitted MHD_DLOG failure message");
#endif /* HAVE_MESSAGES */
  probe_log("post_send_setopt: exit (final return)");
  return;
}
