#include <stdio.h>
#include <stdarg.h>

/* Probe logging helper: append human-readable traces to /tmp/logs/1212.txt */
static void probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/1212.txt", "a");
    if (NULL == f)
        return;
    va_list ap;
    va_start(ap, fmt);
    (void) vfprintf(f, fmt, ap);
    va_end(ap);
    (void) fputc('\n', f);
    (void) fclose(f);
}

static bool
process_interim_params (struct MHD_Daemon *d,
                        const struct sockaddr **ppsockaddr,
                        socklen_t *psockaddr_len,
                        struct MHD_InterimParams_ *params)
{
  /* Log entry and relevant parameter/state snapshot */
  probe_log("ENTER process_interim_params: d=%p, fdset_size_set=%d, fdset_size=%d, listen_fd_set=%d, listen_fd=%ld, server_addr_len_set=%d, pserver_addr_set=%d, server_addr_len=%u",
            (void *) d,
            (int) params->fdset_size_set,
            (int) params->fdset_size,
            (int) params->listen_fd_set,
            (long) params->listen_fd,
            (int) params->server_addr_len_set,
            (int) params->pserver_addr_set,
            (unsigned) params->server_addr_len);

  if (params->fdset_size_set)
  {
    if (0 >= params->fdset_size)
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (d,
                _ ("MHD_OPTION_APP_FD_SETSIZE value (%d) is not positive.\n"),
                params->fdset_size);
#endif /* HAVE_MESSAGES */
      probe_log("Decision: fdset_size invalid -> return false (fdset_size=%d)", params->fdset_size);
      return false;
    }
    if (MHD_D_IS_USING_THREADS_ (d))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (d,
                _ ("MHD_OPTION_APP_FD_SETSIZE is ignored for daemon started " \
                   "with MHD_USE_INTERNAL_POLLING_THREAD.\n"));
#endif /* HAVE_MESSAGES */
      (void) 0;
      /* Probe: record that the threads-using branch (containing the no-op) was taken */
      probe_log("Branch taken: MHD_D_IS_USING_THREADS_ -> executed (void)0 no-op");
    }
    else if (MHD_D_IS_USING_POLL_ (d))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (d,
                _ ("MHD_OPTION_APP_FD_SETSIZE is ignored for daemon started " \
                   "with MHD_USE_POLL.\n"));
#endif /* HAVE_MESSAGES */
      (void) 0;
      /* Probe: record that the poll-using branch (containing the no-op) was taken */
      probe_log("Branch taken: MHD_D_IS_USING_POLL_ -> executed (void)0 no-op");
    }
    else
    { /* The daemon without internal threads, external sockets polling */
#ifndef HAS_FD_SETSIZE_OVERRIDABLE
      if (((int) FD_SETSIZE) != params->fdset_size)
      {
#ifdef HAVE_MESSAGES
        MHD_DLOG (d,
                  _ ("MHD_OPTION_APP_FD_SETSIZE value (%d) does not match " \
                     "the platform FD_SETSIZE value (%d) and this platform " \
                     "does not support overriding of FD_SETSIZE.\n"),
                  params->fdset_size, (int) FD_SETSIZE);
#endif /* HAVE_MESSAGES */
        probe_log("Decision: fdset_size mismatch and not overridable -> return false (app=%d, platform=%d)",
                  params->fdset_size, (int) FD_SETSIZE);
        return false;
      }
#else  /* HAS_FD_SETSIZE_OVERRIDABLE */
      d->fdset_size = params->fdset_size;
      d->fdset_size_set_by_app = true;
      probe_log("Action: fdset_size overridden by app -> d->fdset_size=%d", params->fdset_size);
#endif /* HAS_FD_SETSIZE_OVERRIDABLE */
    }
  }

  if (params->listen_fd_set)
  {
    if (MHD_INVALID_SOCKET == params->listen_fd)
    {
      (void) 0; /* Use MHD-created socket */
      probe_log("Branch: listen_fd == MHD_INVALID_SOCKET -> will use MHD-created socket");
    }
#ifdef HAS_SIGNED_SOCKET
    else if (0 > params->listen_fd)
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (d,
                _ ("The value provided for MHD_OPTION_LISTEN_SOCKET " \
                   "is invalid.\n"));
#endif /* HAVE_MESSAGES */
      probe_log("Decision: listen_fd is negative -> return false (listen_fd=%ld)", (long) params->listen_fd);
      return false;
    }
#endif /* HAS_SIGNED_SOCKET */
    else if (0 != (d->options & MHD_USE_NO_LISTEN_SOCKET))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (d,
                _ ("MHD_OPTION_LISTEN_SOCKET specified for daemon "
                   "with MHD_USE_NO_LISTEN_SOCKET flag set.\n"));
#endif /* HAVE_MESSAGES */
      (void) MHD_socket_close_ (params->listen_fd);
      probe_log("Decision: listen socket specified but MHD_USE_NO_LISTEN_SOCKET set -> closed provided socket and return false (listen_fd=%ld)", (long) params->listen_fd);
      return false;
    }
    else
    {
      d->listen_fd = params->listen_fd;
      d->listen_is_unix = _MHD_UNKNOWN;
#ifdef MHD_USE_GETSOCKNAME
      d->port = 0;  /* Force use of autodetection */
#endif /* MHD_USE_GETSOCKNAME */
      probe_log("Action: adopt provided listen_fd=%ld", (long) params->listen_fd);
    }
  }

  mhd_assert (! params->server_addr_len_set || params->pserver_addr_set);
  if (params->pserver_addr_set)
  {
    if (NULL == params->pserver_addr)
    {
      /* The size must be zero if set */
      if (params->server_addr_len_set && (0 != params->server_addr_len))
      {
        probe_log("Decision: pserver_addr NULL but server_addr_len non-zero -> return false (server_addr_len=%u)", (unsigned) params->server_addr_len);
        return false;
      }
      /* Ignore parameter if it is NULL */
      probe_log("Branch: pserver_addr_set but pserver_addr is NULL -> ignored");
    }
    else if (MHD_INVALID_SOCKET != d->listen_fd)
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (d,
                _ ("MHD_OPTION_LISTEN_SOCKET cannot be used together with " \
                   "MHD_OPTION_SOCK_ADDR_LEN or MHD_OPTION_SOCK_ADDR.\n"));
#endif /* HAVE_MESSAGES */
      probe_log("Decision: pserver_addr provided but listen_fd already set -> return false");
      return false;
    }
    else if (0 != (d->options & MHD_USE_NO_LISTEN_SOCKET))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (d,
                _ ("MHD_OPTION_SOCK_ADDR_LEN or MHD_OPTION_SOCK_ADDR " \
                   "specified for daemon with MHD_USE_NO_LISTEN_SOCKET " \
                   "flag set.\n"));
#endif /* HAVE_MESSAGES */
      if (MHD_INVALID_SOCKET != d->listen_fd)
      {
        (void) MHD_socket_close_ (params->listen_fd);
        params->listen_fd = MHD_INVALID_SOCKET;
      }
      probe_log("Decision: sock addr specified but MHD_USE_NO_LISTEN_SOCKET set -> return false");
      return false;
    }
    else
    {
      *ppsockaddr = params->pserver_addr;
      if (params->server_addr_len_set)
      {
        /* The size must be non-zero if set */
        if (0 == params->server_addr_len)
        {
          probe_log("Decision: server_addr_len set but zero -> return false");
          return false;
        }
        *psockaddr_len = params->server_addr_len;
        probe_log("Action: set ppsockaddr=%p, psockaddr_len=%u", (void *) params->pserver_addr, (unsigned) params->server_addr_len);
      }
      else
      {
        *psockaddr_len = 0;
        probe_log("Action: set ppsockaddr=%p, psockaddr_len=0 (not set by caller)", (void *) params->pserver_addr);
      }
    }
  }
  probe_log("EXIT process_interim_params: returning true");
  return true;
}
