#include <stdio.h>
#include <stdarg.h>
#include <stdint.h>

/* Probe logging helper: append human-readable logs to /tmp/logs/1213.txt */
static void
probe_log (const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/1213.txt", "a");
  if (NULL == f)
    return;

  va_list ap;
  va_start (ap, fmt);
  (void) vfprintf (f, fmt, ap);
  va_end (ap);
  (void) fprintf (f, "\n");
  (void) fclose (f);
}

static bool
process_interim_params (struct MHD_Daemon *d,
                        const struct sockaddr **ppsockaddr,
                        socklen_t *psockaddr_len,
                        struct MHD_InterimParams_ *params)
{
  /* Log entry and some important fields relevant for the violation scenario */
  probe_log (
    "ENTER process_interim_params: pserver_addr_set=%d, pserver_addr=%p, server_addr_len_set=%d, server_addr_len=%lu, listen_fd=%ld, options=0x%lx, fdset_size_set=%d, fdset_size=%d",
    (int) params->pserver_addr_set,
    (void *) params->pserver_addr,
    (int) params->server_addr_len_set,
    (unsigned long) params->server_addr_len,
    (long) d->listen_fd,
    (unsigned long) d->options,
    (int) params->fdset_size_set,
    (int) params->fdset_size);

  if (params->fdset_size_set)
  {
    if (0 >= params->fdset_size)
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (d,
                _ ("MHD_OPTION_APP_FD_SETSIZE value (%d) is not positive.\n"),
                params->fdset_size);
#endif /* HAVE_MESSAGES */
      probe_log ("EXIT process_interim_params: false (fdset_size not positive): fdset_size=%d",
                 (int) params->fdset_size);
      return false;
    }
    if (MHD_D_IS_USING_THREADS_ (d))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (d,
                _ ("MHD_OPTION_APP_FD_SETSIZE is ignored for daemon started " \
                   "with MHD_USE_INTERNAL_POLLING_THREAD.\n"));
#endif /* HAVE_MESSAGES */
      (void) 0;
    }
    else if (MHD_D_IS_USING_POLL_ (d))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (d,
                _ ("MHD_OPTION_APP_FD_SETSIZE is ignored for daemon started " \
                   "with MHD_USE_POLL.\n"));
#endif /* HAVE_MESSAGES */
      (void) 0;
    }
    else
    { /* The daemon without internal threads, external sockets polling */
#ifndef HAS_FD_SETSIZE_OVERRIDABLE
      if (((int) FD_SETSIZE) != params->fdset_size)
      {
#ifdef HAVE_MESSAGES
        MHD_DLOG (d,
                  _ ("MHD_OPTION_APP_FD_SETSIZE value (%d) does not match " \
                     "the platform FD_SETSIZE value (%d) and this platform " \
                     "does not support overriding of FD_SETSIZE.\n"),
                  params->fdset_size, (int) FD_SETSIZE);
#endif /* HAVE_MESSAGES */
        probe_log ("EXIT process_interim_params: false (FD_SETSIZE mismatch): fdset_size=%d, FD_SETSIZE=%d",
                   (int) params->fdset_size, (int) FD_SETSIZE);
        return false;
      }
#else  /* HAS_FD_SETSIZE_OVERRIDABLE */
      d->fdset_size = params->fdset_size;
      d->fdset_size_set_by_app = true;
#endif /* HAS_FD_SETSIZE_OVERRIDABLE */
    }
  }

  if (params->listen_fd_set)
  {
    if (MHD_INVALID_SOCKET == params->listen_fd)
    {
      (void) 0; /* Use MHD-created socket */
    }
#ifdef HAS_SIGNED_SOCKET
    else if (0 > params->listen_fd)
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (d,
                _ ("The value provided for MHD_OPTION_LISTEN_SOCKET " \
                   "is invalid.\n"));
#endif /* HAVE_MESSAGES */
      probe_log ("EXIT process_interim_params: false (invalid listen_fd): listen_fd=%ld",
                 (long) params->listen_fd);
      return false;
    }
#endif /* HAS_SIGNED_SOCKET */
    else if (0 != (d->options & MHD_USE_NO_LISTEN_SOCKET))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (d,
                _ ("MHD_OPTION_LISTEN_SOCKET specified for daemon "
                   "with MHD_USE_NO_LISTEN_SOCKET flag set.\n"));
#endif /* HAVE_MESSAGES */
      (void) MHD_socket_close_ (params->listen_fd);
      probe_log ("EXIT process_interim_params: false (listen_socket with NO_LISTEN flag): listen_fd_closed=%ld",
                 (long) params->listen_fd);
      return false;
    }
    else
    {
      d->listen_fd = params->listen_fd;
      d->listen_is_unix = _MHD_UNKNOWN;
#ifdef MHD_USE_GETSOCKNAME
      d->port = 0;  /* Force use of autodetection */
#endif /* MHD_USE_GETSOCKNAME */
    }
  }

  mhd_assert (! params->server_addr_len_set || params->pserver_addr_set);
  if (params->pserver_addr_set)
  {
    probe_log ("BRANCH pserver_addr_set: pserver_addr=%p, server_addr_len_set=%d, server_addr_len=%lu, listen_fd=%ld, options=0x%lx",
               (void *) params->pserver_addr,
               (int) params->server_addr_len_set,
               (unsigned long) params->server_addr_len,
               (long) d->listen_fd,
               (unsigned long) d->options);

    if (NULL == params->pserver_addr)
    {
      /* The size must be zero if set */
      if (params->server_addr_len_set && (0 != params->server_addr_len))
      {
        probe_log ("EXIT process_interim_params: false (NULL pserver_addr but non-zero len): server_addr_len=%lu",
                   (unsigned long) params->server_addr_len);
        return false;
      }
      /* Ignore parameter if it is NULL */
      probe_log ("INFO pserver_addr is NULL: ignoring this parameter (treated as not set)");
    }
    else if (MHD_INVALID_SOCKET != d->listen_fd)
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (d,
                _ ("MHD_OPTION_LISTEN_SOCKET cannot be used together with " \
                   "MHD_OPTION_SOCK_ADDR_LEN or MHD_OPTION_SOCK_ADDR.\n"));
#endif /* HAVE_MESSAGES */
      probe_log ("EXIT process_interim_params: false (conflict: explicit listen_fd and pserver_addr provided): listen_fd=%ld, pserver_addr=%p",
                 (long) d->listen_fd,
                 (void *) params->pserver_addr);
      return false;
    }
    else if (0 != (d->options & MHD_USE_NO_LISTEN_SOCKET))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (d,
                _ ("MHD_OPTION_SOCK_ADDR_LEN or MHD_OPTION_SOCK_ADDR " \
                   "specified for daemon with MHD_USE_NO_LISTEN_SOCKET " \
                   "flag set.\n"));
#endif /* HAVE_MESSAGES */
      probe_log ("EXIT process_interim_params: false (pserver_addr provided but NO_LISTEN_SOCKET flag is set): options=0x%lx",
                 (unsigned long) d->options);
      if (MHD_INVALID_SOCKET != d->listen_fd)
      {
        (void) MHD_socket_close_ (params->listen_fd);
        params->listen_fd = MHD_INVALID_SOCKET;
        probe_log ("INFO closed listen_fd due to NO_LISTEN_SOCKET while handling pserver_addr: listen_fd_now=%ld",
                   (long) params->listen_fd);
      }
      return false;
    }
    else
    {
      /* Log the assignment to the output pointer to provide evidence of the scenario */
      probe_log ("ACTION assigning *ppsockaddr = params->pserver_addr; before assignment *ppsockaddr=%p, params->pserver_addr=%p",
                 (void *) *ppsockaddr, (void *) params->pserver_addr);

      *ppsockaddr = params->pserver_addr;

      probe_log ("ACTION assigned *ppsockaddr; after assignment *ppsockaddr=%p, params->pserver_addr=%p",
                 (void *) *ppsockaddr, (void *) params->pserver_addr);

      if (params->server_addr_len_set)
      {
        /* The size must be non-zero if set */
        if (0 == params->server_addr_len)
        {
          probe_log ("EXIT process_interim_params: false (server_addr_len set but zero)");
          return false;
        }
        *psockaddr_len = params->server_addr_len;
        probe_log ("ACTION assigned *psockaddr_len = %lu",
                   (unsigned long) *psockaddr_len);
      }
      else
      {
        *psockaddr_len = 0;
        probe_log ("ACTION assigned *psockaddr_len = 0 (server_addr_len not set)");
      }
    }
  }
  probe_log ("EXIT process_interim_params: true");
  return true;
}
