#include <stdio.h>
#include <stdarg.h>
#include <stdint.h>
#include <stdbool.h>
#include <string.h>

/* Probe logging helpers. These are lightweight and only used to
   record control flow and state to /tmp/logs/573.txt. */
static void
log_probe_impl (const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/573.txt", "a");
  if (NULL == f)
    return;
  va_list ap;
  va_start (ap, fmt);
  vfprintf (f, fmt, ap);
  fprintf (f, "\n");
  va_end (ap);
  fflush (f);
  fclose (f);
}

static void
log_buf_bytes (const char *tag, const char *buf, size_t n)
{
  /* log up to first 32 bytes in hex/char form to avoid extremely large logs */
  size_t i, lim = (n < 32) ? n : 32;
  FILE *f = fopen("/tmp/logs/573.txt", "a");
  if (NULL == f)
    return;
  fprintf (f, "%s: addr=%p len=%zu bytes=\"", tag, (const void *)buf, n);
  for (i = 0; i < lim; ++i)
  {
    unsigned char c = (unsigned char) buf[i];
    if (c >= 0x20 && c < 0x7f)
      fputc((int)c, f);
    else if (c == '\n')
      fputs("\\n", f);
    else if (c == '\r')
      fputs("\\r", f);
    else
      fprintf (f, "\\x%02x", c);
  }
  if (n > lim)
    fprintf (f, "... (truncated)");
  fprintf (f, "\"\n");
  fflush (f);
  fclose (f);
}

