#include <stdio.h>
#include <stdarg.h>

/* Probe logging helper: append textual trace to /tmp/logs/1225.txt. */
static void
mhd_probe_log (const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/1225.txt", "a");
  if (f == NULL)
    return;
  va_list ap;
  va_start (ap, fmt);
  vfprintf (f, fmt, ap);
  va_end (ap);
  fputc('\n', f);
  fclose (f);
}

static enum MHD_Result
resume_suspended_connections (struct MHD_Daemon *daemon)
{
  struct MHD_Connection *pos;
  struct MHD_Connection *prev = NULL;
  enum MHD_Result ret;
  const bool used_thr_p_c = (0 != (daemon->options
                                   & MHD_USE_THREAD_PER_CONNECTION));
#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
  mhd_assert (NULL == daemon->worker_pool);
  mhd_assert ( (! MHD_D_IS_USING_THREADS_ (daemon)) || \
               MHD_thread_handle_ID_is_current_thread_ (daemon->tid) );
#endif

  /* Log entry and key daemon state. */
  mhd_probe_log ("enter resume_suspended_connections: daemon=%p resuming=%d suspended_tail=%p shutdown=%d options=0x%lx used_thr_p_c=%d",
                 (void *) daemon,
                 (int) daemon->resuming,
                 (void *) daemon->suspended_connections_tail,
                 (int) daemon->shutdown,
                 (unsigned long) daemon->options,
                 (int) used_thr_p_c);

  ret = MHD_NO;
#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
  MHD_mutex_lock_chk_ (&daemon->cleanup_connection_mutex);
#endif

  if (daemon->resuming)
  {
    prev = daemon->suspended_connections_tail;
    /* During shutdown check for resuming is forced. */
    mhd_assert ((NULL != prev) || (daemon->shutdown) || \
                (0 != (daemon->options & MHD_ALLOW_UPGRADE)));

    mhd_probe_log ("daemon->resuming: found prev=%p", (void *) prev);
  }

  daemon->resuming = false;
  mhd_probe_log ("daemon->resuming set to false");

  while (NULL != (pos = prev))
  {
#ifdef UPGRADE_SUPPORT
    struct MHD_UpgradeResponseHandle *const urh = pos->urh;
#else  /* ! UPGRADE_SUPPORT */
    static const void *const urh = NULL;
#endif /* ! UPGRADE_SUPPORT */
    prev = pos->prev;

    /* Log connection being processed and its flags. */
    mhd_probe_log ("processing pos=%p pos->prev=%p pos->resuming=%d pos->suspended=%d",
                   (void *) pos,
                   (void *) pos->prev,
                   (int) pos->resuming,
                   (int) pos->suspended);

#ifdef UPGRADE_SUPPORT
    mhd_probe_log ("UPGRADE_SUPPORT defined: urh=%p", (void *) urh);
#else
    /* Important probe to show the invariant: urh is a static const NULL here. */
    mhd_probe_log ("UPGRADE_SUPPORT not defined: urh is static const NULL (invariant)");
#endif

    /* Evaluate the continue condition and log detailed components so that
       the decision path is reproducible from logs. */
#ifdef UPGRADE_SUPPORT
    if ( (! pos->resuming) || ( (NULL != urh) && ( (! urh->was_closed) || (! urh->clean_ready) ) ) )
    {
      mhd_probe_log ("continue condition true for pos=%p: pos->resuming=%d urh=%p was_closed=%d clean_ready=%d",
                     (void *) pos,
                     (int) pos->resuming,
                     (void *) urh,
                     (int) (NULL != urh ? urh->was_closed : 0),
                     (int) (NULL != urh ? urh->clean_ready : 0));
      continue;
    }
#else
    if ( (! pos->resuming) )
    {
      mhd_probe_log ("continue condition true for pos=%p: pos->resuming=%d (UPGRADE_SUPPORT not defined; urh invariant NULL)",
                     (void *) pos,
                     (int) pos->resuming);
      continue;
    }
#endif

    ret = MHD_YES;
    mhd_assert (pos->suspended);
    mhd_probe_log ("resuming connection pos=%p: will remove from suspended list", (void *) pos);
    DLL_remove (daemon->suspended_connections_head,
                daemon->suspended_connections_tail,
                pos);
    pos->suspended = false;

    if (NULL == urh)
    {
      mhd_probe_log ("urh is NULL for pos=%p: routing to normal connection resume path", (void *) pos);
      DLL_insert (daemon->connections_head,
                  daemon->connections_tail,
                  pos);
      if (! used_thr_p_c)
      {
        /* Reset timeout timer on resume. */
        if (0 != pos->connection_timeout_ms)
          pos->last_activity = MHD_monotonic_msec_counter ();

        if (pos->connection_timeout_ms == daemon->connection_timeout_ms)
        {
          mhd_probe_log ("pos=%p: inserting into normal_timeout list", (void *) pos);
          XDLL_insert (daemon->normal_timeout_head,
                       daemon->normal_timeout_tail,
                       pos);
        }
        else
        {
          mhd_probe_log ("pos=%p: inserting into manual_timeout list", (void *) pos);
          XDLL_insert (daemon->manual_timeout_head,
                       daemon->manual_timeout_tail,
                       pos);
        }
      }
#ifdef EPOLL_SUPPORT
      if (MHD_D_IS_USING_EPOLL_ (daemon))
      {
        if (0 != (pos->epoll_state & MHD_EPOLL_STATE_IN_EREADY_EDLL))
          MHD_PANIC ("Resumed connection was already in EREADY set.\n");
        /* we always mark resumed connections as ready, as we
           might have missed the edge poll event during suspension */
        EDLL_insert (daemon->eready_head,
                     daemon->eready_tail,
                     pos);
        pos->epoll_state |= MHD_EPOLL_STATE_IN_EREADY_EDLL   \
                            | MHD_EPOLL_STATE_READ_READY
                            | MHD_EPOLL_STATE_WRITE_READY;
        pos->epoll_state &= ~((enum MHD_EpollState) MHD_EPOLL_STATE_SUSPENDED);
        mhd_probe_log ("pos=%p: epoll state after resume: 0x%x", (void *) pos, (unsigned int) pos->epoll_state);
      }
#endif
    }
#ifdef UPGRADE_SUPPORT
    else
    {
      /* Data forwarding was finished (for TLS connections) AND
       * application was closed upgraded connection.
       * Insert connection into cleanup list. */

      mhd_probe_log ("urh non-NULL for pos=%p: routing to cleanup path", (void *) pos);

      if ( (NULL != daemon->notify_completed) &&
           (! MHD_D_IS_USING_THREAD_PER_CONN_ (daemon)) &&
           (pos->rq.client_aware) )
      {
        daemon->notify_completed (daemon->notify_completed_cls,
                                  pos,
                                  &pos->rq.client_context,
                                  MHD_REQUEST_TERMINATED_COMPLETED_OK);
        pos->rq.client_aware = false;
      }
      DLL_insert (daemon->cleanup_head,
                  daemon->cleanup_tail,
                  pos);
      daemon->data_already_pending = true;
    }
#endif /* UPGRADE_SUPPORT */
    pos->resuming = false;
    mhd_probe_log ("finished processing pos=%p; pos->resuming now false", (void *) pos);
  }
#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
  MHD_mutex_unlock_chk_ (&daemon->cleanup_connection_mutex);
#endif
  if ( (used_thr_p_c) &&
       (MHD_NO != ret) )
  {   /* Wake up suspended connections. */
    if (! MHD_itc_activate_ (daemon->itc,
                             "w"))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Failed to signal resume of connection via " \
                   "inter-thread communication channel.\n"));
#endif
      mhd_probe_log ("MHD_itc_activate_ failed for daemon=%p", (void *) daemon);
    }
    else
    {
      mhd_probe_log ("MHD_itc_activate_ succeeded for daemon=%p", (void *) daemon);
    }
  }
  mhd_probe_log ("exit resume_suspended_connections: daemon=%p ret=%d", (void *) daemon, (int) ret);
  return ret;
}
