#include <stdio.h>
#include <stdarg.h>
#include <stdint.h>
#include <stddef.h>
#include <stdbool.h>

/* Probe logging helper: append human-readable trace logs to /tmp/logs/1831.txt */
static void
mhd_log_probe (const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/1831.txt", "a");
  if (NULL == f)
    return;
  {
    va_list ap;
    va_start (ap, fmt);
    vfprintf (f, fmt, ap);
    fprintf (f, "\n");
    va_end (ap);
  }
  fclose (f);
}

static ssize_t
send_iov_nontls (struct MHD_Connection *connection,
                 struct MHD_iovec_track_ *const r_iov,
                 bool push_data)
{
  ssize_t res;
  size_t items_to_send;
#ifdef HAVE_SENDMSG
  struct msghdr msg;
#elif defined(MHD_WINSOCK_SOCKETS)
  DWORD bytes_sent;
  DWORD cnt_w;
#endif /* MHD_WINSOCK_SOCKETS */

  /* Log entry and basic state */
  mhd_log_probe ("send_iov_nontls ENTRY: connection=%p socket_fd=%ld state=%d push_data=%d r_iov=%p r_iov->cnt=%zu r_iov->sent=%zu",
                 (void *) connection,
                 (long) (intptr_t) connection->socket_fd,
                 (int) connection->state,
                 (int) push_data,
                 (void *) r_iov,
                 r_iov->cnt,
                 r_iov->sent);

  mhd_assert (0 == (connection->daemon->options & MHD_USE_TLS));

  if ( (MHD_INVALID_SOCKET == connection->socket_fd) ||
       (MHD_CONNECTION_CLOSED == connection->state) )
  {
    mhd_log_probe ("send_iov_nontls: early return - invalid socket or closed (socket_fd=%ld state=%d)",
                   (long) (intptr_t) connection->socket_fd,
                   (int) connection->state);
    return MHD_ERR_NOTCONN_;
  }

  items_to_send = r_iov->cnt - r_iov->sent;
  mhd_log_probe ("Computed items_to_send=%zu (r_iov->cnt=%zu - r_iov->sent=%zu)",
                 items_to_send, r_iov->cnt, r_iov->sent);
#ifdef _MHD_IOV_MAX
  mhd_log_probe ("_MHD_IOV_MAX is defined and has value: %d", _MHD_IOV_MAX);
  if (_MHD_IOV_MAX < items_to_send)
  {
    mhd_log_probe ("_MHD_IOV_MAX (%d) < items_to_send (%zu): entering truncation branch",
                   _MHD_IOV_MAX, items_to_send);
    mhd_assert (0 < _MHD_IOV_MAX);
    if (0 == _MHD_IOV_MAX)
    {
      mhd_log_probe ("Inner condition 0 == _MHD_IOV_MAX true - returning MHD_ERR_NOTCONN_");
      return MHD_ERR_NOTCONN_; /* Should never happen */
    }
    mhd_log_probe ("Truncating items_to_send from %zu to %d and marking push_data=false",
                   items_to_send, _MHD_IOV_MAX);
    items_to_send = _MHD_IOV_MAX;
    push_data = false; /* Incomplete response */
  }
#endif /* _MHD_IOV_MAX */
#ifdef HAVE_SENDMSG
  memset (&msg, 0, sizeof(struct msghdr));
  msg.msg_iov = r_iov->iov + r_iov->sent;
  msg.msg_iovlen = items_to_send;

  pre_send_setopt (connection, true, push_data);
#ifdef MHD_USE_MSG_MORE
  res = sendmsg (connection->socket_fd, &msg,
                 MSG_NOSIGNAL_OR_ZERO | (push_data ? 0 : MSG_MORE));
#else  /* ! MHD_USE_MSG_MORE */
  res = sendmsg (connection->socket_fd, &msg, MSG_NOSIGNAL_OR_ZERO);
#endif /* ! MHD_USE_MSG_MORE */
#elif defined(HAVE_WRITEV)
  pre_send_setopt (connection, true, push_data);
  res = writev (connection->socket_fd, r_iov->iov + r_iov->sent,
                items_to_send);
#elif defined(MHD_WINSOCK_SOCKETS)
#ifdef _WIN64
  if (items_to_send > UINT32_MAX)
  {
    cnt_w = UINT32_MAX;
    push_data = false; /* Incomplete response */
  }
  else
    cnt_w = (DWORD) items_to_send;
#else  /* ! _WIN64 */
  cnt_w = (DWORD) items_to_send;
#endif /* ! _WIN64 */
  pre_send_setopt (connection, true, push_data);
  if (0 == WSASend (connection->socket_fd,
                    (LPWSABUF) (r_iov->iov + r_iov->sent),
                    cnt_w,
                    &bytes_sent, 0, NULL, NULL))
    res = (ssize_t) bytes_sent;
  else
    res = -1;
#else /* !HAVE_SENDMSG && !HAVE_WRITEV && !MHD_WINSOCK_SOCKETS */
#error No vector-send function available
#endif

  mhd_log_probe ("After send: res=%zd items_to_send=%zu push_data=%d",
                 res, items_to_send, (int) push_data);

  if (0 > res)
  {
    const int err = MHD_socket_get_error_ ();
    mhd_log_probe ("send returned error res=%zd err=%d", res, err);

    if (MHD_SCKT_ERR_IS_EAGAIN_ (err))
    {
#ifdef EPOLL_SUPPORT
      /* EAGAIN --- no longer write-ready */
      connection->epoll_state &=
        ~((enum MHD_EpollState) MHD_EPOLL_STATE_WRITE_READY);
#endif /* EPOLL_SUPPORT */
      mhd_log_probe ("send_iov_nontls: EAGAIN - returning MHD_ERR_AGAIN_");
      return MHD_ERR_AGAIN_;
    }
    if (MHD_SCKT_ERR_IS_EINTR_ (err))
    {
      mhd_log_probe ("send_iov_nontls: EINTR - returning MHD_ERR_AGAIN_");
      return MHD_ERR_AGAIN_;
    }
    if (MHD_SCKT_ERR_IS_REMOTE_DISCNN_ (err))
    {
      mhd_log_probe ("send_iov_nontls: remote disconnect - returning MHD_ERR_CONNRESET_");
      return MHD_ERR_CONNRESET_;
    }
    if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_EPIPE_))
    {
      mhd_log_probe ("send_iov_nontls: EPIPE - returning MHD_ERR_PIPE_");
      return MHD_ERR_PIPE_;
    }
    if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_EOPNOTSUPP_))
    {
      mhd_log_probe ("send_iov_nontls: EOPNOTSUPP - returning MHD_ERR_OPNOTSUPP_");
      return MHD_ERR_OPNOTSUPP_;
    }
    if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_ENOTCONN_))
    {
      mhd_log_probe ("send_iov_nontls: ENOTCONN - returning MHD_ERR_NOTCONN_");
      return MHD_ERR_NOTCONN_;
    }
    if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_EINVAL_))
    {
      mhd_log_probe ("send_iov_nontls: EINVAL - returning MHD_ERR_INVAL_");
      return MHD_ERR_INVAL_;
    }
    if (MHD_SCKT_ERR_IS_LOW_RESOURCES_ (err))
    {
      mhd_log_probe ("send_iov_nontls: low resources - returning MHD_ERR_NOMEM_");
      return MHD_ERR_NOMEM_;
    }
    if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_EBADF_))
    {
      mhd_log_probe ("send_iov_nontls: EBADF - returning MHD_ERR_BADF_");
      return MHD_ERR_BADF_;
    }
    /* Treat any other error as a hard error. */
    mhd_log_probe ("send_iov_nontls: unhandled socket error - returning MHD_ERR_NOTCONN_");
    return MHD_ERR_NOTCONN_;
  }

  /* Some data has been sent */
  if (1)
  {
    size_t track_sent = (size_t) res;
    /* Adjust the internal tracking information for the iovec to
     * take this last send into account. */
    while ((0 != track_sent) && (r_iov->iov[r_iov->sent].iov_len <= track_sent))
    {
      track_sent -= r_iov->iov[r_iov->sent].iov_len;
      r_iov->sent++; /* The iov element has been completely sent */
      mhd_assert ((r_iov->cnt > r_iov->sent) || (0 == track_sent));
      mhd_log_probe ("Advanced r_iov->sent to %zu remaining track_sent=%zu",
                     r_iov->sent, track_sent);
    }

    if (r_iov->cnt == r_iov->sent)
    {
      post_send_setopt (connection, true, push_data);
      mhd_log_probe ("All iov elements sent; called post_send_setopt. r_iov->sent=%zu r_iov->cnt=%zu",
                     r_iov->sent, r_iov->cnt);
    }
    else
    {
#ifdef EPOLL_SUPPORT
      connection->epoll_state &=
        ~((enum MHD_EpollState) MHD_EPOLL_STATE_WRITE_READY);
#endif /* EPOLL_SUPPORT */
      if (0 != track_sent)
      {
        mhd_assert (r_iov->cnt > r_iov->sent);
        /* The last iov element has been partially sent */
        r_iov->iov[r_iov->sent].iov_base =
          (void *) ((uint8_t *) r_iov->iov[r_iov->sent].iov_base + track_sent);
        r_iov->iov[r_iov->sent].iov_len -= (MHD_iov_size_) track_sent;
        mhd_log_probe ("Partial send: adjusted iov[%zu] base and len now %zu",
                       r_iov->sent, (size_t) r_iov->iov[r_iov->sent].iov_len);
      }
    }
  }

  mhd_log_probe ("send_iov_nontls EXIT: returning res=%zd r_iov->sent=%zu r_iov->cnt=%zu",
                 res, r_iov->sent, r_iov->cnt);

  return res;
}
