#include <stdio.h>
#include <stdarg.h>

/* Probe functions to log control flow and state for the epoll setup.
   Logs are appended to /tmp/logs/1243.txt. */
static void
probe_log (const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/1243.txt", "a");
  if (f == NULL)
    return;
  va_list ap;
  va_start (ap, fmt);
  vfprintf (f, fmt, ap);
  va_end (ap);
  fprintf (f, "\n");
  fclose (f);
}

static void
probe_log_event (const char *stage, const struct epoll_event *ev)
{
  /* Print the event flags and the data pointer as evidence of pointer usage */
  probe_log ("%s: event.events=0x%x event.data.ptr=%p", stage,
              (unsigned int) ev->events, ev->data.ptr);
}

static enum MHD_Result
setup_epoll_to_listen (struct MHD_Daemon *daemon)
{
  struct epoll_event event;
  MHD_socket ls;

  mhd_assert (MHD_D_IS_USING_EPOLL_ (daemon));
  mhd_assert (0 == (daemon->options & MHD_USE_THREAD_PER_CONNECTION));
  mhd_assert ( (! MHD_D_IS_USING_THREADS_ (daemon)) || \
               (MHD_INVALID_SOCKET != (ls = daemon->listen_fd)) || \
               MHD_ITC_IS_VALID_ (daemon->itc) );
  daemon->epoll_fd = setup_epoll_fd (daemon);
  if (! MHD_D_IS_USING_THREADS_ (daemon)
      && (0 != (daemon->options & MHD_USE_AUTO)))
  {
    /* Application requested "MHD_USE_AUTO", probably MHD_get_fdset() will be
       used.
       Make sure that epoll FD is suitable for fd_set.
       Actually, MHD_get_fdset() is allowed for MHD_USE_EPOLL direct,
       but most probably direct requirement for MHD_USE_EPOLL means that
       epoll FD will be used directly. This logic is fuzzy, but better
       than nothing with current MHD API. */
    if (! MHD_D_DOES_SCKT_FIT_FDSET_ (daemon->epoll_fd, daemon))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("The epoll FD is too large to be used with fd_set.\n"));
#endif /* HAVE_MESSAGES */
      return MHD_NO;
    }
  }
  if (-1 == daemon->epoll_fd)
    return MHD_NO;
#if defined(HTTPS_SUPPORT) && defined(UPGRADE_SUPPORT)
  if (0 != (MHD_ALLOW_UPGRADE & daemon->options))
  {
    daemon->epoll_upgrade_fd = setup_epoll_fd (daemon);
    if (MHD_INVALID_SOCKET == daemon->epoll_upgrade_fd)
      return MHD_NO;
  }
#endif /* HTTPS_SUPPORT && UPGRADE_SUPPORT */
  if ( (MHD_INVALID_SOCKET != (ls = daemon->listen_fd)) &&
       (! daemon->was_quiesced) )
  {
    event.events = EPOLLIN | EPOLLRDHUP;
    event.data.ptr = daemon;

    /* Log the attempt to add the listen socket to epoll */
    probe_log ("setup_epoll_to_listen: adding listen fd: ls=%d epoll_fd=%d daemon=%p",
               (int) ls, (int) daemon->epoll_fd, (void *) daemon);
    probe_log_event ("before epoll_ctl (listen)", &event);

    if (0 != epoll_ctl (daemon->epoll_fd,
                        EPOLL_CTL_ADD,
                        ls,
                        &event))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Call to epoll_ctl failed: %s\n"),
                MHD_socket_last_strerr_ ());
#endif
      probe_log ("setup_epoll_to_listen: epoll_ctl failed for listen fd: ls=%d epoll_fd=%d",
                 (int) ls, (int) daemon->epoll_fd);
      return MHD_NO;
    }
    daemon->listen_socket_in_epoll = true;
    probe_log ("setup_epoll_to_listen: listen fd added to epoll: ls=%d epoll_fd=%d",
               (int) ls, (int) daemon->epoll_fd);
  }

  if (MHD_ITC_IS_VALID_ (daemon->itc))
  {
    event.events = EPOLLIN | EPOLLRDHUP;

    /* Log the marker object address (const) before casting away const */
    probe_log ("setup_epoll_to_listen: epoll_itc_marker (const) address=%p",
               (const void *) &epoll_itc_marker);

    /* Cast away const here as in original code; keep exact behavior. */
    event.data.ptr = _MHD_DROP_CONST (epoll_itc_marker);

    /* Log what pointer value is actually stored into event.data.ptr */
    probe_log_event ("after assigning event.data.ptr (itc marker)", &event);
    probe_log ("setup_epoll_to_listen: adding itc fd: itc_r_fd=%d epoll_fd=%d stored_ptr=%p",
               (int) MHD_itc_r_fd_ (daemon->itc), (int) daemon->epoll_fd, event.data.ptr);

    if (0 != epoll_ctl (daemon->epoll_fd,
                        EPOLL_CTL_ADD,
                        MHD_itc_r_fd_ (daemon->itc),
                        &event))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Call to epoll_ctl failed: %s\n"),
                MHD_socket_last_strerr_ ());
#endif
      probe_log ("setup_epoll_to_listen: epoll_ctl failed for itc fd: itc_r_fd=%d epoll_fd=%d",
                 (int) MHD_itc_r_fd_ (daemon->itc), (int) daemon->epoll_fd);
      return MHD_NO;
    }
    probe_log ("setup_epoll_to_listen: itc fd added to epoll: itc_r_fd=%d epoll_fd=%d",
               (int) MHD_itc_r_fd_ (daemon->itc), (int) daemon->epoll_fd);
  }
  return MHD_YES;
}
