#include <stdio.h>
#include <time.h>

/* Probe logging helper: records control flow and key state to /tmp/logs/632.txt */
static void
mhd_probe_log (const char *event, struct MHD_Connection *connection,
               unsigned int status_code,
               const char *message,
               size_t message_len,
               char *header_name,
               size_t header_name_len,
               char *header_value,
               size_t header_value_len)
{
  FILE *f = fopen("/tmp/logs/632.txt", "a");
  if (NULL == f)
    return;

  time_t t = time (NULL);
  fprintf (f, "[%ld] event=%s conn=%p state=%d stop_with_error=%d discard_request=%d in_access_handler=%d\n",
           (long) t,
           event,
           (void *) connection,
           (int) connection->state,
           (int) connection->stop_with_error,
           (int) connection->discard_request,
           (int) connection->in_access_handler);
  fprintf (f, "    status_code=%u message_ptr=%p message_len=%zu\n",
           status_code,
           (const void *) message,
           message_len);
  fprintf (f, "    header_name=%p header_name_len=%zu header_value=%p header_value_len=%zu\n",
           (void *) header_name,
           header_name_len,
           (void *) header_value,
           header_value_len);

  fclose (f);
}

static void
transmit_error_response_len (struct MHD_Connection *connection,
                             unsigned int status_code,
                             const char *message,
                             size_t message_len,
                             char *header_name,
                             size_t header_name_len,
                             char *header_value,
                             size_t header_value_len)
{
  struct MHD_Response *response;
  enum MHD_Result iret;

  /* Log entry */
  mhd_probe_log ("entry", connection, status_code, message, message_len,
                 header_name, header_name_len, header_value, header_value_len);

  mhd_assert (! connection->stop_with_error); /* Do not send error twice */
  if (connection->stop_with_error)
  { /* Should not happen */
    mhd_probe_log ("stop_with_error_taken", connection, status_code, message,
                   message_len, header_name, header_name_len, header_value,
                   header_value_len);
    if (MHD_CONNECTION_CLOSED > connection->state)
      connection->state = MHD_CONNECTION_CLOSED;
    free (header_name);
    free (header_value);
    return;
  }
  connection->stop_with_error = true;
  connection->discard_request = true;
#ifdef HAVE_MESSAGES
  /* Log right before the diagnostic logging so the values are captured when
     HAVE_MESSAGES is defined and the extra arguments are evaluated. */
  mhd_probe_log ("before_MHD_DLOG_initial", connection, status_code, message,
                 message_len, header_name, header_name_len, header_value,
                 header_value_len);
  MHD_DLOG (connection->daemon,
            _ ("Error processing request (HTTP response code is %u ('%s')). " \
               "Closing connection.\n"),
            status_code,
            message);
#endif
  if (MHD_CONNECTION_START_REPLY < connection->state)
  {
#ifdef HAVE_MESSAGES
    /* Log that we are too late to send an error response. Capture args as
       well to reproduce the scenario where extra arguments are evaluated. */
    mhd_probe_log ("before_MHD_DLOG_toolate", connection, status_code, message,
                   message_len, header_name, header_name_len, header_value,
                   header_value_len);
    MHD_DLOG (connection->daemon,
              _ ("Too late to send an error response, " \
                 "response is being sent already.\n"),
              status_code,
              message);
#endif
    CONNECTION_CLOSE_ERROR (connection,
                            _ ("Too late for error response."));
    free (header_name);
    free (header_value);
    return;
  }
  /* TODO: remove when special error queue function is implemented */
  connection->state = MHD_CONNECTION_FULL_REQ_RECEIVED;
  if (0 != connection->read_buffer_size)
  {
    /* Read buffer is not needed anymore, discard it
     * to free some space for error response. */
    MHD_pool_deallocate (connection->pool,
                         connection->read_buffer,
                         connection->read_buffer_size);
    connection->read_buffer = NULL;
    connection->read_buffer_size = 0;
    connection->read_buffer_offset = 0;
  }
  if (NULL != connection->rp.response)
  {
    MHD_destroy_response (connection->rp.response);
    connection->rp.response = NULL;
  }
  response = MHD_create_response_from_buffer_static (message_len,
                                                     message);
  if (NULL == response)
  {
#ifdef HAVE_MESSAGES
    /* Log that creation failed and the values that would be passed to
       MHD_DLOG (including the unused ones). This log helps reproduce the
       dead-operation scenario where extra arguments are evaluated but not
       used by the format string. */
    mhd_probe_log ("before_MHD_DLOG_failed_create", connection, status_code,
                   message, message_len, header_name, header_name_len,
                   header_value, header_value_len);
    MHD_DLOG (connection->daemon,
              _ ("Failed to create error response.\n"),
              status_code,
              message);
#endif
    /* can't even send a reply, at least close the connection */
    connection->state = MHD_CONNECTION_CLOSED;
    free (header_name);
    free (header_value);
    return;
  }

  /* Log after successful response creation */
  mhd_probe_log ("response_created", connection, status_code, message,
                 message_len, header_name, header_name_len, header_value,
                 header_value_len);

  mhd_assert ((0 == header_name_len) || (NULL != header_name));
  mhd_assert ((NULL == header_name) || (0 != header_name_len));
  mhd_assert ((0 == header_value_len) || (NULL != header_value));
  mhd_assert ((NULL == header_value) || (0 != header_value_len));
  mhd_assert ((NULL == header_name) || (NULL != header_value));
  mhd_assert ((NULL != header_value) || (NULL == header_name));
  if (NULL != header_name)
  {
    iret = MHD_add_response_entry_no_alloc_ (response,
                                             MHD_HEADER_KIND,
                                             header_name, header_name_len,
                                             header_value, header_value_len);
    if (MHD_NO == iret)
    {
      free (header_name);
      free (header_value);
      mhd_probe_log ("header_add_failed", connection, status_code, message,
                     message_len, NULL, 0, NULL, 0);
    }
  }
  else
    iret = MHD_YES;

  if (MHD_NO != iret)
  {
    bool before = connection->in_access_handler;

    /* Fake the flag for the internal call */
    connection->in_access_handler = true;
    mhd_probe_log ("before_queue_response", connection, status_code, message,
                   message_len, header_name, header_name_len, header_value,
                   header_value_len);
    iret = MHD_queue_response (connection,
                               status_code,
                               response);
    connection->in_access_handler = before;
    mhd_probe_log ("after_queue_response", connection, status_code, message,
                   message_len, header_name, header_name_len, header_value,
                   header_value_len);
  }
  MHD_destroy_response (response);
  if (MHD_NO == iret)
  {
    /* can't even send a reply, at least close the connection */
    CONNECTION_CLOSE_ERROR (connection,
                            _ ("Closing connection " \
                               "(failed to queue error response)."));
    mhd_probe_log ("queue_failed_close", connection, status_code, message,
                   message_len, header_name, header_name_len, header_value,
                   header_value_len);
    return;
  }
  mhd_assert (NULL != connection->rp.response);
  /* Do not reuse this connection. */
  connection->keepalive = MHD_CONN_MUST_CLOSE;
  if (MHD_NO == build_header_response (connection))
  {
    /* No memory. Release everything. */
    mhd_probe_log ("build_header_response_failed_first", connection,
                   status_code, message, message_len, header_name,
                   header_name_len, header_value, header_value_len);
    connection->rq.version = NULL;
    connection->rq.method = NULL;
    connection->rq.url = NULL;
    connection->rq.url_len = 0;
    connection->rq.headers_received = NULL;
    connection->rq.headers_received_tail = NULL;
    connection->write_buffer = NULL;
    connection->write_buffer_size = 0;
    connection->write_buffer_send_offset = 0;
    connection->write_buffer_append_offset = 0;
    connection->read_buffer
      = MHD_pool_reset (connection->pool,
                        NULL,
                        0,
                        0);
    connection->read_buffer_size = 0;

    /* Retry with empty buffer */
    if (MHD_NO == build_header_response (connection))
    {
      CONNECTION_CLOSE_ERROR (connection,
                              _ ("Closing connection " \
                                 "(failed to create error response header)."));
      mhd_probe_log ("build_header_response_failed_second", connection,
                     status_code, message, message_len, header_name,
                     header_name_len, header_value, header_value_len);
      return;
    }
  }
  connection->state = MHD_CONNECTION_HEADERS_SENDING;
  mhd_probe_log ("done", connection, status_code, message, message_len,
                 header_name, header_name_len, header_value, header_value_len);
}
