#include <stdio.h>
#include <stdlib.h>
#include <stdarg.h>

/* Probe helper: append a formatted log line to the given logfile. */
static void
mhd_probe_log_printf (const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/632.txt", "a");
  if (f == NULL)
    return;
  va_list ap;
  va_start (ap, fmt);
  vfprintf (f, fmt, ap);
  va_end (ap);
  fprintf (f, "\n");
  fclose (f);
}

/* Probe helper: dump a few fields of the connection to the logfile.
 * Keep the probe non-intrusive: it only reads fields. */
static void
mhd_probe_log_connection (const char *tag,
                          struct MHD_Connection *connection,
                          unsigned int status_code,
                          const char *message)
{
  if (connection == NULL)
  {
    mhd_probe_log_printf ("%s: connection=NULL status=%u message=%s", tag, status_code, (message ? message : "(null)"));
    return;
  }

  /* Many of these fields are read-only for logging purposes. If some
   * fields are not present in a particular build, the original build
   * would not have compiled either, so this keeps parity with original
   * assumptions. */
  mhd_probe_log_printf ("%s: status=%u stop_with_error=%d state=%d rp.response=%p in_access_handler=%d keepalive=%d message=%s",
                        tag,
                        status_code,
                        (int) connection->stop_with_error,
                        (int) connection->state,
                        (void *) connection->rp.response,
                        (int) connection->in_access_handler,
                        (int) connection->keepalive,
                        (message ? message : "(null)"));
}

static void
transmit_error_response_len (struct MHD_Connection *connection,
                             unsigned int status_code,
                             const char *message,
                             size_t message_len,
                             char *header_name,
                             size_t header_name_len,
                             char *header_value,
                             size_t header_value_len)
{
  struct MHD_Response *response;
  enum MHD_Result iret;

  /* Probe: record state just before the assert that may be a no-op. */
  mhd_probe_log_connection ("BEFORE_ASSERT_stop_with_error", connection, status_code, message);
  mhd_assert (! connection->stop_with_error); /* Do not send error twice */
  /* Probe: record state immediately after the (possibly no-op) assert. */
  mhd_probe_log_connection ("AFTER_ASSERT_stop_with_error", connection, status_code, message);

  if (connection->stop_with_error)
  { /* Should not happen */
    mhd_probe_log_printf ("BRANCH: stop_with_error true; closing/cleanup path. state=%d", (int) connection->state);
    if (MHD_CONNECTION_CLOSED > connection->state)
      connection->state = MHD_CONNECTION_CLOSED;
    free (header_name);
    free (header_value);
    mhd_probe_log_connection ("RETURN_STOP_WITH_ERROR", connection, status_code, message);
    return;
  }
  connection->stop_with_error = true;
  connection->discard_request = true;
#ifdef HAVE_MESSAGES
  MHD_DLOG (connection->daemon,
            _ ("Error processing request (HTTP response code is %u ('%s')). " \
               "Closing connection.\n"),
            status_code,
            message);
#endif
  if (MHD_CONNECTION_START_REPLY < connection->state)
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (connection->daemon,
              _ ("Too late to send an error response, " \
                 "response is being sent already.\n"),
              status_code,
              message);
#endif
    mhd_probe_log_printf ("BRANCH: Too late to send error response; state=%d", (int) connection->state);
    CONNECTION_CLOSE_ERROR (connection,
                            _ ("Too late for error response."));
    free (header_name);
    free (header_value);
    mhd_probe_log_connection ("RETURN_TOO_LATE", connection, status_code, message);
    return;
  }
  /* TODO: remove when special error queue function is implemented */
  connection->state = MHD_CONNECTION_FULL_REQ_RECEIVED;
  if (0 != connection->read_buffer_size)
  {
    /* Read buffer is not needed anymore, discard it
     * to free some space for error response. */
    MHD_pool_deallocate (connection->pool,
                         connection->read_buffer,
                         connection->read_buffer_size);
    connection->read_buffer = NULL;
    connection->read_buffer_size = 0;
    connection->read_buffer_offset = 0;
  }
  if (NULL != connection->rp.response)
  {
    MHD_destroy_response (connection->rp.response);
    connection->rp.response = NULL;
  }
  response = MHD_create_response_from_buffer_static (message_len,
                                                     message);
  if (NULL == response)
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (connection->daemon,
              _ ("Failed to create error response.\n"),
              status_code,
              message);
#endif
    /* can't even send a reply, at least close the connection */
    connection->state = MHD_CONNECTION_CLOSED;
    free (header_name);
    free (header_value);
    mhd_probe_log_connection ("RETURN_NO_RESPONSE_CREATED", connection, status_code, message);
    return;
  }
  mhd_assert ((0 == header_name_len) || (NULL != header_name));
  mhd_assert ((NULL == header_name) || (0 != header_name_len));
  mhd_assert ((0 == header_value_len) || (NULL != header_value));
  mhd_assert ((NULL == header_value) || (0 != header_value_len));
  mhd_assert ((NULL == header_name) || (NULL != header_value));
  mhd_assert ((NULL != header_value) || (NULL == header_name));
  if (NULL != header_name)
  {
    iret = MHD_add_response_entry_no_alloc_ (response,
                                             MHD_HEADER_KIND,
                                             header_name, header_name_len,
                                             header_value, header_value_len);
    if (MHD_NO == iret)
    {
      free (header_name);
      free (header_value);
    }
  }
  else
    iret = MHD_YES;

  if (MHD_NO != iret)
  {
    bool before = connection->in_access_handler;

    /* Fake the flag for the internal call */
    connection->in_access_handler = true;
    iret = MHD_queue_response (connection,
                               status_code,
                               response);
    connection->in_access_handler = before;
  }
  MHD_destroy_response (response);
  if (MHD_NO == iret)
  {
    /* can't even send a reply, at least close the connection */
    CONNECTION_CLOSE_ERROR (connection,
                            _ ("Closing connection " \
                               "(failed to queue error response)."));
    mhd_probe_log_connection ("RETURN_FAILED_TO_QUEUE", connection, status_code, message);
    return;
  }

  /* Probe: log connection state right before the assert that may be a no-op. */
  mhd_probe_log_connection ("BEFORE_ASSERT_rp_response_not_null", connection, status_code, message);
  mhd_assert (NULL != connection->rp.response);
  /* Probe: log connection state immediately after the (possibly no-op) assert. */
  mhd_probe_log_connection ("AFTER_ASSERT_rp_response_not_null", connection, status_code, message);

  /* Do not reuse this connection. */
  connection->keepalive = MHD_CONN_MUST_CLOSE;
  if (MHD_NO == build_header_response (connection))
  {
    /* No memory. Release everything. */
    connection->rq.version = NULL;
    connection->rq.method = NULL;
    connection->rq.url = NULL;
    connection->rq.url_len = 0;
    connection->rq.headers_received = NULL;
    connection->rq.headers_received_tail = NULL;
    connection->write_buffer = NULL;
    connection->write_buffer_size = 0;
    connection->write_buffer_send_offset = 0;
    connection->write_buffer_append_offset = 0;
    connection->read_buffer
      = MHD_pool_reset (connection->pool,
                        NULL,
                        0,
                        0);
    connection->read_buffer_size = 0;

    /* Retry with empty buffer */
    if (MHD_NO == build_header_response (connection))
    {
      CONNECTION_CLOSE_ERROR (connection,
                              _ ("Closing connection " \
                                 "(failed to create error response header)."));
      mhd_probe_log_connection ("RETURN_FAILED_BUILD_HEADER", connection, status_code, message);
      return;
    }
  }
  connection->state = MHD_CONNECTION_HEADERS_SENDING;
  mhd_probe_log_connection ("END_TRANSMIT_ERROR_RESPONSE_LEN", connection, status_code, message);
}
