#include <stdio.h>
#include <stdarg.h>
#include <stdbool.h>
#include <stddef.h>
#include <stdlib.h>

/* Probe logging helper: append textual logs to /tmp/logs/639.txt */
static void
probe_log (const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/639.txt", "a");
  if (NULL == f)
    return;
  va_list ap;
  va_start (ap, fmt);
  vfprintf (f, fmt, ap);
  va_end (ap);
  fprintf (f, "\n");
  fclose (f);
}

static void
transmit_error_response_len (struct MHD_Connection *connection,
                             unsigned int status_code,
                             const char *message,
                             size_t message_len,
                             char *header_name,
                             size_t header_name_len,
                             char *header_value,
                             size_t header_value_len)
{
  struct MHD_Response *response;
  enum MHD_Result iret;

  probe_log ("enter transmit_error_response_len: connection=%p, status_code=%u, message=%p, message_len=%zu, header_name=%p, header_name_len=%zu, header_value=%p, header_value_len=%zu, connection->state=%d, connection->stop_with_error=%d",
             (void *) connection,
             status_code,
             (const void *) message,
             message_len,
             (void *) header_name,
             header_name_len,
             (void *) header_value,
             header_value_len,
             connection ? connection->state : -1,
             connection ? (int) connection->stop_with_error : -1);

  mhd_assert (! connection->stop_with_error); /* Do not send error twice */
  if (connection->stop_with_error)
  { /* Should not happen */
    probe_log ("branch: connection->stop_with_error is true; closing/returning. connection=%p, state(before)=%d",
               (void *) connection,
               connection->state);
    if (MHD_CONNECTION_CLOSED > connection->state)
      connection->state = MHD_CONNECTION_CLOSED;
    free (header_name);
    free (header_value);
    probe_log ("exit transmit_error_response_len (stop_with_error branch): connection=%p", (void *) connection);
    return;
  }
  connection->stop_with_error = true;
  connection->discard_request = true;
#ifdef HAVE_MESSAGES
  MHD_DLOG (connection->daemon,
            _ ("Error processing request (HTTP response code is %u ('%s')). " \
               "Closing connection.\n"),
            status_code,
            message);
#endif
  if (MHD_CONNECTION_START_REPLY < connection->state)
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (connection->daemon,
              _ ("Too late to send an error response, " \
                 "response is being sent already.\n"),
              status_code,
              message);
#endif
    probe_log ("branch: too late to send error response; connection=%p, state=%d",
               (void *) connection,
               connection->state);
    CONNECTION_CLOSE_ERROR (connection,
                            _ ("Too late for error response."));
    free (header_name);
    free (header_value);
    probe_log ("exit transmit_error_response_len (too late branch): connection=%p", (void *) connection);
    return;
  }
  /* TODO: remove when special error queue function is implemented */
  connection->state = MHD_CONNECTION_FULL_REQ_RECEIVED;
  if (0 != connection->read_buffer_size)
  {
    /* Read buffer is not needed anymore, discard it
     * to free some space for error response. */
    MHD_pool_deallocate (connection->pool,
                         connection->read_buffer,
                         connection->read_buffer_size);
    connection->read_buffer = NULL;
    connection->read_buffer_size = 0;
    connection->read_buffer_offset = 0;
  }
  if (NULL != connection->rp.response)
  {
    MHD_destroy_response (connection->rp.response);
    connection->rp.response = NULL;
  }

  probe_log ("about to call MHD_create_response_from_buffer_static: connection=%p, message=%p, message_len=%zu",
             (void *) connection,
             (const void *) message,
             message_len);

  response = MHD_create_response_from_buffer_static (message_len,
                                                     message);

  probe_log ("returned from MHD_create_response_from_buffer_static: connection=%p, response=%p",
             (void *) connection,
             (void *) response);

  if (NULL == response)
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (connection->daemon,
              _ ("Failed to create error response.\n"),
              status_code,
              message);
#endif
    probe_log ("MHD_create_response_from_buffer_static returned NULL: connection=%p, message=%p, message_len=%zu",
               (void *) connection,
               (const void *) message,
               message_len);
    /* can't even send a reply, at least close the connection */
    connection->state = MHD_CONNECTION_CLOSED;
    free (header_name);
    free (header_value);
    probe_log ("exit transmit_error_response_len (response NULL): connection=%p", (void *) connection);
    return;
  }
  mhd_assert ((0 == header_name_len) || (NULL != header_name));
  mhd_assert ((NULL == header_name) || (0 != header_name_len));
  mhd_assert ((0 == header_value_len) || (NULL != header_value));
  mhd_assert ((NULL == header_value) || (0 != header_value_len));
  mhd_assert ((NULL == header_name) || (NULL != header_value));
  mhd_assert ((NULL != header_value) || (NULL == header_name));
  if (NULL != header_name)
  {
    probe_log ("adding header: header_name=%p, header_name_len=%zu, header_value=%p, header_value_len=%zu",
               (void *) header_name,
               header_name_len,
               (void *) header_value,
               header_value_len);
    iret = MHD_add_response_entry_no_alloc_ (response,
                                             MHD_HEADER_KIND,
                                             header_name, header_name_len,
                                             header_value, header_value_len);
    probe_log ("returned from MHD_add_response_entry_no_alloc_: iret=%d", (int) iret);
    if (MHD_NO == iret)
    {
      free (header_name);
      free (header_value);
      probe_log ("freed header_name/header_value after MHD_add_response_entry_no_alloc_ failure");
    }
  }
  else
    iret = MHD_YES;

  if (MHD_NO != iret)
  {
    bool before = connection->in_access_handler;

    /* Fake the flag for the internal call */
    connection->in_access_handler = true;
    probe_log ("calling MHD_queue_response: connection=%p, status_code=%u, response=%p, in_access_handler(before)=%d",
               (void *) connection,
               status_code,
               (void *) response,
               (int) before);
    iret = MHD_queue_response (connection,
                               status_code,
                               response);
    probe_log ("returned from MHD_queue_response: iret=%d, connection->rp.response=%p",
               (int) iret,
               (void *) connection->rp.response);
    connection->in_access_handler = before;
  }
  MHD_destroy_response (response);
  probe_log ("destroyed response object: response was %p", (void *) response);
  if (MHD_NO == iret)
  {
    /* can't even send a reply, at least close the connection */
    CONNECTION_CLOSE_ERROR (connection,
                            _ ("Closing connection " \
                               "(failed to queue error response)."));
    probe_log ("exit transmit_error_response_len (queue failed): connection=%p, iret=%d", (void *) connection, (int) iret);
    return;
  }
  mhd_assert (NULL != connection->rp.response);
  /* Do not reuse this connection. */
  connection->keepalive = MHD_CONN_MUST_CLOSE;
  if (MHD_NO == build_header_response (connection))
  {
    /* No memory. Release everything. */
    probe_log ("build_header_response failed first time: connection=%p", (void *) connection);
    connection->rq.version = NULL;
    connection->rq.method = NULL;
    connection->rq.url = NULL;
    connection->rq.url_len = 0;
    connection->rq.headers_received = NULL;
    connection->rq.headers_received_tail = NULL;
    connection->write_buffer = NULL;
    connection->write_buffer_size = 0;
    connection->write_buffer_send_offset = 0;
    connection->write_buffer_append_offset = 0;
    connection->read_buffer
      = MHD_pool_reset (connection->pool,
                        NULL,
                        0,
                        0);
    connection->read_buffer_size = 0;

    /* Retry with empty buffer */
    if (MHD_NO == build_header_response (connection))
    {
      CONNECTION_CLOSE_ERROR (connection,
                              _ ("Closing connection " \
                                 "(failed to create error response header)."));
      probe_log ("exit transmit_error_response_len (build_header_response failed twice): connection=%p", (void *) connection);
      return;
    }
  }
  connection->state = MHD_CONNECTION_HEADERS_SENDING;
  probe_log ("exit transmit_error_response_len (success): connection=%p, new_state=%d", (void *) connection, connection->state);
}
