#include <stdio.h>
#include <stdarg.h>
#include <stdint.h>
#include <string.h>

/* Probe logging helper that appends to /tmp/logs/34.txt. */
static void lfs_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/34.txt", "a");
    if (!f) {
        return;
    }
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fprintf(f, "\n");
    fclose(f);
}

static int lfs_bd_cmp(lfs_t *lfs,
        const lfs_cache_t *pcache, lfs_cache_t *rcache, lfs_size_t hint,
        lfs_block_t block, lfs_off_t off,
        const void *buffer, lfs_size_t size) {
    const uint8_t *data = buffer;
    lfs_size_t diff = 0;

    /* Log initial invocation parameters. Cast to long long / unsigned long long
       to avoid depending on exact typedefs for the lfs types. */
    lfs_log("lfs_bd_cmp start: block=%llu off=%lld size=%llu hint=%lld",
            (unsigned long long)block,
            (long long)off,
            (unsigned long long)size,
            (long long)hint);

    for (lfs_off_t i = 0; i < size; i += diff) {
        uint8_t dat[8];

        lfs_log("loop_entry: i=%lld diff_before=%llu",
                (long long)i, (unsigned long long)diff);

        diff = lfs_min(size-i, sizeof(dat));
        lfs_log("diff_assigned: diff=%llu (size-i=%llu, sizeof(dat)=%zu)",
                (unsigned long long)diff,
                (unsigned long long)(size - i),
                sizeof(dat));

        int err = lfs_bd_read(lfs,
                pcache, rcache, hint-i,
                block, off+i, &dat, diff);
        lfs_log("lfs_bd_read returned err=%d for i=%lld diff=%llu",
                err, (long long)i, (unsigned long long)diff);
        if (err) {
            lfs_log("returning err=%d at i=%lld", err, (long long)i);
            return err;
        }

        int res = memcmp(dat, data + i, diff);
        lfs_log("memcmp returned res=%d for i=%lld diff=%llu",
                res, (long long)i, (unsigned long long)diff);
        if (res) {
            int ret = res < 0 ? LFS_CMP_LT : LFS_CMP_GT;
            lfs_log("returning cmp result=%d (res=%d)", ret, res);
            return ret;
        }

        /* Log the value that will be used by the iteration expression
           (i += diff). This demonstrates that 'diff' is assigned inside
           the loop body and then used by the loop increment expression. */
        lfs_log("about_to_increment: i=%lld will be incremented by diff=%llu",
                (long long)i, (unsigned long long)diff);
    }

    lfs_log("lfs_bd_cmp end: returning LFS_CMP_EQ");
    return LFS_CMP_EQ;
}
