#include <stdio.h>
#include <stdarg.h>
#include <stdbool.h>

static void lfs_debug_log(const char *fmt, ...) {
    FILE *f = fopen("/tmp/logs/54.txt", "a");
    if (!f) {
        return;
    }
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fputc('\n', f);
    fclose(f);
}

