#include <stdio.h>
#include <stdarg.h>

/* Probe logger used to capture control flow and state for testing.
   Appends human-readable logs to /tmp/logs/254.txt. */
static void lfs_log_probe(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/254.txt", "a");
    if (!f) {
        return;
    }

    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);

    fputs("\n", f);
    fclose(f);
}

static int lfs_dir_find_match(void *data,
        lfs_tag_t tag, const void *buffer) {
    struct lfs_dir_find_match *name = data;
    lfs_t *lfs = name->lfs;
    const struct lfs_diskoff *disk = buffer;

    /* Log entry and key input state */
    lfs_log_probe("ENTER lfs_dir_find_match: name=%p, name->size=%llu, tag_size=%llu, disk=%p, disk->block=%llu, disk->off=%llu",
            (void *)name,
            (unsigned long long)name->size,
            (unsigned long long)lfs_tag_size(tag),
            (void *)disk,
            (unsigned long long)disk->block,
            (unsigned long long)disk->off);

    /* compare with disk */
    lfs_size_t diff = lfs_min(name->size, lfs_tag_size(tag));
    lfs_log_probe("Computed diff = %llu (min of name->size=%llu and tag_size=%llu)",
            (unsigned long long)diff,
            (unsigned long long)name->size,
            (unsigned long long)lfs_tag_size(tag));

    int res = lfs_bd_cmp(lfs,
            NULL, &lfs->rcache, diff,
            disk->block, disk->off, name->name, diff);
    lfs_log_probe("lfs_bd_cmp returned res = %d", res);
    if (res != LFS_CMP_EQ) {
        lfs_log_probe("EXIT lfs_dir_find_match: early return res = %d (not equal)", res);
        return res;
    }

    /* only equal if our size is still the same */
    lfs_log_probe("Checking size equality: name->size=%llu, tag_size=%llu",
            (unsigned long long)name->size,
            (unsigned long long)lfs_tag_size(tag));
    if (name->size != lfs_tag_size(tag)) {
        int out = (name->size < lfs_tag_size(tag)) ? LFS_CMP_LT : LFS_CMP_GT;
        lfs_log_probe("EXIT lfs_dir_find_match: size mismatch -> returning %d", out);
        return out;
    }

    /* found a match! */
    lfs_log_probe("EXIT lfs_dir_find_match: match found -> LFS_CMP_EQ");
    return LFS_CMP_EQ;
}
