suspicious_frags:
if (dir->id == dir->m.count) {
    if (!dir->m.split) {
        return false;
    }

    int err = lfs_dir_fetch(lfs, &dir->m, dir->m.tail);
    if (err) {
        return err;
    }

    dir->id = 0;
}

// special offset for '.' and '..'
if (dir->pos == 0) {
    info->type = LFS_TYPE_DIR;
    strcpy(info->name, ".");
    dir->pos += 1;
    return true;
} else if (dir->pos == 1) {
    info->type = LFS_TYPE_DIR;
    strcpy(info->name, "..");
    dir->pos += 1;
    return true;
}

int err = lfs_dir_getinfo(lfs, &dir->m, dir->id, info);
if (err && err != LFS_ERR_NOENT) {
    return err;
}
