#include <stdio.h>
#include <stdint.h>
#include <sys/stat.h>
#include <errno.h>

static void lfs_log_probe(const char *msg) {
    FILE *f = NULL;
    /* ensure directory exists (ignore errors) */
    (void)mkdir("/tmp/logs", 0777);
    f = fopen("/tmp/logs/367.txt", "a");
    if (!f) {
        return;
    }
    fprintf(f, "%s\n", msg);
    fclose(f);
}

static void lfs_log_probe_fmt(const char *fmt, void *p1, long v2, long v3, long v4) {
    FILE *f = NULL;
    (void)mkdir("/tmp/logs", 0777);
    f = fopen("/tmp/logs/367.txt", "a");
    if (!f) {
        return;
    }
    fprintf(f, fmt, p1, v2, v3, v4);
    fprintf(f, "\n");
    fclose(f);
}

static int lfs_dir_relocatingcommit(lfs_t *lfs, lfs_mdir_t *dir,
        const lfs_block_t pair[2],
        const struct lfs_mattr *attrs, int attrcount,
        lfs_mdir_t *pdir) {
    int state = 0;

    /* Probe: entering function and basic state */
    lfs_log_probe("ENTER lfs_dir_relocatingcommit");
    lfs_log_probe_fmt("  lfs=%p dir=%p off=%ld etag=%ld count=%ld attrcount=%ld",
            (void*)lfs, (long)dir->off, (long)dir->etag, (long)dir->count, (long)attrcount);

    /* calculate changes to the directory */
    bool hasdelete = false;
    for (int i = 0; i < attrcount; i++) {
        /* log each attribute tag encountered */
        lfs_log_probe_fmt("  attrs[%d].tag=%p (index=%d)", (void*)(uintptr_t)attrs[i].tag, (long)i, 0, 0);

        if (lfs_tag_type3(attrs[i].tag) == LFS_TYPE_CREATE) {
            dir->count += 1;
        } else if (lfs_tag_type3(attrs[i].tag) == LFS_TYPE_DELETE) {
            LFS_ASSERT(dir->count > 0);
            dir->count -= 1;
            hasdelete = true;
        } else if (lfs_tag_type1(attrs[i].tag) == LFS_TYPE_TAIL) {
            dir->tail[0] = ((lfs_block_t*)attrs[i].buffer)[0];
            dir->tail[1] = ((lfs_block_t*)attrs[i].buffer)[1];
            dir->split = (lfs_tag_chunk(attrs[i].tag) & 1);
            lfs_pair_fromle32(dir->tail);
        }

        /* log updated count and hasdelete after processing this attr */
        lfs_log_probe_fmt("    after attr %d: dir->count=%ld hasdelete=%ld",
                (void*)(uintptr_t)0, (long)dir->count, (long)hasdelete, 0);
    }

    /* should we actually drop the directory block? */
    if (hasdelete && dir->count == 0) {
        LFS_ASSERT(pdir);
        int err = lfs_fs_pred(lfs, dir->pair, pdir);
        lfs_log_probe_fmt("  lfs_fs_pred returned %ld (err) for dir=%p",
                (void*)(uintptr_t)err, (long)dir, 0, 0);
        if (err && err != LFS_ERR_NOENT) {
            return err;
        }

        if (err != LFS_ERR_NOENT && pdir->split) {
            state = LFS_OK_DROPPED;
            goto fixmlist;
        }
    }

    if (dir->erased) {
        /* try to commit */
        struct lfs_commit commit = {
            .block = dir->pair[0],
            .off = dir->off,
            .ptag = dir->etag,
            .crc = 0xffffffff,

            .begin = dir->off,
            .end = (lfs->cfg->metadata_max ?
                lfs->cfg->metadata_max : lfs->cfg->block_size) - 8,
        };

        /* traverse attrs that need to be written out */
        lfs_pair_tole32(dir->tail);

        /* Probe: before calling lfs_dir_traverse - log parameters that could influence "diff" behavior */
        lfs_log_probe("  ABOUT TO CALL lfs_dir_traverse");
        lfs_log_probe_fmt("    dir=%p off=%ld etag=%ld attrcount=%ld",
                (void*)dir, (long)dir->off, (long)dir->etag, (long)attrcount);
        lfs_log_probe_fmt("    dir->tail[0]=%ld tail[1]=%ld split=%ld",
                (void*)(uintptr_t)dir->tail[0], (long)dir->tail[1], (long)dir->split, 0);

        /* Call and probe result to capture control flow around the call where violation was reported */
        int err = lfs_dir_traverse(lfs,
                dir, dir->off, dir->etag, attrs, attrcount,
                0, 0, 0, 0, 0,
                lfs_dir_commit_commit, &(struct lfs_dir_commit_commit){
                    lfs, &commit});

        /* Probe: after call, log return and relevant state */
        lfs_log_probe_fmt("  lfs_dir_traverse returned %ld for dir=%p",
                (void*)(uintptr_t)err, (long)dir, 0, 0);
        lfs_log_probe_fmt("    post-traverse dir->off=%ld dir->etag=%ld dir->count=%ld",
                (void*)(uintptr_t)0, (long)dir->off, (long)dir->etag, (long)dir->count);

        lfs_pair_fromle32(dir->tail);
        if (err) {
            if (err == LFS_ERR_NOSPC || err == LFS_ERR_CORRUPT) {
                goto compact;
            }
            return err;
        }

        /* commit any global diffs if we have any */
        lfs_gstate_t delta = {0};
        lfs_gstate_xor(&delta, &lfs->gstate);
        lfs_gstate_xor(&delta, &lfs->gdisk);
        lfs_gstate_xor(&delta, &lfs->gdelta);
        delta.tag &= ~LFS_MKTAG(0, 0, 0x3ff);
        if (!lfs_gstate_iszero(&delta)) {
            err = lfs_dir_getgstate(lfs, dir, &delta);
            if (err) {
                return err;
            }

            lfs_gstate_tole32(&delta);
            err = lfs_dir_commitattr(lfs, &commit,
                    LFS_MKTAG(LFS_TYPE_MOVESTATE, 0x3ff,
                        sizeof(delta)), &delta);
            if (err) {
                if (err == LFS_ERR_NOSPC || err == LFS_ERR_CORRUPT) {
                    goto compact;
                }
                return err;
            }
        }

        /* finalize commit with the crc */
        err = lfs_dir_commitcrc(lfs, &commit);
        if (err) {
            if (err == LFS_ERR_NOSPC || err == LFS_ERR_CORRUPT) {
                goto compact;
            }
            return err;
        }

        /* successful commit, update dir */
        LFS_ASSERT(commit.off % lfs->cfg->prog_size == 0);
        dir->off = commit.off;
        dir->etag = commit.ptag;
        /* and update gstate */
        lfs->gdisk = lfs->gstate;
        lfs->gdelta = (lfs_gstate_t){0};

        goto fixmlist;
    }

compact:
    /* fall back to compaction */
    lfs_cache_drop(lfs, &lfs->pcache);

    state = lfs_dir_splittingcompact(lfs, dir, attrs, attrcount,
            dir, 0, dir->count);
    if (state < 0) {
        return state;
    }

    goto fixmlist;

fixmlist:;
    /* this complicated bit of logic is for fixing up any active
     * metadata-pairs that we may have affected
     *
     * note we have to make two passes since the mdir passed to
     * lfs_dir_commit could also be in this list, and even then
     * we need to copy the pair so they don't get clobbered if we refetch
     * our mdir.
     */
    lfs_block_t oldpair[2] = {pair[0], pair[1]};
    for (struct lfs_mlist *d = lfs->mlist; d; d = d->next) {
        if (lfs_pair_cmp(d->m.pair, oldpair) == 0) {
            d->m = *dir;
            if (d->m.pair != pair) {
                for (int i = 0; i < attrcount; i++) {
                    if (lfs_tag_type3(attrs[i].tag) == LFS_TYPE_DELETE &&
                            d->id == lfs_tag_id(attrs[i].tag) &&
                            d->type != LFS_TYPE_DIR) {
                        d->m.pair[0] = LFS_BLOCK_NULL;
                        d->m.pair[1] = LFS_BLOCK_NULL;
                    } else if (lfs_tag_type3(attrs[i].tag) == LFS_TYPE_DELETE &&
                            d->id > lfs_tag_id(attrs[i].tag)) {
                        d->id -= 1;
                        if (d->type == LFS_TYPE_DIR) {
                            ((lfs_dir_t*)d)->pos -= 1;
                        }
                    } else if (lfs_tag_type3(attrs[i].tag) == LFS_TYPE_CREATE &&
                            d->id >= lfs_tag_id(attrs[i].tag)) {
                        d->id += 1;
                        if (d->type == LFS_TYPE_DIR) {
                            ((lfs_dir_t*)d)->pos += 1;
                        }
                    }
                }
            }

            while (d->id >= d->m.count && d->m.split) {
                /* we split and id is on tail now */
                if (lfs_pair_cmp(d->m.tail, lfs->root) != 0) {
                    d->id -= d->m.count;
                }
                int err = lfs_dir_fetch(lfs, &d->m, d->m.tail);
                if (err) {
                    return err;
                }
            }
        }
    }

    /* Probe: exiting function and final state */
    lfs_log_probe_fmt("EXIT lfs_dir_relocatingcommit state=%ld dir=%p dir->count=%ld",
            (void*)(uintptr_t)state, (long)dir, (long)dir->count, 0);

    return state;
}
