#include <stdio.h>
#include <stdarg.h>

static void probe_logf(const char *fmt, ...) {
    FILE *f = fopen("/tmp/logs/367.txt", "a");
    if (!f) {
        return;
    }
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fprintf(f, "\n");
    fclose(f);
}

static int lfs_dir_relocatingcommit(lfs_t *lfs, lfs_mdir_t *dir,
        const lfs_block_t pair[2],
        const struct lfs_mattr *attrs, int attrcount,
        lfs_mdir_t *pdir) {
    int state = 0;

    probe_logf("ENTER lfs_dir_relocatingcommit: pair=(%llu,%llu) dir->off=%llu dir->etag=%llu dir->erased=%d attrcount=%d",
            (unsigned long long)pair[0], (unsigned long long)pair[1],
            (unsigned long long)dir->off, (unsigned long long)dir->etag, (int)dir->erased, attrcount);

    // calculate changes to the directory
    bool hasdelete = false;
    for (int i = 0; i < attrcount; i++) {
        int t3 = lfs_tag_type3(attrs[i].tag);
        int t1 = lfs_tag_type1(attrs[i].tag);
        probe_logf("attr[%d]: tag=0x%08llx type3=%d type1=%d chunk=%d id=%d", i,
                (unsigned long long)attrs[i].tag, t3, t1, lfs_tag_chunk(attrs[i].tag), lfs_tag_id(attrs[i].tag));

        if (t3 == LFS_TYPE_CREATE) {
            dir->count += 1;
            probe_logf("attr[%d] CREATE: new dir->count=%d", i, dir->count);
        } else if (t3 == LFS_TYPE_DELETE) {
            LFS_ASSERT(dir->count > 0);
            dir->count -= 1;
            hasdelete = true;
            probe_logf("attr[%d] DELETE: new dir->count=%d hasdelete=1", i, dir->count);
        } else if (t1 == LFS_TYPE_TAIL) {
            dir->tail[0] = ((lfs_block_t*)attrs[i].buffer)[0];
            dir->tail[1] = ((lfs_block_t*)attrs[i].buffer)[1];
            dir->split = (lfs_tag_chunk(attrs[i].tag) & 1);
            lfs_pair_fromle32(dir->tail);
            probe_logf("attr[%d] TAIL: tail=(%llu,%llu) split=%d", i,
                    (unsigned long long)dir->tail[0], (unsigned long long)dir->tail[1], (int)dir->split);
        }
    }

    probe_logf("after attrs: dir->count=%d hasdelete=%d", dir->count, hasdelete ? 1 : 0);

    // should we actually drop the directory block?
    if (hasdelete && dir->count == 0) {
        LFS_ASSERT(pdir);
        probe_logf("attempting fs_pred for dir->pair=(%llu,%llu)",
                (unsigned long long)dir->pair[0], (unsigned long long)dir->pair[1]);
        int err = lfs_fs_pred(lfs, dir->pair, pdir);
        probe_logf("lfs_fs_pred returned %d pdir->split=%d", err, (int)pdir->split);
        if (err && err != LFS_ERR_NOENT) {
            probe_logf("early return from fs_pred with err=%d", err);
            return err;
        }

        if (err != LFS_ERR_NOENT && pdir->split) {
            state = LFS_OK_DROPPED;
            probe_logf("state set to LFS_OK_DROPPED, jumping to fixmlist");
            goto fixmlist;
        }
    }

    if (dir->erased) {
        // try to commit
        struct lfs_commit commit = {
            .block = dir->pair[0],
            .off = dir->off,
            .ptag = dir->etag,
            .crc = 0xffffffff,

            .begin = dir->off,
            .end = (lfs->cfg->metadata_max ?
                lfs->cfg->metadata_max : lfs->cfg->block_size) - 8,
        };

        probe_logf("commit prepared: block=%llu off=%llu ptag=%llu crc=0x%08llx begin=%llu end=%llu",
                (unsigned long long)commit.block, (unsigned long long)commit.off,
                (unsigned long long)commit.ptag, (unsigned long long)commit.crc,
                (unsigned long long)commit.begin, (unsigned long long)commit.end);

        // traverse attrs that need to be written out
        lfs_pair_tole32(dir->tail);
        int err = lfs_dir_traverse(lfs,
                dir, dir->off, dir->etag, attrs, attrcount,
                0, 0, 0, 0, 0,
                lfs_dir_commit_commit, &(struct lfs_dir_commit_commit){
                    lfs, &commit});
        lfs_pair_fromle32(dir->tail);

        probe_logf("after lfs_dir_traverse returned err=%d commit.ptag=%llu commit.crc=0x%08llx",
                err, (unsigned long long)commit.ptag, (unsigned long long)commit.crc);

        if (err) {
            probe_logf("lfs_dir_traverse signaled err=%d", err);
            if (err == LFS_ERR_NOSPC || err == LFS_ERR_CORRUPT) {
                probe_logf("got NOSPC or CORRUPT, jumping to compact");
                goto compact;
            }
            probe_logf("returning err=%d", err);
            return err;
        }

        // commit any global diffs if we have any
        lfs_gstate_t delta = {0};
        lfs_gstate_xor(&delta, &lfs->gstate);
        lfs_gstate_xor(&delta, &lfs->gdisk);
        lfs_gstate_xor(&delta, &lfs->gdelta);
        delta.tag &= ~LFS_MKTAG(0, 0, 0x3ff);
        if (!lfs_gstate_iszero(&delta)) {
            probe_logf("non-zero delta.tag=0x%08llx, committing gstate", (unsigned long long)delta.tag);
            err = lfs_dir_getgstate(lfs, dir, &delta);
            probe_logf("lfs_dir_getgstate returned %d", err);
            if (err) {
                return err;
            }

            lfs_gstate_tole32(&delta);
            err = lfs_dir_commitattr(lfs, &commit,
                    LFS_MKTAG(LFS_TYPE_MOVESTATE, 0x3ff,
                        sizeof(delta)), &delta);
            probe_logf("lfs_dir_commitattr returned %d", err);
            if (err) {
                if (err == LFS_ERR_NOSPC || err == LFS_ERR_CORRUPT) {
                    probe_logf("commitattr got NOSPC or CORRUPT, jumping to compact");
                    goto compact;
                }
                return err;
            }
        }

        // finalize commit with the crc
        err = lfs_dir_commitcrc(lfs, &commit);
        probe_logf("lfs_dir_commitcrc returned %d", err);
        if (err) {
            if (err == LFS_ERR_NOSPC || err == LFS_ERR_CORRUPT) {
                probe_logf("commitcrc got NOSPC or CORRUPT, jumping to compact");
                goto compact;
            }
            return err;
        }

        // successful commit, update dir
        LFS_ASSERT(commit.off % lfs->cfg->prog_size == 0);
        dir->off = commit.off;
        dir->etag = commit.ptag;
        probe_logf("commit success: updated dir->off=%llu dir->etag=%llu", (unsigned long long)dir->off, (unsigned long long)dir->etag);
        // and update gstate
        lfs->gdisk = lfs->gstate;
        lfs->gdelta = (lfs_gstate_t){0};

        goto fixmlist;
    }

compact:
    // fall back to compaction
    probe_logf("compact: dropping pcache and splitting compact");
    lfs_cache_drop(lfs, &lfs->pcache);

    state = lfs_dir_splittingcompact(lfs, dir, attrs, attrcount,
            dir, 0, dir->count);
    probe_logf("lfs_dir_splittingcompact returned state=%d", state);
    if (state < 0) {
        return state;
    }

    goto fixmlist;

fixmlist:;
    // this complicated bit of logic is for fixing up any active
    // metadata-pairs that we may have affected
    //
    // note we have to make two passes since the mdir passed to
    // lfs_dir_commit could also be in this list, and even then
    // we need to copy the pair so they don't get clobbered if we refetch
    // our mdir.
    lfs_block_t oldpair[2] = {pair[0], pair[1]};
    probe_logf("fixmlist: oldpair=(%llu,%llu)", (unsigned long long)oldpair[0], (unsigned long long)oldpair[1]);
    for (struct lfs_mlist *d = lfs->mlist; d; d = d->next) {
        if (lfs_pair_cmp(d->m.pair, oldpair) == 0) {
            probe_logf("fixmlist: matched mlist id=%d type=%d", d->id, d->type);
            d->m = *dir;
            if (d->m.pair != pair) {
                for (int i = 0; i < attrcount; i++) {
                    if (lfs_tag_type3(attrs[i].tag) == LFS_TYPE_DELETE &&
                            d->id == lfs_tag_id(attrs[i].tag) &&
                            d->type != LFS_TYPE_DIR) {
                        d->m.pair[0] = LFS_BLOCK_NULL;
                        d->m.pair[1] = LFS_BLOCK_NULL;
                        probe_logf("fixmlist: cleared pair for id=%d", d->id);
                    } else if (lfs_tag_type3(attrs[i].tag) == LFS_TYPE_DELETE &&
                            d->id > lfs_tag_id(attrs[i].tag)) {
                        d->id -= 1;
                        if (d->type == LFS_TYPE_DIR) {
                            ((lfs_dir_t*)d)->pos -= 1;
                        }
                        probe_logf("fixmlist: delete adjusted id to %d", d->id);
                    } else if (lfs_tag_type3(attrs[i].tag) == LFS_TYPE_CREATE &&
                            d->id >= lfs_tag_id(attrs[i].tag)) {
                        d->id += 1;
                        if (d->type == LFS_TYPE_DIR) {
                            ((lfs_dir_t*)d)->pos += 1;
                        }
                        probe_logf("fixmlist: create adjusted id to %d", d->id);
                    }
                }
            }

            while (d->id >= d->m.count && d->m.split) {
                // we split and id is on tail now
                if (lfs_pair_cmp(d->m.tail, lfs->root) != 0) {
                    d->id -= d->m.count;
                }
                probe_logf("fixmlist: fetching tail pair=(%llu,%llu) for id=%d", (unsigned long long)d->m.tail[0], (unsigned long long)d->m.tail[1], d->id);
                int err = lfs_dir_fetch(lfs, &d->m, d->m.tail);
                probe_logf("lfs_dir_fetch returned %d", err);
                if (err) {
                    return err;
                }
            }
        }
    }

    probe_logf("EXIT lfs_dir_relocatingcommit: state=%d", state);
    return state;
}
