#include <stdio.h>
#include <stdarg.h>
#include <stdint.h>
#include <stdbool.h>

/* Probe helper: append formatted log lines to /tmp/logs/367.txt */
static void lfs_probe_logf(const char *fmt, ...) {
    FILE *f = fopen("/tmp/logs/367.txt", "a");
    if (!f) {
        return;
    }
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fprintf(f, "\n");
    fclose(f);
}

/* Probe: dump a summary of attrs (tags) up to a limit */
static void lfs_probe_log_attrs(const struct lfs_mattr *attrs, int attrcount) {
    int limit = attrcount < 8 ? attrcount : 8;
    for (int i = 0; i < limit; i++) {
        /* attrs[i].tag is used by original code, so print it */
        lfs_probe_logf("  attr[%d].tag=0x%08x", i, (unsigned)attrs[i].tag);
    }
    if (attrcount > limit) {
        lfs_probe_logf("  ... (%d more attrs)", attrcount - limit);
    }
}

static int lfs_dir_relocatingcommit(lfs_t *lfs, lfs_mdir_t *dir,
        const lfs_block_t pair[2],
        const struct lfs_mattr *attrs, int attrcount,
        lfs_mdir_t *pdir) {
    int state = 0;

    lfs_probe_logf("ENTER lfs_dir_relocatingcommit: lfs=%p dir=%p pair=[%llu,%llu]",
            (void*)lfs, (void*)dir,
            (unsigned long long)pair[0], (unsigned long long)pair[1]);
    lfs_probe_logf("  dir->off=%llu dir->etag=%u dir->count=%d dir->erased=%d dir->split=%d",
            (unsigned long long)dir->off, (unsigned)dir->etag, (int)dir->count,
            (int)dir->erased, (int)dir->split);
    lfs_probe_logf("  attrcount=%d", attrcount);
    if (attrcount > 0 && attrs != NULL) {
        lfs_probe_log_attrs(attrs, attrcount);
    }

    // calculate changes to the directory
    bool hasdelete = false;
    for (int i = 0; i < attrcount; i++) {
        if (lfs_tag_type3(attrs[i].tag) == LFS_TYPE_CREATE) {
            dir->count += 1;
            lfs_probe_logf("  APPLY CREATE: incremented dir->count -> %d (attr idx=%d, tag=0x%08x)",
                    dir->count, i, (unsigned)attrs[i].tag);
        } else if (lfs_tag_type3(attrs[i].tag) == LFS_TYPE_DELETE) {
            LFS_ASSERT(dir->count > 0);
            dir->count -= 1;
            hasdelete = true;
            lfs_probe_logf("  APPLY DELETE: decremented dir->count -> %d (attr idx=%d, tag=0x%08x)",
                    dir->count, i, (unsigned)attrs[i].tag);
        } else if (lfs_tag_type1(attrs[i].tag) == LFS_TYPE_TAIL) {
            lfs_probe_logf("  APPLY TAIL: old tail=[%llu,%llu] split=%d (attr idx=%d)",
                    (unsigned long long)dir->tail[0], (unsigned long long)dir->tail[1],
                    (int)dir->split, i);
            dir->tail[0] = ((lfs_block_t*)attrs[i].buffer)[0];
            dir->tail[1] = ((lfs_block_t*)attrs[i].buffer)[1];
            dir->split = (lfs_tag_chunk(attrs[i].tag) & 1);
            lfs_pair_fromle32(dir->tail);
            lfs_probe_logf("  APPLY TAIL: new tail=[%llu,%llu] split=%d",
                    (unsigned long long)dir->tail[0], (unsigned long long)dir->tail[1],
                    (int)dir->split);
        }
    }

    // should we actually drop the directory block?
    if (hasdelete && dir->count == 0) {
        lfs_probe_logf("BRANCH: hasdelete && dir->count==0 -> entering drop check");
        LFS_ASSERT(pdir);
        int err = lfs_fs_pred(lfs, dir->pair, pdir);
        lfs_probe_logf("  lfs_fs_pred returned %d pdir=%p pdir->split=%d pdir->count=%d",
                err, (void*)pdir, (int)pdir->split, (int)pdir->count);
        if (err && err != LFS_ERR_NOENT) {
            lfs_probe_logf("EXIT lfs_dir_relocatingcommit early: lfs_fs_pred error=%d", err);
            return err;
        }

        if (err != LFS_ERR_NOENT && pdir->split) {
            state = LFS_OK_DROPPED;
            lfs_probe_logf("  DECISION: mark state=LFS_OK_DROPPED and goto fixmlist");
            goto fixmlist;
        }
    }

    if (dir->erased) {
        lfs_probe_logf("BRANCH: dir->erased -> attempting commit");
        // try to commit
        struct lfs_commit commit = {
            .block = dir->pair[0],
            .off = dir->off,
            .ptag = dir->etag,
            .crc = 0xffffffff,

            .begin = dir->off,
            .end = (lfs->cfg->metadata_max ?
                lfs->cfg->metadata_max : lfs->cfg->block_size) - 8,
        };

        // traverse attrs that need to be written out
        lfs_probe_logf("  before pair_tole32: tail=[%llu,%llu]",
                (unsigned long long)dir->tail[0], (unsigned long long)dir->tail[1]);
        lfs_pair_tole32(dir->tail);
        lfs_probe_logf("  after pair_tole32: tail=[%llu,%llu]",
                (unsigned long long)dir->tail[0], (unsigned long long)dir->tail[1]);

        int err = lfs_dir_traverse(lfs,
                dir, dir->off, dir->etag, attrs, attrcount,
                0, 0, 0, 0, 0,
                lfs_dir_commit_commit, &(struct lfs_dir_commit_commit){
                    lfs, &commit});

        lfs_probe_logf("  lfs_dir_traverse returned err=%d", err);
        lfs_pair_fromle32(dir->tail);
        lfs_probe_logf("  after pair_fromle32: tail=[%llu,%llu]",
                (unsigned long long)dir->tail[0], (unsigned long long)dir->tail[1]);

        if (err) {
            if (err == LFS_ERR_NOSPC || err == LFS_ERR_CORRUPT) {
                lfs_probe_logf("  lfs_dir_traverse indicated NOSPC or CORRUPT -> goto compact");
                goto compact;
            }
            lfs_probe_logf("EXIT lfs_dir_relocatingcommit early: lfs_dir_traverse error=%d", err);
            return err;
        }

        // commit any global diffs if we have any
        lfs_probe_logf("  computing global diffs (gstate/gdisk/gdelta)");
        lfs_gstate_t delta = {0};
        lfs_gstate_xor(&delta, &lfs->gstate);
        lfs_gstate_xor(&delta, &lfs->gdisk);
        lfs_gstate_xor(&delta, &lfs->gdelta);
        delta.tag &= ~LFS_MKTAG(0, 0, 0x3ff);
        if (!lfs_gstate_iszero(&delta)) {
            lfs_probe_logf("  non-zero delta -> committing gstate attr");
            err = lfs_dir_getgstate(lfs, dir, &delta);
            lfs_probe_logf("  lfs_dir_getgstate returned %d", err);
            if (err) {
                lfs_probe_logf("EXIT lfs_dir_relocatingcommit early: lfs_dir_getgstate error=%d", err);
                return err;
            }

            lfs_gstate_tole32(&delta);
            err = lfs_dir_commitattr(lfs, &commit,
                    LFS_MKTAG(LFS_TYPE_MOVESTATE, 0x3ff,
                        sizeof(delta)), &delta);
            lfs_probe_logf("  lfs_dir_commitattr returned %d", err);
            if (err) {
                if (err == LFS_ERR_NOSPC || err == LFS_ERR_CORRUPT) {
                    lfs_probe_logf("  lfs_dir_commitattr indicated NOSPC or CORRUPT -> goto compact");
                    goto compact;
                }
                lfs_probe_logf("EXIT lfs_dir_relocatingcommit early: lfs_dir_commitattr error=%d", err);
                return err;
            }
        }

        // finalize commit with the crc
        err = lfs_dir_commitcrc(lfs, &commit);
        lfs_probe_logf("  lfs_dir_commitcrc returned %d", err);
        if (err) {
            if (err == LFS_ERR_NOSPC || err == LFS_ERR_CORRUPT) {
                lfs_probe_logf("  lfs_dir_commitcrc indicated NOSPC or CORRUPT -> goto compact");
                goto compact;
            }
            lfs_probe_logf("EXIT lfs_dir_relocatingcommit early: lfs_dir_commitcrc error=%d", err);
            return err;
        }

        // successful commit, update dir
        LFS_ASSERT(commit.off % lfs->cfg->prog_size == 0);
        dir->off = commit.off;
        dir->etag = commit.ptag;
        // and update gstate
        lfs->gdisk = lfs->gstate;
        lfs->gdelta = (lfs_gstate_t){0};

        lfs_probe_logf("  commit successful: dir->off=%llu dir->etag=%u",
                (unsigned long long)dir->off, (unsigned)dir->etag);

        goto fixmlist;
    }

compact:
    // fall back to compaction
    lfs_probe_logf("LABEL compact: falling back to compaction");
    lfs_cache_drop(lfs, &lfs->pcache);

    state = lfs_dir_splittingcompact(lfs, dir, attrs, attrcount,
            dir, 0, dir->count);
    lfs_probe_logf("  lfs_dir_splittingcompact returned %d", state);
    if (state < 0) {
        lfs_probe_logf("EXIT lfs_dir_relocatingcommit early: splittingcompact error=%d", state);
        return state;
    }

    goto fixmlist;

fixmlist:;
    lfs_probe_logf("LABEL fixmlist: fixing up metadata list (mlist)");
    // this complicated bit of logic is for fixing up any active
    // metadata-pairs that we may have affected
    //
    // note we have to make two passes since the mdir passed to
    // lfs_dir_commit could also be in this list, and even then
    // we need to copy the pair so they don't get clobbered if we refetch
    // our mdir.
    lfs_block_t oldpair[2] = {pair[0], pair[1]};
    lfs_probe_logf("  oldpair=[%llu,%llu]", (unsigned long long)oldpair[0], (unsigned long long)oldpair[1]);
    for (struct lfs_mlist *d = lfs->mlist; d; d = d->next) {
        if (lfs_pair_cmp(d->m.pair, oldpair) == 0) {
            lfs_probe_logf("  mlist entry match: d=%p id=%d type=%d", (void*)d, d->id, d->type);
            d->m = *dir;
            if (d->m.pair != pair) {
                for (int i = 0; i < attrcount; i++) {
                    if (lfs_tag_type3(attrs[i].tag) == LFS_TYPE_DELETE &&
                            d->id == lfs_tag_id(attrs[i].tag) &&
                            d->type != LFS_TYPE_DIR) {
                        d->m.pair[0] = LFS_BLOCK_NULL;
                        d->m.pair[1] = LFS_BLOCK_NULL;
                        lfs_probe_logf("    applied delete to mlist entry: set pair to NULL (d=%p)", (void*)d);
                    } else if (lfs_tag_type3(attrs[i].tag) == LFS_TYPE_DELETE &&
                            d->id > lfs_tag_id(attrs[i].tag)) {
                        d->id -= 1;
                        if (d->type == LFS_TYPE_DIR) {
                            ((lfs_dir_t*)d)->pos -= 1;
                        }
                        lfs_probe_logf("    adjusted mlist id down: d=%p new id=%d", (void*)d, d->id);
                    } else if (lfs_tag_type3(attrs[i].tag) == LFS_TYPE_CREATE &&
                            d->id >= lfs_tag_id(attrs[i].tag)) {
                        d->id += 1;
                        if (d->type == LFS_TYPE_DIR) {
                            ((lfs_dir_t*)d)->pos += 1;
                        }
                        lfs_probe_logf("    adjusted mlist id up: d=%p new id=%d", (void*)d, d->id);
                    }
                }
            }

            while (d->id >= d->m.count && d->m.split) {
                // we split and id is on tail now
                lfs_probe_logf("    while: d->id >= d->m.count && d->m.split -> d->id=%d d->m.count=%d split=%d",
                        d->id, d->m.count, (int)d->m.split);
                if (lfs_pair_cmp(d->m.tail, lfs->root) != 0) {
                    d->id -= d->m.count;
                }
                int err = lfs_dir_fetch(lfs, &d->m, d->m.tail);
                lfs_probe_logf("    lfs_dir_fetch returned %d for d=%p", err, (void*)d);
                if (err) {
                    lfs_probe_logf("EXIT lfs_dir_relocatingcommit early: lfs_dir_fetch error=%d", err);
                    return err;
                }
            }
        }
    }

    lfs_probe_logf("EXIT lfs_dir_relocatingcommit normal: returning state=%d", state);
    return state;
}
