#include <stdio.h>
#include <stdarg.h>

/* Probe logging helpers. These do not modify program logic, only append
   diagnostics to /tmp/logs/367.txt so the control flow and state leading
   up to the compaction/relocation can be inspected. */
static void lfs_probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/367.txt", "a");
    if (!f) {
        return;
    }
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fprintf(f, "\n");
    fflush(f);
    fclose(f);
}

/* Generic printer for a pair of blocks. Cast to unsigned long long for
   portability when printing integer-like block types. */
static void lfs_probe_log_pairs(const char *prefix, const lfs_block_t pair[2])
{
    lfs_probe_log("%s pair[0]=%llu pair[1]=%llu", prefix,
            (unsigned long long)pair[0], (unsigned long long)pair[1]);
}

static int lfs_dir_relocatingcommit(lfs_t *lfs, lfs_mdir_t *dir,
        const lfs_block_t pair[2],
        const struct lfs_mattr *attrs, int attrcount,
        lfs_mdir_t *pdir) {
    int state = 0;

    lfs_probe_log("ENTER lfs_dir_relocatingcommit: dir=%p pdir=%p attrcount=%d", (void*)dir, (void*)pdir, attrcount);
    lfs_probe_log_pairs("ENTRY pair", pair);
    lfs_probe_log("ENTRY dir->count=%d dir->off=%llu dir->etag=%u dir->erased=%d dir->split=%d", dir->count,
            (unsigned long long)dir->off, (unsigned)dir->etag, (int)dir->erased, (int)dir->split);

    // calculate changes to the directory
    bool hasdelete = false;
    for (int i = 0; i < attrcount; i++) {
        if (lfs_tag_type3(attrs[i].tag) == LFS_TYPE_CREATE) {
            dir->count += 1;
        } else if (lfs_tag_type3(attrs[i].tag) == LFS_TYPE_DELETE) {
            LFS_ASSERT(dir->count > 0);
            dir->count -= 1;
            hasdelete = true;
        } else if (lfs_tag_type1(attrs[i].tag) == LFS_TYPE_TAIL) {
            dir->tail[0] = ((lfs_block_t*)attrs[i].buffer)[0];
            dir->tail[1] = ((lfs_block_t*)attrs[i].buffer)[1];
            dir->split = (lfs_tag_chunk(attrs[i].tag) & 1);
            lfs_pair_fromle32(dir->tail);
        }
    }

    lfs_probe_log("AFTER attrs: dir->count=%d hasdelete=%d dir->split=%d tail[0]=%llu tail[1]=%llu", dir->count, (int)hasdelete,
            (int)dir->split, (unsigned long long)dir->tail[0], (unsigned long long)dir->tail[1]);

    // should we actually drop the directory block?
    if (hasdelete && dir->count == 0) {
        LFS_ASSERT(pdir);
        lfs_probe_log("CHECK drop: calling lfs_fs_pred with dir->pair[0]=%llu dir->pair[1]=%llu", (unsigned long long)dir->pair[0], (unsigned long long)dir->pair[1]);
        int err = lfs_fs_pred(lfs, dir->pair, pdir);
        lfs_probe_log("lfs_fs_pred returned %d pdir=%p pdir->split=%d", err, (void*)pdir, (pdir ? (int)pdir->split : -1));
        if (err && err != LFS_ERR_NOENT) {
            lfs_probe_log("EXIT lfs_dir_relocatingcommit early: lfs_fs_pred error=%d", err);
            return err;
        }

        if (err != LFS_ERR_NOENT && pdir->split) {
            state = LFS_OK_DROPPED;
            lfs_probe_log("Directory dropped: setting state=LFS_OK_DROPPED and jumping to fixmlist");
            goto fixmlist;
        }
    }

    if (dir->erased) {
        // try to commit
        struct lfs_commit commit = {
            .block = dir->pair[0],
            .off = dir->off,
            .ptag = dir->etag,
            .crc = 0xffffffff,

            .begin = dir->off,
            .end = (lfs->cfg->metadata_max ?
                lfs->cfg->metadata_max : lfs->cfg->block_size) - 8,
        };

        lfs_probe_log("ATTEMPT commit: commit.block=%llu commit.off=%llu commit.ptag=%u commit.end=%llu", (unsigned long long)commit.block,
                (unsigned long long)commit.off, (unsigned)commit.ptag, (unsigned long long)commit.end);

        // traverse attrs that need to be written out
        lfs_pair_tole32(dir->tail);
        int err = lfs_dir_traverse(lfs,
                dir, dir->off, dir->etag, attrs, attrcount,
                0, 0, 0, 0, 0,
                lfs_dir_commit_commit, &(struct lfs_dir_commit_commit){
                    lfs, &commit});
        lfs_pair_fromle32(dir->tail);
        lfs_probe_log("lfs_dir_traverse returned %d", err);
        if (err) {
            if (err == LFS_ERR_NOSPC || err == LFS_ERR_CORRUPT) {
                lfs_probe_log("lfs_dir_traverse suggests compact (err=%d)", err);
                goto compact;
            }
            lfs_probe_log("EXIT lfs_dir_relocatingcommit early: traverse error=%d", err);
            return err;
        }

        // commit any global diffs if we have any
        lfs_gstate_t delta = {0};
        lfs_gstate_xor(&delta, &lfs->gstate);
        lfs_gstate_xor(&delta, &lfs->gdisk);
        lfs_gstate_xor(&delta, &lfs->gdelta);
        delta.tag &= ~LFS_MKTAG(0, 0, 0x3ff);
        if (!lfs_gstate_iszero(&delta)) {
            err = lfs_dir_getgstate(lfs, dir, &delta);
            lfs_probe_log("lfs_dir_getgstate returned %d", err);
            if (err) {
                lfs_probe_log("EXIT lfs_dir_relocatingcommit early: getgstate error=%d", err);
                return err;
            }

            lfs_gstate_tole32(&delta);
            err = lfs_dir_commitattr(lfs, &commit,
                    LFS_MKTAG(LFS_TYPE_MOVESTATE, 0x3ff,
                        sizeof(delta)), &delta);
            lfs_probe_log("lfs_dir_commitattr returned %d", err);
            if (err) {
                if (err == LFS_ERR_NOSPC || err == LFS_ERR_CORRUPT) {
                    lfs_probe_log("lfs_dir_commitattr suggests compact (err=%d)", err);
                    goto compact;
                }
                lfs_probe_log("EXIT lfs_dir_relocatingcommit early: commitattr error=%d", err);
                return err;
            }
        }

        // finalize commit with the crc
        err = lfs_dir_commitcrc(lfs, &commit);
        lfs_probe_log("lfs_dir_commitcrc returned %d", err);
        if (err) {
            if (err == LFS_ERR_NOSPC || err == LFS_ERR_CORRUPT) {
                lfs_probe_log("lfs_dir_commitcrc suggests compact (err=%d)", err);
                goto compact;
            }
            lfs_probe_log("EXIT lfs_dir_relocatingcommit early: commitcrc error=%d", err);
            return err;
        }

        // successful commit, update dir
        LFS_ASSERT(commit.off % lfs->cfg->prog_size == 0);
        dir->off = commit.off;
        dir->etag = commit.ptag;
        // and update gstate
        lfs->gdisk = lfs->gstate;
        lfs->gdelta = (lfs_gstate_t){0};

        lfs_probe_log("COMMIT success: updated dir->off=%llu dir->etag=%u", (unsigned long long)dir->off, (unsigned)dir->etag);

        goto fixmlist;
    }

compact:
    // fall back to compaction
    lfs_probe_log("ENTER compact fallback: dropping pcache and calling lfs_dir_splittingcompact");
    lfs_cache_drop(lfs, &lfs->pcache);

    lfs_probe_log("BEFORE lfs_dir_splittingcompact: dir->count=%d attrcount=%d", dir->count, attrcount);
    state = lfs_dir_splittingcompact(lfs, dir, attrs, attrcount,
            dir, 0, dir->count);
    lfs_probe_log("AFTER lfs_dir_splittingcompact: returned state=%d", state);
    if (state < 0) {
        lfs_probe_log("EXIT lfs_dir_relocatingcommit early: splittingcompact error=%d", state);
        return state;
    }

    goto fixmlist;

fixmlist:;
    lfs_probe_log("ENTER fixmlist: state=%d attrcount=%d", state, attrcount);
    lfs_probe_log_pairs("oldpair", oldpair);
    // this complicated bit of logic is for fixing up any active
    // metadata-pairs that we may have affected
    //
    // note we have to make two passes since the mdir passed to
    // lfs_dir_commit could also be in this list, and even then
    // we need to copy the pair so they don't get clobbered if we refetch
    // our mdir.
    lfs_block_t oldpair[2] = {pair[0], pair[1]};
    for (struct lfs_mlist *d = lfs->mlist; d; d = d->next) {
        if (lfs_pair_cmp(d->m.pair, oldpair) == 0) {
            lfs_probe_log("fixmlist: matched mlist node %p (id=%d type=%d)", (void*)d, d->id, d->type);
            d->m = *dir;
            if (d->m.pair != pair) {
                for (int i = 0; i < attrcount; i++) {
                    if (lfs_tag_type3(attrs[i].tag) == LFS_TYPE_DELETE &&
                            d->id == lfs_tag_id(attrs[i].tag) &&
                            d->type != LFS_TYPE_DIR) {
                        d->m.pair[0] = LFS_BLOCK_NULL;
                        d->m.pair[1] = LFS_BLOCK_NULL;
                        lfs_probe_log("fixmlist: cleared pair for node %p due to DELETE (id match)", (void*)d);
                    } else if (lfs_tag_type3(attrs[i].tag) == LFS_TYPE_DELETE &&
                            d->id > lfs_tag_id(attrs[i].tag)) {
                        d->id -= 1;
                        if (d->type == LFS_TYPE_DIR) {
                            ((lfs_dir_t*)d)->pos -= 1;
                        }
                        lfs_probe_log("fixmlist: decremented id for node %p now id=%d", (void*)d, d->id);
                    } else if (lfs_tag_type3(attrs[i].tag) == LFS_TYPE_CREATE &&
                            d->id >= lfs_tag_id(attrs[i].tag)) {
                        d->id += 1;
                        if (d->type == LFS_TYPE_DIR) {
                            ((lfs_dir_t*)d)->pos += 1;
                        }
                        lfs_probe_log("fixmlist: incremented id for node %p now id=%d", (void*)d, d->id);
                    }
                }
            }

            while (d->id >= d->m.count && d->m.split) {
                // we split and id is on tail now
                if (lfs_pair_cmp(d->m.tail, lfs->root) != 0) {
                    d->id -= d->m.count;
                }
                lfs_probe_log("fixmlist: fetching tail for node %p with new id=%d", (void*)d, d->id);
                int err = lfs_dir_fetch(lfs, &d->m, d->m.tail);
                lfs_probe_log("lfs_dir_fetch returned %d for node %p", err, (void*)d);
                if (err) {
                    lfs_probe_log("EXIT lfs_dir_relocatingcommit early: lfs_dir_fetch error=%d", err);
                    return err;
                }
            }
        }
    }

    lfs_probe_log("EXIT lfs_dir_relocatingcommit: returning state=%d", state);
    return state;
}
