#include <stdio.h>
#include <stdlib.h>

/* Probe logging helper for lfs_dir_seek_ to capture control flow and state.
   Appends logs to /tmp/logs/407.txt. Keeps original logic intact. */
static void lfs_dir_seek_log(const char *stage, lfs_t *lfs, lfs_dir_t *dir, lfs_off_t off, int err) {
    FILE *f = fopen("/tmp/logs/407.txt", "a");
    if (!f) {
        return;
    }
    /* Print a compact snapshot. Casts used to reasonably format unknown typedefs. */
    fprintf(f, "%s: off=%lld, dir->pos=%lld, dir->id=%lld, m.count=%lld, m.split=%d, err=%d\n",
            stage,
            (long long)off,
            (long long)dir->pos,
            (long long)dir->id,
            (long long)dir->m.count,
            (int)dir->m.split,
            err);
    fclose(f);
}

static int lfs_dir_seek_(lfs_t *lfs, lfs_dir_t *dir, lfs_off_t off) {
    /* Entry log */
    lfs_dir_seek_log("enter", lfs, dir, off, 0);

    // simply walk from head dir
    int err = lfs_dir_rewind_(lfs, dir);
    lfs_dir_seek_log("after_rewind", lfs, dir, off, err);
    if (err) {
        /* Log before returning due to rewind error */
        lfs_dir_seek_log("return_rewind_err", lfs, dir, off, err);
        return err;
    }

    // first two for ./..
    dir->pos = lfs_min(2, off);
    lfs_dir_seek_log("after_pos_set", lfs, dir, off, 0);

    off -= dir->pos;
    /* Log the modification to the parameter 'off' which is the focus of the violation scenario. */
    lfs_dir_seek_log("after_off_minus_pos", lfs, dir, off, 0);

    // skip superblock entry
    dir->id = (off > 0 && lfs_pair_cmp(dir->head, lfs->root) == 0);
    lfs_dir_seek_log("after_skip_superblock", lfs, dir, off, 0);

    while (off > 0) {
        lfs_dir_seek_log("loop_top", lfs, dir, off, 0);
        if (dir->id == dir->m.count) {
            if (!dir->m.split) {
                /* Log the invalid condition before returning */
                lfs_dir_seek_log("return_inval_before", lfs, dir, off, LFS_ERR_INVAL);
                return LFS_ERR_INVAL;
            }

            err = lfs_dir_fetch(lfs, &dir->m, dir->m.tail);
            lfs_dir_seek_log("after_fetch", lfs, dir, off, err);
            if (err) {
                /* Log the error returned by fetch */
                lfs_dir_seek_log("return_fetch_err", lfs, dir, off, err);
                return err;
            }

            dir->id = 0;
            lfs_dir_seek_log("after_reset_id", lfs, dir, off, 0);
        }

        int diff = lfs_min(dir->m.count - dir->id, off);
        dir->id += diff;
        dir->pos += diff;
        off -= diff;

        lfs_dir_seek_log("after_iteration_updates", lfs, dir, off, 0);
    }

    /* Final success log */
    lfs_dir_seek_log("return_success", lfs, dir, off, 0);
    return 0;
}
