#include <stdio.h>
#include <stdarg.h>
#include <inttypes.h>
#include <stdint.h>

/* Probe logging helper: append textual logs to /tmp/logs/426.txt */
static void lfs_log_probe(const char *fmt, ...) {
    FILE *f = fopen("/tmp/logs/426.txt", "a");
    if (!f) {
        return;
    }

    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);

    fprintf(f, "\n");
    fclose(f);
}

static int lfs_dir_splittingcompact(lfs_t *lfs, lfs_mdir_t *dir,
        const struct lfs_mattr *attrs, int attrcount,
        lfs_mdir_t *source, uint16_t begin, uint16_t end) {
    /* Log entry and key parameters */
    lfs_log_probe("ENTER lfs_dir_splittingcompact: lfs=%p dir=%p attrs=%p attrcount=%d source=%p begin=%u end=%u",
            (void *)lfs, (void *)dir, (const void *)attrs, attrcount,
            (void *)source, (unsigned)begin, (unsigned)end);

    int outer_loop = 0;
    while (true) {
        outer_loop++;
        lfs_log_probe("loop %d: begin=%u end=%u", outer_loop, (unsigned)begin, (unsigned)end);

        /* find size of first split, we do this by halving the split until
         * the metadata is guaranteed to fit
         *
         * Note that this isn't a true binary search, we never increase the
         * split size. This may result in poorly distributed metadata but isn't
         * worth the extra code size or performance hit to fix.
         */
        lfs_size_t split = begin;
        while (end - split > 1) {
            lfs_size_t size = 0;
            lfs_log_probe("about to call lfs_dir_traverse: source=%p split=%llu end=%u attrs=%p attrcount=%d",
                    (void *)source, (unsigned long long)split, (unsigned)end,
                    (const void *)attrs, attrcount);

            int err = lfs_dir_traverse(lfs,
                    source, 0, 0xffffffff, attrs, attrcount,
                    LFS_MKTAG(0x400, 0x3ff, 0),
                    LFS_MKTAG(LFS_TYPE_NAME, 0, 0),
                    split, end, -split,
                    lfs_dir_commit_size, &size);

            lfs_log_probe("returned from lfs_dir_traverse: err=%d size=%llu",
                    err, (unsigned long long)size);

            if (err) {
                lfs_log_probe("lfs_dir_traverse error -> returning %d", err);
                return err;
            }

            /*
             * space is complicated, we need room for:
             *
             * - tail:         4+2*4 = 12 bytes
             * - gstate:       4+3*4 = 16 bytes
             * - move delete:  4     = 4 bytes
             * - crc:          4+4   = 8 bytes
             *                 total = 40 bytes
             *
             * And we cap at half a block to avoid degenerate cases with
             * nearly-full metadata blocks.
             *
             */
            lfs_size_t metadata_max = (lfs->cfg->metadata_max)
                    ? lfs->cfg->metadata_max
                    : lfs->cfg->block_size;
            if (end - split < 0xff
                    && size <= lfs_min(
                        metadata_max - 40,
                        lfs_alignup(
                            metadata_max/2,
                            lfs->cfg->prog_size))) {
                lfs_log_probe("split %llu is small enough (size=%llu)",
                        (unsigned long long)split, (unsigned long long)size);
                break;
            }

            split = split + ((end - split) / 2);
            lfs_log_probe("increasing split -> %llu", (unsigned long long)split);
        }

        if (split == begin) {
            /* no split needed */
            lfs_log_probe("no split needed (split==begin=%u)", (unsigned)begin);
            break;
        }

        /* split into two metadata pairs and continue */
        lfs_log_probe("calling lfs_dir_split: dir=%p attrs=%p attrcount=%d source=%p split=%llu end=%u",
                (void *)dir, (const void *)attrs, attrcount, (void *)source,
                (unsigned long long)split, (unsigned)end);
        int err = lfs_dir_split(lfs, dir, attrs, attrcount,
                source, split, end);
        lfs_log_probe("returned from lfs_dir_split: err=%d", err);
        if (err && err != LFS_ERR_NOSPC) {
            lfs_log_probe("lfs_dir_split fatal error -> returning %d", err);
            return err;
        }

        if (err) {
            /* we can't allocate a new block, try to compact with degraded
             * performance
             */
            LFS_WARN("Unable to split {0x%"PRIx32", 0x%"PRIx32"}",
                    dir->pair[0], dir->pair[1]);
            lfs_log_probe("lfs_dir_split returned NOSPC -> break and compact later");
            break;
        } else {
            end = split;
            lfs_log_probe("split succeeded, new end=%u", (unsigned)end);
        }
    }

    if (lfs_dir_needsrelocation(lfs, dir)
            && lfs_pair_cmp(dir->pair, (const lfs_block_t[2]){0, 1}) == 0) {
        /* oh no! we're writing too much to the superblock,
         * should we expand?
         */
        lfs_ssize_t size = lfs_fs_size_(lfs);
        if (size < 0) {
            lfs_log_probe("lfs_fs_size_ returned error %lld -> returning", (long long)size);
            return (int)size;
        }

        lfs_log_probe("superblock relocation check: fs_size=%lld block_count=%u",
                (long long)size, (unsigned)lfs->block_count);

        /* littlefs cannot reclaim expanded superblocks, so expand cautiously
         *
         * if our filesystem is more than ~88% full, don't expand, this is
         * somewhat arbitrary
         */
        if (lfs->block_count - size > lfs->block_count/8) {
            LFS_DEBUG("Expanding superblock at rev %"PRIu32, dir->rev);
            lfs_log_probe("attempting lfs_dir_split to expand superblock");
            int err = lfs_dir_split(lfs, dir, attrs, attrcount,
                    source, begin, end);
            lfs_log_probe("returned from lfs_dir_split (expand attempt): err=%d", err);
            if (err && err != LFS_ERR_NOSPC) {
                lfs_log_probe("expand split fatal error -> returning %d", err);
                return err;
            }

            if (err) {
                /* welp, we tried, if we ran out of space there's not much
                 * we can do, we'll error later if we've become frozen
                 */
                LFS_WARN("Unable to expand superblock");
                lfs_log_probe("expand attempt returned NOSPC");
            } else {
                /* duplicate the superblock entry into the new superblock */
                end = 1;
                lfs_log_probe("expand succeeded, setting end=1");
            }
        }
    }

    lfs_log_probe("calling lfs_dir_compact: dir=%p attrs=%p attrcount=%d source=%p begin=%u end=%u",
            (void *)dir, (const void *)attrs, attrcount, (void *)source,
            (unsigned)begin, (unsigned)end);
    int ret = lfs_dir_compact(lfs, dir, attrs, attrcount, source, begin, end);
    lfs_log_probe("returned from lfs_dir_compact: ret=%d -> EXIT lfs_dir_splittingcompact", ret);
    return ret;
}
