#include <stdio.h>
#include <stdarg.h>
#include <stddef.h>

/* Probe logging helper. Appends human-readable logs to /tmp/logs/427.txt */
static void lfs_log_probe(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/427.txt", "a");
    if (!f) {
        return;
    }

    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);

    fprintf(f, "\n");
    fclose(f);
}

static int lfs_dir_splittingcompact(lfs_t *lfs, lfs_mdir_t *dir,
        const struct lfs_mattr *attrs, int attrcount,
        lfs_mdir_t *source, uint16_t begin, uint16_t end) {
    /* Log entry state */
    lfs_log_probe("ENT: lfs_dir_splittingcompact(dir=%p, source=%p, begin=%u, end=%u, attrcount=%d, attrs=%p)",
            (void *)dir, (void *)source, (unsigned)begin, (unsigned)end, attrcount, (const void *)attrs);

    while (true) {
        // find size of first split, we do this by halving the split until
        // the metadata is guaranteed to fit
        //
        // Note that this isn't a true binary search, we never increase the
        // split size. This may result in poorly distributed metadata but isn't
        // worth the extra code size or performance hit to fix.
        lfs_size_t split = begin;
        while (end - split > 1) {
            lfs_size_t size = 0;
            lfs_log_probe("CALL: lfs_dir_traverse(split=%zu, end=%u, attrcount=%d, attrs=%p)",
                    (size_t)split, (unsigned)end, attrcount, (const void *)attrs);

            int err = lfs_dir_traverse(lfs,
                    source, 0, 0xffffffff, attrs, attrcount,
                    LFS_MKTAG(0x400, 0x3ff, 0),
                    LFS_MKTAG(LFS_TYPE_NAME, 0, 0),
                    split, end, -split,
                    lfs_dir_commit_size, &size);

            lfs_log_probe("RET: lfs_dir_traverse -> err=%d, size=%zu", err, (size_t)size);

            if (err) {
                lfs_log_probe("ERR: lfs_dir_traverse returned %d; aborting split calculation", err);
                return err;
            }

            // space is complicated, we need room for:
            //
            // - tail:         4+2*4 = 12 bytes
            // - gstate:       4+3*4 = 16 bytes
            // - move delete:  4     = 4 bytes
            // - crc:          4+4   = 8 bytes
            //                 total = 40 bytes
            //
            // And we cap at half a block to avoid degenerate cases with
            // nearly-full metadata blocks.
            //
            lfs_size_t metadata_max = (lfs->cfg->metadata_max)
                    ? lfs->cfg->metadata_max
                    : lfs->cfg->block_size;
            if (end - split < 0xff
                    && size <= lfs_min(
                        metadata_max - 40,
                        lfs_alignup(
                            metadata_max/2,
                            lfs->cfg->prog_size))) {
                lfs_log_probe("INFO: Found acceptable split: split=%zu, end=%u, size=%zu, metadata_max=%zu",
                        (size_t)split, (unsigned)end, (size_t)size, (size_t)metadata_max);
                break;
            }

            lfs_log_probe("INFO: Increasing split: before=%zu", (size_t)split);
            split = split + ((end - split) / 2);
            lfs_log_probe("INFO: Increasing split: after=%zu", (size_t)split);
        }

        if (split == begin) {
            // no split needed
            lfs_log_probe("INFO: No split needed (split==begin==%u)", (unsigned)begin);
            break;
        }

        // split into two metadata pairs and continue
        lfs_log_probe("CALL: lfs_dir_split(dir=%p, source=%p, split=%zu, end=%u, attrcount=%d)",
                (void *)dir, (void *)source, (size_t)split, (unsigned)end, attrcount);

        int err = lfs_dir_split(lfs, dir, attrs, attrcount,
                source, split, end);
        lfs_log_probe("RET: lfs_dir_split -> err=%d", err);

        if (err && err != LFS_ERR_NOSPC) {
            lfs_log_probe("ERR: lfs_dir_split returned %d (fatal)", err);
            return err;
        }

        if (err) {
            // we can't allocate a new block, try to compact with degraded
            // performance
            LFS_WARN("Unable to split {0x%" PRIx32 ", 0x%" PRIx32 "}",
                    dir->pair[0], dir->pair[1]);
            lfs_log_probe("WARN: lfs_dir_split returned LFS_ERR_NOSPC; will try to compact instead");
            break;
        } else {
            lfs_log_probe("INFO: Split successful; updating end: old=%u new=%zu", (unsigned)end, (size_t)split);
            end = split;
        }
    }

    if (lfs_dir_needsrelocation(lfs, dir)
            && lfs_pair_cmp(dir->pair, (const lfs_block_t[2]){0, 1}) == 0) {
        // oh no! we're writing too much to the superblock,
        // should we expand?
        lfs_ssize_t size = lfs_fs_size_(lfs);
        if (size < 0) {
            lfs_log_probe("ERR: lfs_fs_size_ returned %zd", (ssize_t)size);
            return size;
        }

        // littlefs cannot reclaim expanded superblocks, so expand cautiously
        //
        // if our filesystem is more than ~88% full, don't expand, this is
        // somewhat arbitrary
        if (lfs->block_count - size > lfs->block_count/8) {
            LFS_DEBUG("Expanding superblock at rev %" PRIu32, dir->rev);
            lfs_log_probe("INFO: Attempting to expand superblock; dir->rev=%" PRIu32, dir->rev);
            int err = lfs_dir_split(lfs, dir, attrs, attrcount,
                    source, begin, end);
            lfs_log_probe("RET: lfs_dir_split (expand) -> err=%d", err);
            if (err && err != LFS_ERR_NOSPC) {
                lfs_log_probe("ERR: lfs_dir_split (expand) returned %d (fatal)", err);
                return err;
            }

            if (err) {
                // welp, we tried, if we ran out of space there's not much
                // we can do, we'll error later if we've become frozen
                LFS_WARN("Unable to expand superblock");
                lfs_log_probe("WARN: Unable to expand superblock (NOSPC)");
            } else {
                // duplicate the superblock entry into the new superblock
                end = 1;
                lfs_log_probe("INFO: Expanded superblock successfully; set end=1");
            }
        }
    }

    lfs_log_probe("CALL: lfs_dir_compact(dir=%p, source=%p, begin=%u, end=%u, attrcount=%d, attrs=%p)",
            (void *)dir, (void *)source, (unsigned)begin, (unsigned)end, attrcount, (const void *)attrs);
    int res = lfs_dir_compact(lfs, dir, attrs, attrcount, source, begin, end);
    lfs_log_probe("RET: lfs_dir_compact -> %d", res);
    lfs_log_probe("EXIT: lfs_dir_splittingcompact returning %d", res);
    return res;
}
