#include <stdio.h>
#include <stdarg.h>
#include <stdint.h>

/* Probe logging helper: append human-readable traces to /tmp/logs/427.txt */
static void lfs_probe_log(const char *fmt, ...) {
    FILE *f = fopen("/tmp/logs/427.txt", "a");
    if (!f) return;
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fprintf(f, "\n");
    fclose(f);
}

static int lfs_dir_splittingcompact(lfs_t *lfs, lfs_mdir_t *dir,
        const struct lfs_mattr *attrs, int attrcount,
        lfs_mdir_t *source, uint16_t begin, uint16_t end) {
    lfs_probe_log("ENTER lfs_dir_splittingcompact: lfs=%p dir=%p attrs=%p attrcount=%d source=%p begin=%u end=%u",
            (void*)lfs, (void*)dir, (const void*)attrs, attrcount, (void*)source,
            (unsigned)begin, (unsigned)end);

    while (true) {
        // find size of first split, we do this by halving the split until
        // the metadata is guaranteed to fit
        //
        // Note that this isn't a true binary search, we never increase the
        // split size. This may result in poorly distributed metadata but isn't
        // worth the extra code size or performance hit to fix.
        lfs_size_t split = begin;
        while (end - split > 1) {
            lfs_size_t size = 0;

            /* Prepare tags for logging as they are passed into lfs_dir_traverse */
            uint32_t probe_tag1 = LFS_MKTAG(0x400, 0x3ff, 0);
            uint32_t probe_tag2 = LFS_MKTAG(LFS_TYPE_NAME, 0, 0);

            lfs_probe_log("CALL lfs_dir_traverse: source=%p off=%d ptag=0x%08x attrs=%p attrcount=%d tag1=0x%08x tag2=0x%08x split=%llu end=%u diff=%lld",
                    (void*)source, 0, 0xffffffffu, (const void*)attrs, attrcount,
                    (unsigned)probe_tag1, (unsigned)probe_tag2,
                    (unsigned long long)split, (unsigned)end, (long long)(- (int)split));

            int err = lfs_dir_traverse(lfs,
                    source, 0, 0xffffffff, attrs, attrcount,
                    LFS_MKTAG(0x400, 0x3ff, 0),
                    LFS_MKTAG(LFS_TYPE_NAME, 0, 0),
                    split, end, -split,
                    lfs_dir_commit_size, &size);

            lfs_probe_log("RETURN lfs_dir_traverse: err=%d size=%llu (after call)", err, (unsigned long long)size);

            if (err) {
                lfs_probe_log("lfs_dir_traverse returned error %d, returning from lfs_dir_splittingcompact", err);
                return err;
            }

            // space is complicated, we need room for:
            //
            // - tail:         4+2*4 = 12 bytes
            // - gstate:       4+3*4 = 16 bytes
            // - move delete:  4     = 4 bytes
            // - crc:          4+4   = 8 bytes
            //                 total = 40 bytes
            //
            // And we cap at half a block to avoid degenerate cases with
            // nearly-full metadata blocks.
            //
            lfs_size_t metadata_max = (lfs->cfg->metadata_max)
                    ? lfs->cfg->metadata_max
                    : lfs->cfg->block_size;
            if (end - split < 0xff
                    && size <= lfs_min(
                        metadata_max - 40,
                        lfs_alignup(
                            metadata_max/2,
                            lfs->cfg->prog_size))) {
                lfs_probe_log("split %llu is acceptable (size=%llu)", (unsigned long long)split, (unsigned long long)size);
                break;
            }

            lfs_probe_log("split %llu too small/large, updating split (end=%u)", (unsigned long long)split, (unsigned)end);
            split = split + ((end - split) / 2);
        }

        if (split == begin) {
            // no split needed
            lfs_probe_log("No split needed (split == begin == %u). Exiting loop.", (unsigned)begin);
            break;
        }

        // split into two metadata pairs and continue
        lfs_probe_log("Attempting lfs_dir_split: dir=%p attrs=%p attrcount=%d source=%p split=%llu end=%u",
                (void*)dir, (const void*)attrs, attrcount, (void*)source, (unsigned long long)split, (unsigned)end);

        int err = lfs_dir_split(lfs, dir, attrs, attrcount,
                source, split, end);
        if (err && err != LFS_ERR_NOSPC) {
            lfs_probe_log("lfs_dir_split returned error %d (not NOSPC), returning", err);
            return err;
        }

        if (err) {
            // we can't allocate a new block, try to compact with degraded
            // performance
            LFS_WARN("Unable to split {0x%"PRIx32", 0x%"PRIx32"}",
                    dir->pair[0], dir->pair[1]);
            lfs_probe_log("lfs_dir_split returned NOSPC; will try to compact instead. dir->pair=[%u,%u]",
                    (unsigned)dir->pair[0], (unsigned)dir->pair[1]);
            break;
        } else {
            lfs_probe_log("lfs_dir_split succeeded; updating end from %u to %llu", (unsigned)end, (unsigned long long)split);
            end = split;
        }
    }

    if (lfs_dir_needsrelocation(lfs, dir)
            && lfs_pair_cmp(dir->pair, (const lfs_block_t[2]){0, 1}) == 0) {
        // oh no! we're writing too much to the superblock,
        // should we expand?
        lfs_ssize_t size = lfs_fs_size_(lfs);
        if (size < 0) {
            lfs_probe_log("lfs_fs_size_ returned error %lld, returning", (long long)size);
            return size;
        }

        // littlefs cannot reclaim expanded superblocks, so expand cautiously
        //
        // if our filesystem is more than ~88% full, don't expand, this is
        // somewhat arbitrary
        if (lfs->block_count - size > lfs->block_count/8) {
            LFS_DEBUG("Expanding superblock at rev %"PRIu32, dir->rev);
            lfs_probe_log("Attempting superblock expansion: dir->rev=%u", (unsigned)dir->rev);
            int err = lfs_dir_split(lfs, dir, attrs, attrcount,
                    source, begin, end);
            if (err && err != LFS_ERR_NOSPC) {
                lfs_probe_log("lfs_dir_split (expand) returned error %d, returning", err);
                return err;
            }

            if (err) {
                // welp, we tried, if we ran out of space there's not much
                // we can do, we'll error later if we've become frozen
                LFS_WARN("Unable to expand superblock");
                lfs_probe_log("lfs_dir_split (expand) returned NOSPC; continuing without expansion");
            } else {
                // duplicate the superblock entry into the new superblock
                end = 1;
                lfs_probe_log("Superblock expansion succeeded; set end=1");
            }
        }
    }

    lfs_probe_log("Calling lfs_dir_compact with dir=%p attrs=%p attrcount=%d source=%p begin=%u end=%u",
            (void*)dir, (const void*)attrs, attrcount, (void*)source,
            (unsigned)begin, (unsigned)end);

    int final_err = lfs_dir_compact(lfs, dir, attrs, attrcount, source, begin, end);
    lfs_probe_log("lfs_dir_compact returned %d; EXIT lfs_dir_splittingcompact", final_err);
    return final_err;
}
