#include <stdio.h>
#include <stdint.h>
#include <inttypes.h>

/* Probe logging helper to record control flow and state to /tmp/logs/427.txt */
static void lfs_log_probe(const char *location,
        const struct lfs_mattr *attrs, int attrcount,
        lfs_mdir_t *source, uint16_t begin, uint16_t end,
        int err, unsigned long long additional) {
    FILE *f = fopen("/tmp/logs/427.txt", "a");
    if (!f) {
        return;
    }
    fprintf(f, "[lfs_dir_splittingcompact] %s\n", location);
    fprintf(f, "  attrs ptr: %p, attrcount: %d\n", (const void*)attrs, attrcount);
    fprintf(f, "  source: %p, begin: %u, end: %u\n", (void*)source, (unsigned)begin, (unsigned)end);
    fprintf(f, "  err: %d, extra: %llu\n", err, additional);
    fflush(f);
    fclose(f);
}

static int lfs_dir_splittingcompact(lfs_t *lfs, lfs_mdir_t *dir,
        const struct lfs_mattr *attrs, int attrcount,
        lfs_mdir_t *source, uint16_t begin, uint16_t end) {
    /* Log entry to function */
    lfs_log_probe("enter", attrs, attrcount, source, begin, end, 0, 0ULL);

    while (true) {
        /* find size of first split, we do this by halving the split until
         * the metadata is guaranteed to fit
         *
         * Note that this isn't a true binary search, we never increase the
         * split size. This may result in poorly distributed metadata but isn't
         * worth the extra code size or performance hit to fix.
         */
        lfs_size_t split = begin;
        while (end - split > 1) {
            lfs_size_t size = 0;
            /* Log before calling lfs_dir_traverse to capture pre-call state */
            lfs_log_probe("before lfs_dir_traverse", attrs, attrcount, source,
                    (uint16_t)split, end, 0, 0ULL);

            int err = lfs_dir_traverse(lfs,
                    source, 0, 0xffffffff, attrs, attrcount,
                    LFS_MKTAG(0x400, 0x3ff, 0),
                    LFS_MKTAG(LFS_TYPE_NAME, 0, 0),
                    split, end, -split,
                    lfs_dir_commit_size, &size);

            /* Log after call to detect if attrs/attrcount were modified */
            lfs_log_probe("after lfs_dir_traverse", attrs, attrcount, source,
                    (uint16_t)split, end, err, (unsigned long long)size);

            if (err) {
                return err;
            }

            /* space is complicated, we need room for:
             *
             * - tail:         4+2*4 = 12 bytes
             * - gstate:       4+3*4 = 16 bytes
             * - move delete:  4     = 4 bytes
             * - crc:          4+4   = 8 bytes
             *                 total = 40 bytes
             *
             * And we cap at half a block to avoid degenerate cases with
             * nearly-full metadata blocks.
             *
             */
            lfs_size_t metadata_max = (lfs->cfg->metadata_max)
                    ? lfs->cfg->metadata_max
                    : lfs->cfg->block_size;
            if (end - split < 0xff
                    && size <= lfs_min(
                        metadata_max - 40,
                        lfs_alignup(
                            metadata_max/2,
                            lfs->cfg->prog_size))) {
                break;
            }

            split = split + ((end - split) / 2);
        }

        if (split == begin) {
            /* no split needed */
            lfs_log_probe("no split needed (split == begin)", attrs, attrcount,
                    source, begin, end, 0, 0ULL);
            break;
        }

        /* split into two metadata pairs and continue */
        /* Log before lfs_dir_split to capture pre-call state */
        lfs_log_probe("before lfs_dir_split", attrs, attrcount, source,
                begin, end, 0, (unsigned long long)split);

        int err = lfs_dir_split(lfs, dir, attrs, attrcount,
                source, split, end);

        /* Log after lfs_dir_split to capture post-call state */
        lfs_log_probe("after lfs_dir_split", attrs, attrcount, source,
                begin, end, err, (unsigned long long)split);

        if (err && err != LFS_ERR_NOSPC) {
            return err;
        }

        if (err) {
            /* we can't allocate a new block, try to compact with degraded
             * performance
             */
            LFS_WARN("Unable to split {0x%"PRIx32", 0x%"PRIx32"}",
                    dir->pair[0], dir->pair[1]);
            lfs_log_probe("split failed (NOSPC)", attrs, attrcount, source,
                    begin, end, err, 0ULL);
            break;
        } else {
            end = split;
            lfs_log_probe("split succeeded, updating end=split", attrs, attrcount,
                    source, begin, end, 0, (unsigned long long)split);
        }
    }

    if (lfs_dir_needsrelocation(lfs, dir)
            && lfs_pair_cmp(dir->pair, (const lfs_block_t[2]){0, 1}) == 0) {
        /* oh no! we're writing too much to the superblock,
         * should we expand?
         */
        lfs_ssize_t size = lfs_fs_size_(lfs);
        if (size < 0) {
            return size;
        }

        /* littlefs cannot reclaim expanded superblocks, so expand cautiously
         *
         * if our filesystem is more than ~88% full, don't expand, this is
         * somewhat arbitrary
         */
        if (lfs->block_count - size > lfs->block_count/8) {
            LFS_DEBUG("Expanding superblock at rev %"PRIu32, dir->rev);
            /* Log before attempted expand (lfs_dir_split) */
            lfs_log_probe("before expand lfs_dir_split", attrs, attrcount,
                    source, begin, end, 0, 0ULL);

            int err = lfs_dir_split(lfs, dir, attrs, attrcount,
                    source, begin, end);

            /* Log after attempted expand */
            lfs_log_probe("after expand lfs_dir_split", attrs, attrcount,
                    source, begin, end, err, 0ULL);

            if (err && err != LFS_ERR_NOSPC) {
                return err;
            }

            if (err) {
                /* welp, we tried, if we ran out of space there's not much
                 * we can do, we'll error later if we've become frozen
                 */
                LFS_WARN("Unable to expand superblock");
                lfs_log_probe("expand failed (NOSPC)", attrs, attrcount,
                        source, begin, end, err, 0ULL);
            } else {
                /* duplicate the superblock entry into the new superblock */
                end = 1;
                lfs_log_probe("expand succeeded, set end=1", attrs, attrcount,
                        source, begin, end, 0, 0ULL);
            }
        }
    }

    /* Log before final compact call */
    lfs_log_probe("before lfs_dir_compact", attrs, attrcount, source,
            begin, end, 0, 0ULL);

    int final_err = lfs_dir_compact(lfs, dir, attrs, attrcount, source, begin, end);

    /* Log after final compact call */
    lfs_log_probe("after lfs_dir_compact", attrs, attrcount, source,
            begin, end, final_err, 0ULL);

    return final_err;
}
