#include <stdio.h>
#include <stdarg.h>
#include <time.h>

/*
 * Probe logger that appends human-readable logs to /tmp/logs/462.txt.
 * Keeps minimal footprint and does not change the original logic.
 */
static void lfs_probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/462.txt", "a");
    if (f == NULL) {
        return;
    }

    time_t t = time(NULL);
    if (t != (time_t)-1) {
        fprintf(f, "[%ld] ", (long)t);
    } else {
        fprintf(f, "[time_unknown] ");
    }

    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);

    fprintf(f, "\n");
    fclose(f);
}

int lfs_file_close(lfs_t *lfs, lfs_file_t *file) {
    /* Log entry and input pointers */
    lfs_probe_log("ENTER lfs_file_close: lfs=%p, file=%p", (void*)lfs, (void*)file);

    int err = LFS_LOCK(lfs->cfg);
    /* Record the result of LFS_LOCK so the behaviour (always zero in the reported config)
       can be observed in the log. */
    lfs_probe_log("After LFS_LOCK: err=%d", err);

    if (err) {
        /* Log the taken branch before returning to provide evidence that the branch
           is or is not reachable in the running configuration. */
        lfs_probe_log("LFS_LOCK returned non-zero, returning err=%d", err);
        return err;
    }

    LFS_TRACE("lfs_file_close(%p, %p)", (void*)lfs, (void*)file);
    LFS_ASSERT(lfs_mlist_isopen(lfs->mlist, (struct lfs_mlist*)file));

    lfs_probe_log("Calling lfs_file_close_ with lfs=%p, file=%p", (void*)lfs, (void*)file);
    err = lfs_file_close_(lfs, file);
    lfs_probe_log("lfs_file_close_ returned %d", err);

    LFS_TRACE("lfs_file_close -> %d", err);

    lfs_probe_log("Calling LFS_UNLOCK(lfs->cfg)");
    LFS_UNLOCK(lfs->cfg);

    lfs_probe_log("EXIT lfs_file_close: returning %d", err);
    return err;
}
