#include <stdio.h>

/* Probe function to log calls to lfs_ctz_find and the state around them.
   Logs to /tmp/logs/502.txt so other agents can reproduce the scenario and
   observe that the off pointer given is a temporary whose value is not
   inspected after the call. */
static void lfs_probe_ctz_find_log(const char *phase, lfs_t *lfs, lfs_file_t *file,
        lfs_off_t query_pos, void *block_ptr, lfs_off_t *off_ptr) {
    FILE *f = fopen("/tmp/logs/502.txt", "a");
    if (!f) {
        return;
    }

    /* Safely print relevant state: pointer addresses and integer values.
       Casts to long long / unsigned long long used to avoid format warnings
       for unknown typedef widths. */
    fprintf(f, "%s: file=%p query_pos=%lld block_ptr=%p block_val=%llu off_ptr=%p off_val=%lld flags=0x%X\n",
            phase,
            (void*)file,
            (long long)query_pos,
            block_ptr,
            (unsigned long long)file->block,
            (void*)off_ptr,
            (long long)*off_ptr,
            (unsigned)file->flags);

    fclose(f);
}

static lfs_ssize_t lfs_file_flushedwrite(lfs_t *lfs, lfs_file_t *file,
        const void *buffer, lfs_size_t size) {
    const uint8_t *data = buffer;
    lfs_size_t nsize = size;

    if ((file->flags & LFS_F_INLINE) &&
            lfs_max(file->pos+nsize, file->ctz.size) > lfs->inline_max) {
        // inline file doesn't fit anymore
        int err = lfs_file_outline(lfs, file);
        if (err) {
            file->flags |= LFS_F_ERRED;
            return err;
        }
    }

    while (nsize > 0) {
        // check if we need a new block
        if (!(file->flags & LFS_F_WRITING) ||
                file->off == lfs->cfg->block_size) {
            if (!(file->flags & LFS_F_INLINE)) {
                if (!(file->flags & LFS_F_WRITING) && file->pos > 0) {
                    /* find out which block we're extending from
                       Replace the original compound literal with a local
                       temporary so we can probe its address and value in
                       logs without changing observable behaviour. */
                    lfs_off_t _probe_off = 0;
                    lfs_off_t *probe_off_ptr = &_probe_off;

                    /* Log state before calling lfs_ctz_find */
                    lfs_probe_ctz_find_log("BEFORE lfs_ctz_find",
                            lfs, file, file->pos-1, &file->block, probe_off_ptr);

                    int err = lfs_ctz_find(lfs, NULL, &file->cache,
                            file->ctz.head, file->ctz.size,
                            file->pos-1, &file->block, probe_off_ptr);

                    /* Log state after calling lfs_ctz_find so callers can see
                       whether the callee wrote to the off pointer. Note that
                       this value is not used by the caller after the call in
                       the original code (was passed as a compound literal).
                       The log is evidence for that scenario. */
                    lfs_probe_ctz_find_log("AFTER  lfs_ctz_find",
                            lfs, file, file->pos-1, &file->block, probe_off_ptr);

                    if (err) {
                        file->flags |= LFS_F_ERRED;
                        return err;
                    }

                    // mark cache as dirty since we may have read data into it
                    lfs_cache_zero(lfs, &file->cache);
                }

                // extend file with new blocks
                lfs_alloc_ckpoint(lfs);
                int err = lfs_ctz_extend(lfs, &file->cache, &lfs->rcache,
                        file->block, file->pos,
                        &file->block, &file->off);
                if (err) {
                    file->flags |= LFS_F_ERRED;
                    return err;
                }
            } else {
                file->block = LFS_BLOCK_INLINE;
                file->off = file->pos;
            }

            file->flags |= LFS_F_WRITING;
        }

        // program as much as we can in current block
        lfs_size_t diff = lfs_min(nsize, lfs->cfg->block_size - file->off);
        while (true) {
            int err = lfs_bd_prog(lfs, &file->cache, &lfs->rcache, true,
                    file->block, file->off, data, diff);
            if (err) {
                if (err == LFS_ERR_CORRUPT) {
                    goto relocate;
                }
                file->flags |= LFS_F_ERRED;
                return err;
            }

            break;
relocate:
            err = lfs_file_relocate(lfs, file);
            if (err) {
                file->flags |= LFS_F_ERRED;
                return err;
            }
        }

        file->pos += diff;
        file->off += diff;
        data += diff;
        nsize -= diff;

        lfs_alloc_ckpoint(lfs);
    }

    return size;
}
