#include <stdio.h>
#include <stdarg.h>

static void lfs_probe_log(const char *fmt, ...) {
    FILE *f = fopen("/tmp/logs/539.txt", "a");
    if (!f) return;
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    fprintf(f, "\n");
    va_end(ap);
    fclose(f);
}

static int lfs_file_opencfg_(lfs_t *lfs, lfs_file_t *file,
        const char *path, int flags,
        const struct lfs_file_config *cfg) {
#ifndef LFS_READONLY
    // deorphan if we haven't yet, needed at most once after poweron
    if ((flags & LFS_O_WRONLY) == LFS_O_WRONLY) {
        int err = lfs_fs_forceconsistency(lfs);
        if (err) {
            lfs_probe_log("lfs_file_opencfg_: lfs_fs_forceconsistency returned err=%d", err);
            return err;
        }
    }
#else
    LFS_ASSERT((flags & LFS_O_RDONLY) == LFS_O_RDONLY);
#endif

    lfs_probe_log("ENTER lfs_file_opencfg_: lfs=%p file=%p path=%s flags=0x%x cfg=%p",
            (void*)lfs, (void*)file, (path?path:"(null)"), flags, (void*)cfg);

    // setup simple file details
    int err;
    file->cfg = cfg;
    file->flags = flags;
    file->pos = 0;
    file->off = 0;
    file->cache.buffer = NULL;

    lfs_probe_log("after init: file=%p file->cfg=%p file->cache.buffer=%p",
            (void*)file, (void*)file->cfg, (void*)file->cache.buffer);

    // allocate entry for file if it doesn't exist
    lfs_stag_t tag = lfs_dir_find(lfs, &file->m, &path, &file->id);
    lfs_probe_log("lfs_dir_find returned tag=%d file->id=%u path_now=%s",
            (int)tag, (unsigned)file->id, (path?path:"(null)"));
    if (tag < 0 && !(tag == LFS_ERR_NOENT && lfs_path_islast(path))) {
        err = tag;
        lfs_probe_log("early exit: tag error %d", err);
        goto cleanup;
    }

    // get id, add to list of mdirs to catch update changes
    file->type = LFS_TYPE_REG;
    lfs_mlist_append(lfs, (struct lfs_mlist *)file);

#ifdef LFS_READONLY
    if (tag == LFS_ERR_NOENT) {
        err = LFS_ERR_NOENT;
        goto cleanup;
#else
    if (tag == LFS_ERR_NOENT) {
        if (!(flags & LFS_O_CREAT)) {
            err = LFS_ERR_NOENT;
            goto cleanup;
        }

        // don't allow trailing slashes
        if (lfs_path_isdir(path)) {
            err = LFS_ERR_NOTDIR;
            goto cleanup;
        }

        // check that name fits
        lfs_size_t nlen = lfs_path_namelen(path);
        if (nlen > lfs->name_max) {
            err = LFS_ERR_NAMETOOLONG;
            goto cleanup;
        }

        // get next slot and create entry to remember name
        err = lfs_dir_commit(lfs, &file->m, LFS_MKATTRS(
                {LFS_MKTAG(LFS_TYPE_CREATE, file->id, 0), NULL},
                {LFS_MKTAG(LFS_TYPE_REG, file->id, nlen), path},
                {LFS_MKTAG(LFS_TYPE_INLINESTRUCT, file->id, 0), NULL}));

        // it may happen that the file name doesn't fit in the metadata blocks, e.g., a 256 byte file name will
        // not fit in a 128 byte block.
        err = (err == LFS_ERR_NOSPC) ? LFS_ERR_NAMETOOLONG : err;
        if (err) {
            lfs_probe_log("lfs_dir_commit returned err=%d", err);
            goto cleanup;
        }

        tag = LFS_MKTAG(LFS_TYPE_INLINESTRUCT, 0, 0);
    } else if (flags & LFS_O_EXCL) {
        err = LFS_ERR_EXIST;
        goto cleanup;
#endif
    } else if (lfs_tag_type3(tag) != LFS_TYPE_REG) {
        err = LFS_ERR_ISDIR;
        goto cleanup;
#ifndef LFS_READONLY
    } else if (flags & LFS_O_TRUNC) {
        // truncate if requested
        tag = LFS_MKTAG(LFS_TYPE_INLINESTRUCT, file->id, 0);
        file->flags |= LFS_F_DIRTY;
#endif
    } else {
        // try to load what's on disk, if it's inlined we'll fix it later
        tag = lfs_dir_get(lfs, &file->m, LFS_MKTAG(0x700, 0x3ff, 0),
                LFS_MKTAG(LFS_TYPE_STRUCT, file->id, 8), &file->ctz);
        lfs_probe_log("lfs_dir_get for struct returned tag=%d", (int)tag);
        if (tag < 0) {
            err = tag;
            goto cleanup;
        }
        lfs_ctz_fromle32(&file->ctz);
    }

    // fetch attrs
    lfs_probe_log("fetch attrs: cfg=%p attr_count=%u",
            (void*)file->cfg, (unsigned)(file->cfg?file->cfg->attr_count:0));
    for (unsigned i = 0; i < file->cfg->attr_count; i++) {
        // if opened for read / read-write operations
        if ((file->flags & LFS_O_RDONLY) == LFS_O_RDONLY) {
            lfs_stag_t res = lfs_dir_get(lfs, &file->m,
                    LFS_MKTAG(0x7ff, 0x3ff, 0),
                    LFS_MKTAG(LFS_TYPE_USERATTR + file->cfg->attrs[i].type,
                        file->id, file->cfg->attrs[i].size),
                        file->cfg->attrs[i].buffer);
            lfs_probe_log("lfs_dir_get attr[%u] res=%d type=%u size=%u buffer=%p",
                    i, (int)res, (unsigned)file->cfg->attrs[i].type,
                    (unsigned)file->cfg->attrs[i].size, (void*)file->cfg->attrs[i].buffer);
            if (res < 0 && res != LFS_ERR_NOENT) {
                err = res;
                goto cleanup;
            }
        }

#ifndef LFS_READONLY
        // if opened for write / read-write operations
        if ((file->flags & LFS_O_WRONLY) == LFS_O_WRONLY) {
            if (file->cfg->attrs[i].size > lfs->attr_max) {
                err = LFS_ERR_NOSPC;
                goto cleanup;
            }

            file->flags |= LFS_F_DIRTY;
        }
#endif
    }

    // allocate buffer if needed
    if (file->cfg->buffer) {
        file->cache.buffer = file->cfg->buffer;
        lfs_probe_log("using caller-provided buffer: file->cache.buffer=%p lfs->cfg->cache_size=%u",
                (void*)file->cache.buffer, (unsigned)lfs->cfg->cache_size);
    } else {
        file->cache.buffer = lfs_malloc(lfs->cfg->cache_size);
        lfs_probe_log("allocated buffer: file->cache.buffer=%p size=%u",
                (void*)file->cache.buffer, (unsigned)lfs->cfg->cache_size);
        if (!file->cache.buffer) {
            err = LFS_ERR_NOMEM;
            lfs_probe_log("lfs_malloc failed for size=%u", (unsigned)lfs->cfg->cache_size);
            goto cleanup;
        }
    }

    // zero to avoid information leak
    lfs_probe_log("about to zero cache: cache.buffer=%p cache.size=%u cfg_buffer=%p cfg=%p",
            (void*)file->cache.buffer, (unsigned)lfs->cfg->cache_size,
            (void*)(file->cfg?file->cfg->buffer:NULL), (void*)file->cfg);
    lfs_cache_zero(lfs, &file->cache);

    if (lfs_tag_type3(tag) == LFS_TYPE_INLINESTRUCT) {
        // load inline files
        file->ctz.head = LFS_BLOCK_INLINE;
        file->ctz.size = lfs_tag_size(tag);
        file->flags |= LFS_F_INLINE;
        file->cache.block = file->ctz.head;
        file->cache.off = 0;
        file->cache.size = lfs->cfg->cache_size;

        // don't always read (may be new/trunc file)
        if (file->ctz.size > 0) {
            lfs_stag_t res = lfs_dir_get(lfs, &file->m,
                    LFS_MKTAG(0x700, 0x3ff, 0),
                    LFS_MKTAG(LFS_TYPE_STRUCT, file->id,
                        lfs_min(file->cache.size, 0x3fe)),
                    file->cache.buffer);
            lfs_probe_log("lfs_dir_get inline read res=%d into buffer=%p read_size=%u",
                    (int)res, (void*)file->cache.buffer, (unsigned)lfs_min(file->cache.size, 0x3fe));
            if (res < 0) {
                err = res;
                goto cleanup;
            }
        }
    }

    lfs_probe_log("EXIT lfs_file_opencfg_ success: file=%p cache.buffer=%p cache_size=%u",
            (void*)file, (void*)file->cache.buffer, (unsigned)lfs->cfg->cache_size);

    return 0;

cleanup:
    // clean up lingering resources
#ifndef LFS_READONLY
    file->flags |= LFS_F_ERRED;
#endif
    lfs_probe_log("cleanup lfs_file_opencfg_: err=%d file=%p cache.buffer=%p",
            err, (void*)file, (void*)file->cache.buffer);
    lfs_file_close_(lfs, file);
    return err;
}
