#include <stdio.h>
#include <stdarg.h>
#include <inttypes.h>

/* Minimal probe logger that appends control-flow/state information to a file. */
static void lfs_debug_log(const char *fmt, ...) {
    FILE *f = fopen("/tmp/logs/551.txt", "a");
    if (!f) {
        /* Best-effort logging only; do not change program control flow. */
        return;
    }
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fputc('\n', f);
    fclose(f);
}

lfs_ssize_t lfs_file_read(lfs_t *lfs, lfs_file_t *file,
        void *buffer, lfs_size_t size) {
    /* Preserve original lock behavior; probe the returned value. */
    int err = LFS_LOCK(lfs->cfg);

    /* Log the result of the lock call and input state. */
    lfs_debug_log("[probe] lfs_file_read: entered. lfs=%p, file=%p, buffer=%p, size=%" PRIu32 ", lock_err=%d",
            (void*)lfs, (void*)file, buffer, size, err);

    if (err) {
        /* If this branch is ever taken, log and return the error as before. */
        lfs_debug_log("[probe] lfs_file_read: lock failed -> returning err=%d", err);
        return err;
    }

    lfs_debug_log("[probe] lfs_file_read: lock succeeded (err=%d), continuing", err);

    LFS_TRACE("lfs_file_read(%p, %p, %p, %"PRIu32")",
            (void*)lfs, (void*)file, buffer, size);
    LFS_ASSERT(lfs_mlist_isopen(lfs->mlist, (struct lfs_mlist*)file));

    lfs_debug_log("[probe] lfs_file_read: about to call lfs_file_read_");
    lfs_ssize_t res = lfs_file_read_(lfs, file, buffer, size);

    lfs_debug_log("[probe] lfs_file_read_: returned res=%" PRId32, res);

    LFS_TRACE("lfs_file_read -> %"PRId32, res);
    LFS_UNLOCK(lfs->cfg);

    lfs_debug_log("[probe] lfs_file_read: unlocked and returning res=%" PRId32, res);
    return res;
}
