#include <stdio.h>
#include <stdarg.h>
#include <inttypes.h>

/* Probe logging helper: append textual traces to /tmp/logs/652.txt */
static void lfs_probe_log(const char *fmt, ...)
{
    FILE *fp = fopen("/tmp/logs/652.txt", "a");
    if (!fp) {
        return;
    }
    va_list ap;
    va_start(ap, fmt);
    vfprintf(fp, fmt, ap);
    va_end(ap);
    fprintf(fp, "\n");
    fclose(fp);
}

lfs_soff_t lfs_file_tell(lfs_t *lfs, lfs_file_t *file) {
    int err = LFS_LOCK(lfs->cfg);
    lfs_probe_log("ENTER: lfs_file_tell(lfs=%p, file=%p, cfg=%p) -> LFS_LOCK returned %d",
                  (void*)lfs, (void*)file, (void*)lfs->cfg, err);
    if (err) {
        lfs_probe_log("EXIT EARLY: lfs_file_tell returning err=%d", err);
        return err;
    }
    LFS_TRACE("lfs_file_tell(%p, %p)", (void*)lfs, (void*)file);
    LFS_ASSERT(lfs_mlist_isopen(lfs->mlist, (struct lfs_mlist*)file));

    lfs_probe_log("CALL: lfs_file_tell_ with lfs=%p file=%p", (void*)lfs, (void*)file);
    lfs_soff_t res = lfs_file_tell_(lfs, file);
    lfs_probe_log("RETURNED from lfs_file_tell_: res=%" PRId32, res);

    LFS_TRACE("lfs_file_tell -> %"PRId32, res);

    /* Log immediately before and after the macro invocation so that a trace
       consumer can see the call site of LFS_UNLOCK and verify that the macro
       itself has no observable effect on cfg. */
    lfs_probe_log("BEFORE LFS_UNLOCK: cfg=%p, res=%" PRId32, (void*)lfs->cfg, res);
    LFS_UNLOCK(lfs->cfg);
    lfs_probe_log("AFTER LFS_UNLOCK: cfg=%p, res=%" PRId32, (void*)lfs->cfg, res);

    lfs_probe_log("EXIT: lfs_file_tell returning res=%" PRId32, res);
    return res;
}
