#include <stdio.h>
#include <stdarg.h>
#include <inttypes.h>

/* Probe helper: append a formatted line to the log file. */
static void probe_logf(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/685.txt", "a");
    if (!f) {
        return;
    }
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fprintf(f, "\n");
    fclose(f);
}

lfs_ssize_t lfs_file_write(lfs_t *lfs, lfs_file_t *file,
        const void *buffer, lfs_size_t size) {
    /* Preserve original locking and logic. Insert probes around it. */
    int err = LFS_LOCK(lfs->cfg);
    probe_logf("[probe] LFS_LOCK called for lfs=%p returned err=%d", (void*)lfs, err);

    if (err) {
        probe_logf("[probe] early return from lfs_file_write: err=%d (lfs=%p file=%p size=%" PRIu32 ")",
                err, (void*)lfs, (void*)file, size);
        return err;
    }

    probe_logf("[probe] lfs_file_write proceeding (lfs=%p file=%p buffer=%p size=%" PRIu32 ")",
            (void*)lfs, (void*)file, buffer, size);

    LFS_TRACE("lfs_file_write(%p, %p, %p, %"PRIu32")",
            (void*)lfs, (void*)file, buffer, size);
    LFS_ASSERT(lfs_mlist_isopen(lfs->mlist, (struct lfs_mlist*)file));

    probe_logf("[probe] calling lfs_file_write_(lfs=%p file=%p size=%" PRIu32 ")",
            (void*)lfs, (void*)file, size);
    lfs_ssize_t res = lfs_file_write_(lfs, file, buffer, size);
    probe_logf("[probe] lfs_file_write_ returned res=%" PRId32 ", (lfs=%p file=%p)",
            res, (void*)lfs, (void*)file);

    LFS_TRACE("lfs_file_write -> %"PRId32, res);
    LFS_UNLOCK(lfs->cfg);
    probe_logf("[probe] LFS_UNLOCK called for lfs=%p", (void*)lfs);
    return res;
}
