#include <stdio.h>
#include <stdarg.h>

static void lfs_probe_log(const char *fmt, ...) {
    FILE *f = fopen("/tmp/logs/682.txt", "a");
    if (!f) {
        return;
    }
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fprintf(f, "\n");
    fclose(f);
}

lfs_ssize_t lfs_file_write(lfs_t *lfs, lfs_file_t *file,
        const void *buffer, lfs_size_t size) {
    /* probe: entry */
    lfs_probe_log("ENTER lfs_file_write: lfs=%p file=%p buffer=%p size=%lu",
            (void*)lfs, (void*)file, buffer, (unsigned long)size);

    int err = LFS_LOCK(lfs->cfg);
    /* probe: after lock */
    lfs_probe_log("LFS_LOCK returned err=%d (cfg=%p)", err, (void*)lfs->cfg);

    if (err) {
        /* probe: unreachable branch evidence if taken */
        lfs_probe_log("lfs_file_write: early return due to lock error err=%d", err);
        return err;
    }

    LFS_TRACE("lfs_file_write(%p, %p, %p, %"PRIu32")",
            (void*)lfs, (void*)file, buffer, size);
    LFS_ASSERT(lfs_mlist_isopen(lfs->mlist, (struct lfs_mlist*)file));

    lfs_probe_log("Calling lfs_file_write_ (payload write)");
    lfs_ssize_t res = lfs_file_write_(lfs, file, buffer, size);

    lfs_probe_log("lfs_file_write_: result=%ld", (long)res);

    LFS_TRACE("lfs_file_write -> %"PRId32, res);
    LFS_UNLOCK(lfs->cfg);
    lfs_probe_log("LFS_UNLOCK called (cfg=%p); returning res=%ld", (void*)lfs->cfg, (long)res);
    return res;
}
