#include <stdio.h>
#include <stdarg.h>
#include <inttypes.h>
#include <time.h>

/* Probe logging helper. Appends human-readable logs to /tmp/logs/795.txt. */
static void lfs_log_probe(const char *fmt, ...) {
    FILE *f = fopen("/tmp/logs/795.txt", "a");
    if (!f) {
        return;
    }

    time_t now = time(NULL);
    struct tm tm_now;
#if defined(_MSC_VER)
    localtime_s(&tm_now, &now);
#else
    localtime_r(&now, &tm_now);
#endif
    char timestr[64];
    if (strftime(timestr, sizeof(timestr), "%Y-%m-%d %H:%M:%S", &tm_now) == 0) {
        timestr[0] = '\0';
    }

    fprintf(f, "%s - ", timestr);

    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);

    fprintf(f, "\n");
    fclose(f);
}

int lfs_fs_grow(lfs_t *lfs, lfs_size_t block_count) {
    /* Log entry and parameters */
    lfs_log_probe("ENTER lfs_fs_grow: lfs=%p block_count=%" PRIu32, (void*)lfs, block_count);

    int err = LFS_LOCK(lfs->cfg);

    /* Log the return value of the lock call to capture invariant behaviour */
    lfs_log_probe("After LFS_LOCK: err=%d (expected nonzero on error)", err);

    if (err) {
        /* Log early return due to lock error */
        lfs_log_probe("Early RETURN from lfs_fs_grow due to err=%d", err);
        return err;
    }

    LFS_TRACE("lfs_fs_grow(%p, %"PRIu32")", (void*)lfs, block_count);

    err = lfs_fs_grow_(lfs, block_count);

    /* Log result from internal grow helper */
    lfs_log_probe("After lfs_fs_grow_: err=%d", err);

    LFS_TRACE("lfs_fs_grow -> %d", err);
    LFS_UNLOCK(lfs->cfg);

    /* Log unlock and final return value */
    lfs_log_probe("LFS_UNLOCK called; RETURNing err=%d", err);
    return err;
}
