#include <stdio.h>
#include <time.h>

/* Probe helper: append a small log entry to /tmp/logs/872.txt
   This preserves the original logic but creates observable side-effects
   so other agents can reproduce the execution path and state. */
static void lfs_fs_stat_probe(const char *stage, const void *lfs, const void *fsinfo, int err) {
    FILE *f = fopen("/tmp/logs/872.txt", "a");
    if (!f) {
        return;
    }
    time_t now = time(NULL);
    fprintf(f, "%ld: stage=%s lfs=%p fsinfo=%p err=%d\n", (long)now, stage, lfs, fsinfo, err);
    fclose(f);
}

int lfs_fs_stat(lfs_t *lfs, struct lfs_fsinfo *fsinfo) {
    int err = LFS_LOCK(lfs->cfg);
    lfs_fs_stat_probe("after_lock", (void*)lfs, (void*)fsinfo, err);
    if (err) {
        lfs_fs_stat_probe("return_after_lock", (void*)lfs, (void*)fsinfo, err);
        return err;
    }
    LFS_TRACE("lfs_fs_stat(%p, %p)", (void*)lfs, (void*)fsinfo);
    lfs_fs_stat_probe("before_lfs_fs_stat_", (void*)lfs, (void*)fsinfo, err);

    err = lfs_fs_stat_(lfs, fsinfo);

    lfs_fs_stat_probe("after_lfs_fs_stat_", (void*)lfs, (void*)fsinfo, err);
    LFS_TRACE("lfs_fs_stat -> %d", err);
    lfs_fs_stat_probe("before_unlock", (void*)lfs, (void*)fsinfo, err);
    LFS_UNLOCK(lfs->cfg);
    lfs_fs_stat_probe("after_unlock", (void*)lfs, (void*)fsinfo, err);
    return err;
}
