#include <stdio.h>

/* Probe function to log control flow and state to /tmp/logs/992.txt
   Kept static to avoid external linkage and preserve original logic. */
static void lfs_dbg_log_probe(const char *event, void *lfs_ptr, const char *path, int err) {
    FILE *f = fopen("/tmp/logs/992.txt", "a");
    if (f) {
        fprintf(f, "%s: lfs=%p path=\"%s\" err=%d\n",
                event, lfs_ptr, (path ? path : "(null)"), err);
        fclose(f);
    }
}

int lfs_remove(lfs_t *lfs, const char *path) {
    /* Log state immediately before attempting the lock. */
    lfs_dbg_log_probe("before_LFS_LOCK", (void*)lfs, path, 0);

    int err = LFS_LOCK(lfs->cfg);

    /* Log result of LFS_LOCK. This will show that err is 0 when LFS_LOCK
       expands to ((void)cfg, 0) and thus has no observable effect. */
    lfs_dbg_log_probe("after_LFS_LOCK", (void*)lfs, path, err);

    if (err) {
        lfs_dbg_log_probe("return_due_to_lock_err", (void*)lfs, path, err);
        return err;
    }

    LFS_TRACE("lfs_remove(%p, \"%s\")", (void*)lfs, path);

    lfs_dbg_log_probe("before_lfs_remove_", (void*)lfs, path, 0);
    err = lfs_remove_(lfs, path);
    lfs_dbg_log_probe("after_lfs_remove_", (void*)lfs, path, err);

    LFS_TRACE("lfs_remove -> %d", err);

    lfs_dbg_log_probe("before_LFS_UNLOCK", (void*)lfs, path, err);
    LFS_UNLOCK(lfs->cfg);
    lfs_dbg_log_probe("after_LFS_UNLOCK", (void*)lfs, path, err);

    return err;
}
