#include <stdio.h>
#include <stdarg.h>
#include <sys/stat.h>
#include <inttypes.h>
#include <stdint.h>

static void log_probe(const char *fmt, ...)
{
    /* Try to create the directory if it doesn't exist. Ignore errors. */
    (void)mkdir("/tmp/logs", 0777);

    FILE *f = fopen("/tmp/logs/1012.txt", "a");
    if (!f) {
        return;
    }

    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);

    fprintf(f, "\n");
    fclose(f);
}

int lfs_removeattr(lfs_t *lfs, const char *path, uint8_t type) {
    int err = LFS_LOCK(lfs->cfg);
    if (err) {
        /* Log and preserve original behaviour */
        log_probe("lfs_removeattr: LFS_LOCK failed err=%d, lfs=%p, path=\"%s\", type=%" PRIu8, err, (void*)lfs, path, type);
        return err;
    }
    LFS_TRACE("lfs_removeattr(%p, \"%s\", %"PRIu8")", (void*)lfs, path, type);

    /* Entry probe */
    log_probe("lfs_removeattr: entered lfs=%p, path=\"%s\", type=%" PRIu8, (void*)lfs, path, type);

    err = lfs_removeattr_(lfs, path, type);

    LFS_TRACE("lfs_removeattr -> %d", err);

    /* Log state immediately before unlock */
    void *cfg_before = (void*)lfs->cfg;
    log_probe("lfs_removeattr: after lfs_removeattr_, err=%d", err);
    log_probe("lfs_removeattr: cfg pointer before LFS_UNLOCK = %p", cfg_before);

    /* Call the original unlock (may expand to a no-op) */
    LFS_UNLOCK(lfs->cfg);

    /* Log state immediately after unlock to detect any side-effects */
    void *cfg_after = (void*)lfs->cfg;
    log_probe("lfs_removeattr: cfg pointer after LFS_UNLOCK = %p", cfg_after);
    log_probe("lfs_removeattr: cfg pointer changed = %s", (cfg_before == cfg_after) ? "false" : "true");

    log_probe("lfs_removeattr: returning err=%d", err);
    return err;
}
