#include <stdio.h>
#include <stdarg.h>

/* Probe logging function: append human-readable traces to /tmp/logs/172.txt */
static void osapi_log_msg(const char *fmt, ...)
{
    va_list ap;
    FILE *f = fopen("/tmp/logs/172.txt", "a");
    if (f == NULL)
    {
        return;
    }

    va_start(ap, fmt);
    (void)vfprintf(f, fmt, ap);
    va_end(ap);

    (void)fprintf(f, "\n");
    (void)fclose(f);
}

int32 OS_API_Impl_Init(osal_objtype_t idtype)
{
    int32 return_code;

    osapi_log_msg("OS_API_Impl_Init: entry idtype=%d", (int)idtype);

    return_code = OS_Posix_TableMutex_Init(idtype);
    osapi_log_msg("OS_API_Impl_Init: OS_Posix_TableMutex_Init returned %d", (int)return_code);
    if (return_code != OS_SUCCESS)
    {
        osapi_log_msg("OS_API_Impl_Init: early return due to TableMutex_Init failure (%d)", (int)return_code);
        return return_code;
    }

    switch (idtype)
    {
        case OS_OBJECT_TYPE_OS_TASK:
            osapi_log_msg("OS_API_Impl_Init: case OS_OBJECT_TYPE_OS_TASK selected");
            return_code = OS_Posix_TaskAPI_Impl_Init();
            osapi_log_msg("OS_API_Impl_Init: OS_Posix_TaskAPI_Impl_Init returned %d", (int)return_code);
            break;
        case OS_OBJECT_TYPE_OS_QUEUE:
            osapi_log_msg("OS_API_Impl_Init: case OS_OBJECT_TYPE_OS_QUEUE selected");
            return_code = OS_Posix_QueueAPI_Impl_Init();
            osapi_log_msg("OS_API_Impl_Init: OS_Posix_QueueAPI_Impl_Init returned %d", (int)return_code);
            break;
        case OS_OBJECT_TYPE_OS_BINSEM:
            osapi_log_msg("OS_API_Impl_Init: case OS_OBJECT_TYPE_OS_BINSEM selected");
            return_code = OS_Posix_BinSemAPI_Impl_Init();
            osapi_log_msg("OS_API_Impl_Init: OS_Posix_BinSemAPI_Impl_Init returned %d", (int)return_code);
            break;
        case OS_OBJECT_TYPE_OS_COUNTSEM:
            osapi_log_msg("OS_API_Impl_Init: case OS_OBJECT_TYPE_OS_COUNTSEM selected");
            return_code = OS_Posix_CountSemAPI_Impl_Init();
            osapi_log_msg("OS_API_Impl_Init: OS_Posix_CountSemAPI_Impl_Init returned %d", (int)return_code);
            break;
        case OS_OBJECT_TYPE_OS_MUTEX:
            osapi_log_msg("OS_API_Impl_Init: case OS_OBJECT_TYPE_OS_MUTEX selected");
            return_code = OS_Posix_MutexAPI_Impl_Init();
            osapi_log_msg("OS_API_Impl_Init: OS_Posix_MutexAPI_Impl_Init returned %d", (int)return_code);
            break;
        case OS_OBJECT_TYPE_OS_MODULE:
            osapi_log_msg("OS_API_Impl_Init: case OS_OBJECT_TYPE_OS_MODULE selected");
            return_code = OS_Posix_ModuleAPI_Impl_Init();
            osapi_log_msg("OS_API_Impl_Init: OS_Posix_ModuleAPI_Impl_Init returned %d", (int)return_code);
            break;
        case OS_OBJECT_TYPE_OS_TIMEBASE:
            osapi_log_msg("OS_API_Impl_Init: case OS_OBJECT_TYPE_OS_TIMEBASE selected");
            return_code = OS_Posix_TimeBaseAPI_Impl_Init();
            osapi_log_msg("OS_API_Impl_Init: OS_Posix_TimeBaseAPI_Impl_Init returned %d", (int)return_code);
            break;
        case OS_OBJECT_TYPE_OS_STREAM:
            osapi_log_msg("OS_API_Impl_Init: case OS_OBJECT_TYPE_OS_STREAM selected");
            return_code = OS_Posix_StreamAPI_Impl_Init();
            osapi_log_msg("OS_API_Impl_Init: OS_Posix_StreamAPI_Impl_Init returned %d", (int)return_code);
            break;
        case OS_OBJECT_TYPE_OS_DIR:
            osapi_log_msg("OS_API_Impl_Init: case OS_OBJECT_TYPE_OS_DIR selected");
            return_code = OS_Posix_DirAPI_Impl_Init();
            osapi_log_msg("OS_API_Impl_Init: OS_Posix_DirAPI_Impl_Init returned %d", (int)return_code);
            break;
        case OS_OBJECT_TYPE_OS_FILESYS:
            osapi_log_msg("OS_API_Impl_Init: case OS_OBJECT_TYPE_OS_FILESYS selected");
            return_code = OS_Posix_FileSysAPI_Impl_Init();
            osapi_log_msg("OS_API_Impl_Init: OS_Posix_FileSysAPI_Impl_Init returned %d", (int)return_code);
            break;
        case OS_OBJECT_TYPE_OS_CONDVAR:
            osapi_log_msg("OS_API_Impl_Init: case OS_OBJECT_TYPE_OS_CONDVAR selected");
            return_code = OS_Posix_CondVarAPI_Impl_Init();
            osapi_log_msg("OS_API_Impl_Init: OS_Posix_CondVarAPI_Impl_Init returned %d", (int)return_code);
            break;
        default:
            /* Log that the default branch was taken. This is the probe for the reported violation scenario. */
            osapi_log_msg("OS_API_Impl_Init: default branch taken for idtype=%d; no action performed (fall-through behavior)", (int)idtype);
            break;
    }

    osapi_log_msg("OS_API_Impl_Init: exit returning %d", (int)return_code);

    return return_code;
}
